import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import Swal from "sweetalert2";

const AdminLogin = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      /* 1. G·ªåI API LOGIN */
      const res = await authService.login({ email, password });
      const data = res.data;
      const token = data.token || data.jwt || data["jwt-token"];

      if (!token) throw new Error("Server kh√¥ng tr·∫£ v·ªÅ m√£ x√°c th·ª±c!");

      /* 2. GI·∫¢I M√É KI·ªÇM TRA QUY·ªÄN ADMIN */
      const payload = JSON.parse(window.atob(token.split('.')[1]));
      const rawRole = payload.roles || payload.authorities || "";
      const roleStr = JSON.stringify(rawRole);

      if (!roleStr.includes("ADMIN")) {
        // TH√îNG B√ÅO SAI QUY·ªÄN H·∫†N
        Swal.fire({
          icon: "warning",
          title: "TRUY C·∫¨P B·ªä CH·∫∂N!",
          text: "T√†i kho·∫£n c·ªßa m√†y kh√¥ng c√≥ quy·ªÅn Qu·∫£n tr·ªã vi√™n.",
          background: "#1e1e2d",
          color: "#fff",
          confirmButtonColor: "#f39c12",
        });
        setLoading(false);
        return;
      }

      /* 3. L∆ØU V√ÄO KHO RI√äNG */
      localStorage.setItem("admin-token", token);
      localStorage.setItem("admin-role", "ADMIN");

      /* 4. TH√îNG B√ÅO TH√ÄNH C√îNG */
      Swal.fire({
        icon: "success",
        title: "X√ÅC TH·ª∞C TH√ÄNH C√îNG",
        text: "Ch√†o s·∫øp quay tr·ªü l·∫°i h·ªá th·ªëng!",
        timer: 1500,
        showConfirmButton: false,
        background: "#1e1e2d",
        color: "#fff",
      });

      navigate("/admin");
    } catch (err) {
      /* 5. TH√îNG B√ÅO L·ªñI ƒêƒÇNG NH·∫¨P */
      Swal.fire({
        icon: "error",
        title: "ƒêƒÇNG NH·∫¨P TH·∫§T B·∫†I",
        text: err.response?.data?.message || "Sai Email ho·∫∑c m·∫≠t kh·∫©u r·ªìi m√†y ∆°i!",
        background: "#1e1e2d",
        color: "#fff",
        confirmButtonColor: "#e74c3c",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      {/* C√°c ƒë·ªëm ƒë√®n trang tr√≠ ch·∫°y ph√≠a sau */}
      <div style={styles.light1}></div>
      <div style={styles.light2}></div>

      <div style={styles.glassCard}>
        <div style={styles.header}>
          <div style={styles.iconBox}>
            <span style={{ fontSize: "30px" }}>üîê</span>
          </div>
          <h2 style={styles.title}>ADMIN SYSTEM</h2>
          <p style={styles.subtitle}>C·ª≠a ng√µ qu·∫£n tr·ªã an to√†n</p>
        </div>

        <form onSubmit={handleSubmit} style={styles.form}>
          <div style={styles.inputGroup}>
            <label style={styles.label}>EMAIL QU·∫¢N TR·ªä</label>
            <input
              type="email"
              style={styles.input}
              placeholder="admin@email.com"
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div style={styles.inputGroup}>
            <label style={styles.label}>M·∫¨T KH·∫®U B·∫¢O M·∫¨T</label>
            <input
              type="password"
              style={styles.input}
              placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          <button type="submit" style={styles.button} disabled={loading}>
            {loading ? "ƒêANG X·ª¨ L√ù..." : "ƒêƒÇNG NH·∫¨P NGAY"}
          </button>
        </form>

        <div style={styles.footer}>
          <button onClick={() => navigate("/")} style={styles.backBtn}>
            ‚Üê Quay v·ªÅ trang ch·ªß c·ª≠a h√†ng
          </button>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    height: "100vh",
    width: "100vw",
    background: "#0f0f1a", // N·ªÅn t·ªëi s√¢u c·ª±c ƒë·∫πp
    display: "flex",
