import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import productService from "../../services/productService";

const ProductDetail = () => {
  const { id } = useParams(); // Lấy id từ URL
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!id) return;
    productService
      .getProductById(id)
      .then((data) => setProduct(data))
      .catch((err) => console.error("❌ Không tìm thấy sản phẩm:", err))
      .finally(() => setLoading(false));
  }, [id]);

  if (loading) return <p>Đang tải sản phẩm...</p>;
  if (!product) return <p>Không tìm thấy sản phẩm.</p>;

  return (
    <div className="product-detail max-w-3xl mx-auto p-6">
      <h2 className="text-2xl font-bold mb-4">{product.product_name}</h2>
      <img
        src={productService.getProductImage(product.image)}
        alt={product.product_name}
        className="w-full h-96 object-cover mb-4"
      />
      <p className="mb-2">{product.description}</p>
      <p className="font-semibold">
        Giá: {product.price.toLocaleString()} ₫
      </p>
      {product.special_price && (
        <p className="text-red-500 font-semibold">
          Giá khuyến mãi: {product.special_price.toLocaleString()} ₫
        </p>
      )}
    </div>
  );
};

export default ProductDetail;
