import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import cartService from '../../services/cartService';
import Swal from 'sweetalert2';

const Checkout = () => {
    const [cart, setCart] = useState(null);
    const [loading, setLoading] = useState(true);
    const [processing, setProcessing] = useState(false);
    const [user, setUser] = useState(null);
    const [paymentMethod, setPaymentMethod] = useState('COD');
    const [shippingAddress, setShippingAddress] = useState('');
    const navigate = useNavigate();

    const currentUser = JSON.parse(localStorage.getItem('user')) || { email: null };

    useEffect(() => {
        if (!currentUser.email) {
            navigate('/login');
            return;
        }
        loadCartAndUser();
    }, []);

    const loadCartAndUser = async () => {
        try {
            setLoading(true);
            const cartData = await cartService.getActiveCart(currentUser.email);

            if (!cartData || !cartData.products || cartData.products.length === 0) {
                navigate('/cart');
                return;
            }
            setCart(cartData);
            setUser(currentUser);
            setShippingAddress(localStorage.getItem('shippingAddress') || '');
        } catch (error) {
            Swal.fire('Lỗi', 'Không thể tải thông tin thanh toán', 'error');
        } finally {
            setLoading(false);
        }
    };

    const handlePlaceOrder = async () => {
        if (!shippingAddress.trim()) {
            Swal.fire('Thông báo', 'Vui lòng nhập địa chỉ giao hàng chi tiết', 'warning');
            return;
        }

        const confirm = await Swal.fire({
            title: 'Xác nhận đặt hàng?',
            text: `Tổng tiền: ${finalAmount.toLocaleString()}₫`,
            icon: 'question',
            showCancelButton: true,
            confirmButtonColor: '#ffc107',
            confirmButtonText: 'Đặt hàng ngay'
        });

        if (!confirm.isConfirmed) return;

        try {
            setProcessing(true);
            localStorage.setItem('shippingAddress', shippingAddress);

            // 1. Gọi API đặt hàng
            const order = await cartService.createOrder(user.email, cart.cartId, paymentMethod, shippingAddress);

            // 2. Làm rỗng giỏ hàng (Gọi API clearCart)
            await cartService.clearCart(user.email, cart.cartId);

            // 3. Thông báo thành công
            await Swal.fire({
                title: 'Thành công!',
                text: 'Đơn hàng của bạn đã được tiếp nhận.',
                icon: 'success',
                timer: 2000,
                showConfirmButton: false
            });

            window.dispatchEvent(new Event("cartUpdated"));
            navigate('/');
        } catch (error) {
            Swal.fire('Lỗi', error.response?.data?.message || 'Đặt hàng thất bại', 'error');
        } finally {
            setProcessing(false);
        }
    };

    const totalAmount = cart ? cart.products.reduce((acc, item) => acc + (item.specialPrice || item.price) * item.quantity, 0) : 0;
    const finalAmount = totalAmount; // Có thể thêm phí ship ở đây

    if (loading) return (
        <div className="d-flex justify-content-center align-items-center" style={{height: '60vh'}}>
            <div className="spinner-grow text-warning" role="status"></div>
        </div>
    );

    return (
        <div className="container mt-4 mb-5">
            <nav aria-label="breadcrumb" className="mb-4">
                <ol className="breadcrumb">
                    <li className="breadcrumb-item"><Link to="/">Trang chủ</Link></li>
                    <li className="breadcrumb-item"><Link to="/cart">Giỏ hàng</Link></li>
                    <li className="breadcrumb-item active">Thanh toán</li>
                </ol>
            </nav>

            <div className="row">
                {/* Bên trái: Thông tin khách hàng & Thanh toán */}
                <div className="col-lg-7">
                    <h4 className="fw-bold mb-4">THÔNG TIN GIAO HÀNG</h4>

                    <div className="card checkout-card shadow-sm p-4 mb-4">
                        <div className="row">
                            <div className="col-md-6 mb-3">
                                <label className="form-label small text-uppercase fw-bold text-muted">Người nhận</label>
                                <div className="p-2 border-bottom fw-bold">{user.firstName} {user.lastName}</div>
                            </div>
                            <div className="col-md-6 mb-3">
                                <label className="form-label small text-uppercase fw-bold text-muted">Số điện thoại</label>
                                <div className="p-2 border-bottom fw-bold">{user.mobileNumber || 'Chưa cập nhật'}</div>
                            </div>
                            <div className="col-12 mb-3">
                                <label className="form-label small text-uppercase fw-bold text-muted">Địa chỉ nhận hàng *</label>
                                <textarea 
                                    className="form-control border-warning-subtle" 
                                    rows="3" 
                                    placeholder="Số nhà, tên đường, phường/xã..."
                                    value={shippingAddress}
                                    onChange={(e) => setShippingAddress(e.target.value)}
                                ></textarea>
                            </div>
                        </div>
                    </div>

                    <h4 className="fw-bold mb-4">PHƯƠNG THỨC THANH TOÁN</h4>
                    <div className="mb-4">
                        {[
                            { id: 'COD', label: 'Thanh toán khi nhận hàng (COD)', icon: 'fa-truck' },
                            { id: 'BANKING', label: 'Chuyển khoản Ngân hàng', icon: 'fa-university' },
                            { id: 'MOMO', label: 'Ví điện tử MoMo', icon: 'fa-wallet' }
                        ].map((method) => (
                            <div 
                                key={method.id}
                                className={`payment-method-item d-flex align-items-center ${paymentMethod === method.id ? 'active' : ''}`}
                                onClick={() => setPaymentMethod(method.id)}
                            >
                                <div className="me-3">
                                    <i className={`fas ${method.icon} fs-4 ${paymentMethod === method.id ? 'text-warning' : 'text-muted'}`}></i>
                                </div>
                                <div className="flex-grow-1 fw-bold">{method.label}</div>
                                <div className="form-check">
                                    <input className="form-check-input" type="radio" checked={paymentMethod === method.id} readOnly />
                                </div>
                            </div>
                        ))}
                    </div>
                </div>

                {/* Bên phải: Tóm tắt đơn hàng */}
                <div className="col-lg-5">
                    <div className="card checkout-card shadow-sm sticky-top" style={{ top: '20px' }}>
                        <div className="card-body p-4">
                            <h5 className="fw-bold mb-4">ĐƠN HÀNG CỦA BẠN</h5>

                            <div className="product-list mb-4" style={{maxHeight: '300px', overflowY: 'auto'}}>
                                {cart.products.map((item, idx) => (
                                    <div key={idx} className="d-flex align-items-center mb-3">
                                        <div className="position-relative">
                                            <img 
                                                src={`http://localhost:8080/api/public/products/image/${item.image}`} 
                                                className="product-item-img border" 
                                                alt={item.productName} 
                                            />
                                            <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-secondary">
                                                {item.quantity}
                                            </span>
                                        </div>
                                        <div className="ms-4 flex-grow-1">
                                            <div className="small fw-bold text-truncate" style={{maxWidth: '180px'}}>{item.productName}</div>
                                            <div className="small text-muted">{(item.specialPrice || item.price).toLocaleString()}₫</div>
                                        </div>
                                        <div className="fw-bold small">
                                            {((item.specialPrice || item.price) * item.quantity).toLocaleString()}₫
                                        </div>
                                    </div>
                                ))}
                            </div>

                            <div className="summary-box p-3 mb-4">
                                <div className="d-flex justify-content-between mb-2">
                                    <span className="text-muted">Tạm tính:</span>
                                    <span>{totalAmount.toLocaleString()}₫</span>
                                </div>
                                <div className="d-flex justify-content-between mb-2">
                                    <span className="text-muted">Phí vận chuyển:</span>
                                    <span className="text-success fw-bold">Miễn phí</span>
                                </div>
                                <hr />
                                <div className="d-flex justify-content-between">
                                    <span className="fw-bold fs-5">TỔNG CỘNG:</span>
                                    <span className="fw-bold fs-5 text-danger">{finalAmount.toLocaleString()}₫</span>
                                </div>
                            </div>

                            <button 
                                className="btn btn-warning w-100 py-3 fw-bold rounded-pill shadow-sm mb-3"
                                onClick={handlePlaceOrder}
                                disabled={processing}
                            >
                                {processing ? (
                                    <span className="spinner-border spinner-border-sm me-2"></span>
                                ) : <i className="fas fa-lock me-2"></i>}
                                HOÀN TẤT THANH TOÁN
                            </button>

                            <Link to="/cart" className="btn btn-light w-100 rounded-pill text-muted small">
                                <i className="fas fa-chevron-left me-2"></i>Sửa lại giỏ hàng
                            </Link>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Checkout;
