import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../services/userService";
import categoryService from "../services/categoryService";
import bannerService from "../services/bannerService"; // âœ… ThÃªm bannerService

// âœ… Import CSS, Font, Bootstrap
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import "../assets/fonts/fontawesome/css/all.min.css";

// âœ… Import hÃ¬nh áº£nh
import flagVN from "../assets/images/icons/flags/VN.webp";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/banners/image";

const Header = () => {
  const [user, setUser] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [categories, setCategories] = useState([]);
  const [loadingCategories, setLoadingCategories] = useState(false);
  const [logo, setLogo] = useState(null); // âœ… State cho logo
  const navigate = useNavigate();

  // âœ… Láº¥y user tá»« localStorage
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);

        if (parsedUser.userId && typeof parsedUser.userId === 'number') {
          setLoading(true);

          userService
            .getUserById(parsedUser.userId)
            .then((userData) => {
              console.log("âœ… User data received:", userData);
              setUserInfo(userData);

              const updatedUser = {
                ...parsedUser,
                first_name: userData.first_name || userData.firstName,
                last_name: userData.last_name || userData.lastName,
                email: userData.email,
                mobile_number: userData.mobile_number
              };
              localStorage.setItem("user", JSON.stringify(updatedUser));
            })
            .catch((err) => {
              console.error("âŒ Lá»—i láº¥y thÃ´ng tin user:", err);
              if (parsedUser.first_name || parsedUser.last_name) {
                setUserInfo({
                  first_name: parsedUser.first_name,
                  last_name: parsedUser.last_name
                });
              }
            })
            .finally(() => {
              setLoading(false);
            });
        } else {
          if (parsedUser.first_name || parsedUser.last_name) {
            setUserInfo({
              first_name: parsedUser.first_name,
              last_name: parsedUser.last_name
            });
          }
        }
      } catch (error) {
        console.error("âŒ Parse user lá»—i:", error);
        setLoading(false);
      }
    }
  }, []);

  // âœ… Láº¥y danh sÃ¡ch categories tá»« API
  useEffect(() => {
    fetchCategories();
  }, []);

  // âœ… Láº¥y logo tá»« bannerService (id 4)
  useEffect(() => {
    fetchLogo();
  }, []);

  const fetchCategories = async () => {
    try {
      setLoadingCategories(true);
      console.log('ðŸ”„ Äang táº£i danh má»¥c...');

      const categoriesData = await categoryService.getAllCategories();
      console.log('âœ… Danh sÃ¡ch categories:', categoriesData);

      setCategories(categoriesData || []);

    } catch (error) {
      console.error('âŒ Lá»—i khi láº¥y danh má»¥c:', error);
      setCategories([]);
    } finally {
      setLoadingCategories(false);
    }
  };

  // âœ… HÃ m láº¥y logo tá»« bannerService
  const fetchLogo = async () => {
    try {
      console.log('ðŸ”„ Äang táº£i logo tá»« banner service...');

      // Láº¥y táº¥t cáº£ banners
      const banners = await bannerService.getAllBanners();
      console.log('âœ… Táº¥t cáº£ banners:', banners);

      // TÃ¬m banner cÃ³ id = 4
      const logoBanner = banners.find(banner => 
        banner.bannerId === 4 || banner.id === 4
      );

      if (logoBanner) {
        console.log('âœ… TÃ¬m tháº¥y logo banner:', logoBanner);
        setLogo(logoBanner);
      } else {
        console.warn('âš ï¸ KhÃ´ng tÃ¬m tháº¥y banner vá»›i id 4, sá»­ dá»¥ng banner Ä‘áº§u tiÃªn');
        // Fallback: sá»­ dá»¥ng banner Ä‘áº§u tiÃªn náº¿u khÃ´ng tÃ¬m tháº¥y id 4
        if (banners.length > 0) {
          setLogo(banners[0]);
        } else {
          console.error('âŒ KhÃ´ng cÃ³ banner nÃ o Ä‘á»ƒ lÃ m logo');
        }
      }

    } catch (error) {
      console.error('âŒ Lá»—i khi láº¥y logo:', error);
    }
  };

  // âœ… HÃ m láº¥y URL áº£nh logo
  const getLogoUrl = () => {
    if (!logo) return "https://via.placeholder.com/60x60/667eea/ffffff?text=Logo";

    const imageName = logo.imageUrl || logo.image_url;

    if (!imageName) {
      return "https://via.placeholder.com/60x60/667eea/ffffff?text=Logo";
    }

    if (imageName.startsWith('http')) {
      return imageName;
    }

    return `${IMAGE_BASE_URL}/${imageName}`;
  };

  // âœ… Äiá»u hÆ°á»›ng Ä‘áº¿n trang sáº£n pháº©m theo category
  const handleCategoryClick = (category) => {
    console.log('ðŸŽ¯ Chá»n category:', category);

    const categoryId = category.categoryId || category.id;
    const categoryName = category.categoryName || category.name || category.title || 'Danh má»¥c';

    if (categoryId) {
      navigate(`/product`, { 
        state: { 
          selectedCategory: categoryId,
          categoryName: categoryName
        }
      });
    } else {
      console.warn('âš ï¸ Category khÃ´ng cÃ³ ID:', category);
    }
  };

  // âœ… ÄÄƒng xuáº¥t
  const handleLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    setUser(null);
    setUserInfo(null);
    navigate("/login");
  };

  // âœ… Chuyá»ƒn Ä‘áº¿n trang chá»‰nh sá»­a profile
  const handleProfileClick = () => {
    if (user) {
      navigate("/profile");
    } else {
      navigate("/login");
    }
  };

  // âœ… HÃ m hiá»ƒn thá»‹ tÃªn ngÆ°á»i dÃ¹ng
  const renderUserName = () => {
    if (!user) {
      return "My profile";
    }

    if (loading) {
      return (
        <div className="d-flex align-items-center">
          <div className="spinner-border spinner-border-sm text-primary me-2" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <span>Loading...</span>
        </div>
      );
    }

    if (userInfo) {
      const firstName = userInfo.first_name || "";
      const lastName = userInfo.last_name || "";
      const fullName = `${firstName} ${lastName}`.trim();

      if (fullName) {
        return fullName;
      }
    }

    if (user.email) {
      return user.email;
    }

    return "User";
  };

  // âœ… HÃ m hiá»ƒn thá»‹ lá»i chÃ o
  const renderGreeting = () => {
    if (!user) return null;

    if (userInfo && (userInfo.first_name || userInfo.last_name)) {
      const firstName = userInfo.first_name || "";
      return `ðŸ‘‹ Hi, ${firstName}`;
    }

    if (user.email) {
      return `ðŸ‘‹ Hi, ${user.email.split('@')[0]}`;
    }

    return "ðŸ‘‹ Hi, User";
  };

  // âœ… HÃ m hiá»ƒn thá»‹ tÃªn category
  const getCategoryName = (category) => {
    return category.categoryName || category.name || category.title || 'Danh má»¥c';
  };

  return (
    <header className="section-header">
      {/* ======= THANH TRÃŠN ======= */}
      <nav className="navbar d-none d-md-flex p-md-0 navbar-expand-sm navbar-light border-bottom">
        <div className="container">
          <div className="collapse navbar-collapse" id="navbarTop4">
            <ul className="navbar-nav mr-auto">
              <li className="nav-item">
                {user ? (
                  <span className="nav-link">
                    {renderGreeting()} |{" "}
                    <button
                      onClick={handleLogout}
                      className="btn btn-link p-0 text-danger"
                      style={{ textDecoration: "none" }}
                    >
                      Logout
                    </button>
                  </span>
                ) : (
                  <span className="nav-link">
                    Hi, <Link to="/login">Log In</Link> or{" "}
                    <Link to="/register">Register</Link>
                  </span>
                )}
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  Deals
                </a>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  Sell
                </a>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  Help
                </a>
              </li>
            </ul>

            <ul className="navbar-nav">
              <li className="nav-item">
                <a href="#" className="nav-link">
                  <img src={flagVN} height="24" alt="flag" /> Ship to
                </a>
              </li>
              <li className="nav-item dropdown">
                <a
                  href="#"
                  className="nav-link dropdown-toggle"
                  data-bs-toggle="dropdown" 
                  aria-expanded="false"
                >
                  Watchlist
                </a>
                <ul className="dropdown-menu">
                  <li>
                    <a className="dropdown-item" href="#">
                      First item
                    </a>
                  </li>
                  <li>
                    <a className="dropdown-item" href="#">
                      Second item
                    </a>
                  </li>
                  <li>
                    <a className="dropdown-item" href="#">
                      Third item
                    </a>
                  </li>
                </ul>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  My shop
                </a>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  <i className="fa fa-bell"></i>
                </a>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">
                  <i className="fa fa-shopping-cart"></i>
                </a>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {/* ======= THANH CHÃNH ======= */}
      <div className="container">
        <section className="header-main border-bottom py-3">
          <div className="row align-items-center">
            {/* âœ… Logo hÃ¬nh trÃ²n - CHIáº¾M TOÃ€N Bá»˜ KHÃ”NG GIAN */}
            <div className="col-md-4">
              <Link to="/" className="brand-wrap text-decoration-none">
                <div className="d-flex align-items-center">
                  <div 
                    className="rounded-circle overflow-hidden border shadow-sm"
                    style={{
                      width: '70px',
                      height: '70px',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      backgroundColor: '#f8f9fa'
                    }}
                  >
                    <img
                      src={getLogoUrl()}
                      alt="Logo"
                      className="img-fluid"
                      style={{
                        width: '100%',
                        height: '100%',
                        objectFit: 'cover'
                      }}
                      onError={(e) => {
                        e.target.src = "https://via.placeholder.com/70x70/667eea/ffffff?text=Logo";
                      }}
                    />
                  </div>
                  <div className="ms-3">
                    <h4 className="fw-bold text-primary mb-0">SHOP</h4>
                    <small className="text-muted">Online Store</small>
                  </div>
                </div>
              </Link>
            </div>

            {/* âœ… CÃ¡c icon bÃªn pháº£i - CHIáº¾M TOÃ€N Bá»˜ PHáº¦N CÃ’N Láº I */}
            <div className="col-md-8">
              <div className="widgets-wrap d-flex justify-content-end">
                {/* Widget Profile */}
                <div className="widget-header me-4">
                  <a 
                    href="#" 
                    className="widget-view text-decoration-none"
                    onClick={(e) => {
                      e.preventDefault();
                      handleProfileClick();
                    }}
                  >
                    <div className="icon-area text-center mb-1">
                      <div className="bg-light rounded-circle p-2">
                        <i className="fa fa-user text-primary fa-lg"></i>
                      </div>
                    </div>
                    <small className="text-muted d-block text-center">
                      {renderUserName()}
                    </small>
                  </a>
                </div>

                <div className="widget-header me-4">
                  <a href="#" className="widget-view text-decoration-none">
                    <div className="icon-area text-center mb-1 position-relative">
                      <div className="bg-light rounded-circle p-2">
                        <i className="fa fa-comment-dots text-primary fa-lg"></i>
                      </div>
                      <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">
                        1
                      </span>
                    </div>
                    <small className="text-muted d-block text-center">Message</small>
                  </a>
                </div>

                <div className="widget-header me-4">
                  <a href="#" className="widget-view text-decoration-none">
                    <div className="icon-area text-center mb-1">
                      <div className="bg-light rounded-circle p-2">
                        <i className="fa fa-store text-primary fa-lg"></i>
                      </div>
                    </div>
                    <small className="text-muted d-block text-center">Orders</small>
                  </a>
                </div>

                <div className="widget-header">
                  <a href="#" className="widget-view text-decoration-none">
                    <div className="icon-area text-center mb-1">
                      <div className="bg-light rounded-circle p-2">
                        <i className="fa fa-shopping-cart text-primary fa-lg"></i>
                      </div>
                    </div>
                    <small className="text-muted d-block text-center">Cart</small>
                  </a>
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* Menu chÃ­nh vá»›i Categories */}
        <nav className="navbar navbar-main navbar-expand pl-0 py-2">
          <ul className="navbar-nav flex-wrap">
            <li className="nav-item">
              <Link to="/" className="nav-link">
                <i className="fa fa-home me-1"></i>
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/product" className="nav-link">
                <i className="fa fa-box me-1"></i>
                All Products
              </Link>
            </li>

            {/* âœ… Categories Dropdown */}
            <li className="nav-item dropdown">
              <a
                className="nav-link dropdown-toggle"
                href="#"
                id="categoriesDropdown"
                role="button"
                data-bs-toggle="dropdown"
                aria-expanded="false"
              >
                <i className="fa fa-list me-1"></i>
                Categories
                {loadingCategories && (
                  <span className="ms-1">
                    <div className="spinner-border spinner-border-sm text-primary" role="status">
                      <span className="visually-hidden">Loading...</span>
                    </div>
                  </span>
                )}
              </a>

              <ul 
                className="dropdown-menu" 
                aria-labelledby="categoriesDropdown"
                style={{ maxHeight: '400px', overflowY: 'auto' }}
              >
                {loadingCategories ? (
                  <li>
                    <div className="dropdown-item text-muted">
                      <div className="d-flex align-items-center">
                        <div className="spinner-border spinner-border-sm me-2" role="status">
                          <span className="visually-hidden">Loading...</span>
                        </div>
                        Loading categories...
                      </div>
                    </div>
                  </li>
                ) : categories.length > 0 ? (
                  categories.map((category, index) => (
                    <li key={category.categoryId || category.id || index}>
                      <a
                        className="dropdown-item"
                        href="#"
                        onClick={(e) => {
                          e.preventDefault();
                          handleCategoryClick(category);
                        }}
                      >
                        <i className="fa fa-folder me-2"></i>
                        {getCategoryName(category)}
                      </a>
                    </li>
                  ))
                ) : (
                  <li>
                    <div className="dropdown-item text-muted">
                      <i className="fa fa-exclamation-circle me-2"></i>
                      No categories found
                    </div>
                  </li>
                )}
              </ul>
            </li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
