import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";

const Login = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await authService.login(form);
      console.log("‚úÖ LOGIN RESULT:", res.data);

      // ‚úÖ DEBUG: Ki·ªÉm tra c·∫•u tr√∫c response
      const userData = res.data;
      console.log("üîç Response structure:", {
        hasUserId: !!(userData.user_id || userData.userId),
        hasUserObject: !!userData.user,
        keys: Object.keys(userData)
      });

      let userToStore = {};

      // ‚úÖ X·ª≠ l√Ω c√°c tr∆∞·ªùng h·ª£p response kh√°c nhau
      if (userData.user_id || userData.userId) {
        // Tr∆∞·ªùng h·ª£p 1: Response c√≥ user_id/userId tr·ª±c ti·∫øp
        userToStore = {
          userId: userData.user_id || userData.userId,
          email: userData.email,
          first_name: userData.first_name,
          last_name: userData.last_name
        };
      } else if (userData.user && (userData.user.user_id || userData.user.userId)) {
        // Tr∆∞·ªùng h·ª£p 2: Response c√≥ object user
        userToStore = {
          userId: userData.user.user_id || userData.user.userId,
          email: userData.user.email,
          first_name: userData.user.first_name,
          last_name: userData.user.last_name
        };
      } else {
        // Tr∆∞·ªùng h·ª£p 3: Kh√¥ng c√≥ userId, t·∫°o t·∫°m t·ª´ email
        userToStore = {
          userId: `temp_${Date.now()}`, // ‚úÖ T·∫°o userId t·∫°m th·ªùi
          email: userData.email,
          first_name: userData.first_name || "",
          last_name: userData.last_name || ""
        };
        console.warn("‚ö†Ô∏è Kh√¥ng t√¨m th·∫•y userId, s·ª≠ d·ª•ng t·∫°m th·ªùi:", userToStore.userId);
      }

      // ‚úÖ L∆∞u v√†o localStorage
      localStorage.setItem("user", JSON.stringify(userToStore));

      // ‚úÖ L∆∞u token n·∫øu c√≥
      if (userData.token) {
        localStorage.setItem("token", userData.token);
      }

      console.log("üíæ User saved to localStorage:", userToStore);

      alert("‚úÖ ƒêƒÉng nh·∫≠p th√†nh c√¥ng!");

      // ‚úÖ Reload ƒë·ªÉ Header c·∫≠p nh·∫≠t ngay
      window.location.href = "/";

    } catch (err) {
      console.error("‚ùå LOGIN ERROR:", err);
      alert("‚ùå ƒêƒÉng nh·∫≠p th·∫•t b·∫°i! Vui l√≤ng ki·ªÉm tra email v√† m·∫≠t kh·∫©u.");
    }
  };

  return (
    <div className="card mx-auto" style={{ maxWidth: "380px", marginTop: "100px" }}>
      <div className="card-body">
        <h4 className="card-title mb-4">Sign in</h4>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              name="email"
              className="form-control"
              placeholder="Email"
              type="email"
              value={form.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <input
              name="password"
              className="form-control"
              placeholder="Password"
              type="password"
              value={form.password}
              onChange={handleChange}
              required
            />
          </div>

          <button type="submit" className="btn btn-primary btn-block">
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
