import React, { useState, useEffect } from 'react';
import ReactStars from 'react-stars';
import axios from 'axios';
import Swal from 'sweetalert2';

const ProductReview = ({ productId, currentUser }) => {
  const [reviews, setReviews] = useState([]);
  const [canReview, setCanReview] = useState(false);
  const [comment, setComment] = useState("");
  const [rating, setRating] = useState(5);

  useEffect(() => {
    fetchReviews();
    checkReviewPermission();
  }, [productId, currentUser]);

  const fetchReviews = async () => {
    try {
      const res = await axios.get(`http://localhost:8080/api/public/products/${productId}/reviews`);
      setReviews(res.data);
    } catch (err) {
      console.error("Lỗi lấy review:", err);
    }
  };

  const checkReviewPermission = async () => {
    if (currentUser?.userId) {
      try {
        const res = await axios.get(`http://localhost:8080/api/public/can-review`, {
          params: { userId: currentUser.userId, productId: productId }
        });
        setCanReview(res.data);
      } catch (err) {
        setCanReview(false);
      }
    }
  };

  const handleSubmit = async () => {
    if (!comment.trim()) {
      Swal.fire('Thông báo', 'Bạn quên chưa nhập nội dung đánh giá kìa!', 'warning');
      return;
    }

    try {
      const reviewDTO = {
        comment,
        rating,
        productId,
        userId: currentUser.userId
      };
      const res = await axios.post(`http://localhost:8080/api/admin/reviews`, reviewDTO);
      setReviews([res.data, ...reviews]);
      setComment("");
      setCanReview(false);
      Swal.fire({ icon: 'success', title: 'Đã gửi đánh giá!', text: 'Cảm ơn Bạn đã đóng góp ý kiến!', timer: 2000, showConfirmButton: false });
    } catch (err) {
      Swal.fire('Lỗi', err.response?.data || 'Không thể gửi đánh giá', 'error');
    }
  };

  // Tính trung bình cộng số sao
  const avgRating = reviews.length > 0 
    ? (reviews.reduce((acc, r) => acc + r.rating, 0) / reviews.length).toFixed(1) 
    : 0;

  return (
    <div className="mt-5 border-top pt-5 pb-5">
      <div className="d-flex align-items-center mb-5">
        <div className="section-title-line me-3"></div>
        <h3 className="fw-bold mb-0 text-uppercase ls-1">Đánh giá từ khách hàng</h3>
      </div>

      <div className="row g-4 mb-5">
        {/* CỘT 1: TỔNG QUAN SỐ SAO */}
        <div className="col-lg-4">
          <div className="card border-0 shadow-sm rounded-5 p-4 text-center bg-white h-100 d-flex flex-column justify-content-center">
            <h1 className="display-3 fw-bold text-primary mb-0">{avgRating}</h1>
            <div className="d-flex justify-content-center">
              <ReactStars count={5} size={30} value={parseFloat(avgRating)} edit={false} color2={'#ffd700'} />
            </div>
            <p className="text-muted mt-2">{reviews.length} đánh giá thực tế</p>
          </div>
        </div>

        {/* CỘT 2: FORM ĐÁNH GIÁ (NẾU CÓ QUYỀN) */}
        <div className="col-lg-8">
          {canReview ? (
            <div className="card border-0 shadow-sm rounded-5 p-4 bg-white h-100">
              <h5 className="fw-bold mb-3">Trải nghiệm của Bạn thế nào?</h5>
              <div className="d-flex align-items-center mb-3 bg-light p-2 rounded-4 px-3 w-fit">
                <span className="small fw-bold me-3">Độ hài lòng:</span>
                <ReactStars count={5} size={24} value={rating} onChange={(newRating) => setRating(newRating)} color2={'#ffd700'} />
              </div>
              <textarea 
                className="form-control border-light bg-light rounded-4 p-3 shadow-none" 
                rows="3" 
                value={comment} 
                onChange={(e) => setComment(e.target.value)}
                placeholder="Nhập cảm nhận của Bạn về sản phẩm này..."
                style={{ resize: 'none' }}
              ></textarea>
              <div className="text-end mt-3">
                <button className="btn btn-primary px-5 py-2 rounded-pill fw-bold shadow-sm transition-up" onClick={handleSubmit}>
                  Gửi đánh giá <i className="bi bi-send-fill ms-2"></i>
                </button>
              </div>
            </div>
          ) : (
            <div className="card border-0 shadow-sm rounded-5 p-4 bg-light-blue h-100 d-flex align-items-center justify-content-center text-center">
              {currentUser ? (
                <div>
                  <div className="icon-box-sm mb-3 mx-auto"><i className="bi bi-lock-fill text-primary"></i></div>
                  <h6 className="fw-bold">Bạn chưa thể đánh giá sản phẩm này</h6>
                  <p className="small text-muted mb-0">Chỉ những khách hàng đã mua và nhận hàng thành công mới có quyền để lại đánh giá.</p>
                </div>
              ) : (
                <p className="mb-0 fw-bold">Vui lòng <a href="/login" className="text-primary text-decoration-none">đăng nhập</a> để tham gia đánh giá.</p>
              )}
            </div>
          )}
        </div>
      </div>

      {/* DANH SÁCH REVIEW */}
      <div className="row g-3">
        {reviews.length > 0 ? (
          reviews.map((r) => (
            <div key={r.reviewId} className="col-12">
              <div className="review-item p-4 rounded-5 bg-white shadow-sm border border-light transition-all">
                <div className="d-flex justify-content-between">
                  <div className="d-flex align-items-center">
                    <div className="avatar-review me-3 shadow-sm">
                      {r.userName?.charAt(0).toUpperCase()}
                    </div>
                    <div>
                      <h6 className="mb-0 fw-bold">{r.userName}</h6>
                      <div className="d-flex align-items-center">
                        <ReactStars count={5} size={14} edit={false} value={r.rating} color2={'#ffd700'} />
                        <span className="ms-2 badge bg-success-soft text-success x-small"><i className="bi bi-patch-check-fill me-1"></i> Đã mua hàng</span>
                      </div>
                    </div>
                  </div>
                  <div className="text-muted small italic">{new Date(r.createdAt).toLocaleDateString()}</div>
                </div>
                <div className="mt-3 text-secondary lh-lg">"{r.comment}"</div>
              </div>
            </div>
          ))
        ) : (
          <div className="text-center py-5">
            <i className="bi bi-chat-dots display-1 text-light"></i>
            <p className="text-muted mt-3">Chưa có ai đánh giá!</p>
          </div>
        )}
      </div>

      <style>{`
        .ls-1 { letter-spacing: 1px; }
        .bg-light-blue { background-color: #f0f7ff; }
