import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../services/userService";

// ... c√°c import kh√°c

const Header = () => {
  const [user, setUser] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [displayName, setDisplayName] = useState("");
  const navigate = useNavigate();

  // ‚úÖ L·∫•y user t·ª´ localStorage
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);

        // ‚úÖ L·∫•y th√¥ng tin ƒë·∫ßy ƒë·ªß c·ªßa user t·ª´ API
        const userId = parsedUser.userId || parsedUser.user_id;
        if (userId) {
          userService
            .getUserProfile(userId) // ‚úÖ S·ª≠ d·ª•ng getUserProfile m·ªõi
            .then((userData) => {
              setUserInfo(userData);
              setDisplayName(userData.fullName || userData.email || "User");
            })
            .catch((err) => {
              console.error("‚ùå L·ªói l·∫•y th√¥ng tin user:", err);
              // Fallback: s·ª≠ d·ª•ng th√¥ng tin t·ª´ localStorage
              setDisplayName(parsedUser.email || "User");
            });
        }
      } catch (error) {
        console.error("‚ùå Parse user l·ªói:", error);
      }
    }
  }, []);

  // ‚úÖ ƒêƒÉng xu·∫•t
  const handleLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    setUser(null);
    setUserInfo(null);
    setDisplayName("");
    navigate("/login");
  };

  return (
    <header className="section-header">
      {/* ======= THANH TR√äN ======= */}
      <nav className="navbar d-none d-md-flex p-md-0 navbar-expand-sm navbar-light border-bottom">
        <div className="container">
          {/* ... ph·∫ßn code navbar tr√™n ... */}

          <div className="collapse navbar-collapse" id="navbarTop4">
            <ul className="navbar-nav mr-auto">
              <li>
                {user ? (
                  <span className="nav-link">
                    üëã Hi, <strong>{displayName}</strong> |{" "}
                    <button
                      onClick={handleLogout}
                      className="btn btn-link p-0 text-danger"
                      style={{ textDecoration: "none" }}
                    >
                      Logout
                    </button>
                  </span>
                ) : (
                  <span className="nav-link">
                    Hi, <Link to="/login">Log In</Link> or{" "}
                    <Link to="/register">Register</Link>
                  </span>
                )}
              </li>
              {/* ... c√°c menu item kh√°c ... */}
            </ul>
          </div>
        </div>
      </nav>

      {/* ======= THANH CH√çNH ======= */}
      <div className="container">
        <section className="header-main border-bottom">
          <div className="row row-sm">
            {/* Logo */}
            <div className="col-6 col-sm col-md col-lg flex-grow-0">
              <Link to="/" className="brand-wrap">
                <img src={logo} alt="Logo" height={56} />
              </Link>
            </div>

            {/* Thanh t√¨m ki·∫øm */}
            <div className="col-lg-6 col-xl col-md-5 col-sm-12 flex-grow-1">
              <form action="#" className="search-header">
                <div className="input-group">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Search"
                  />
                </div>
              </form>
            </div>

            {/* N√∫t search */}
            <div className="col col-lg col-md flex-grow-0">
              <button className="btn btn-block btn-primary" type="submit">
                Search
              </button>
            </div>

            {/* C√°c icon b√™n ph·∫£i */}
            <div className="col-xl-4 col-lg-5 col-md-6">
              <div className="widgets-wrap float-md-right">
                {/* Widget Profile */}
                <div className="widget-header mr-3">
                  <a href="#" className="widget-view">
                    <div className="icon-area">
                      <i className="fa fa-user"></i>
                    </div>
                    <small className="text">
                      {user ? (
                        userInfo ? (
                          <span>
                            {userInfo.firstName} {userInfo.lastName}
                          </span>
                        ) : (
                          user.email || "Loading..."
                        )
                      ) : (
                        "My profile"
                      )}
                    </small>
                  </a>
                </div>

                {/* ... c√°c widget kh√°c ... */}
              </div>
            </div>
          </div>
        </section>

        {/* Menu ch√≠nh */}
        <nav className="navbar navbar-main navbar-expand pl-0">
          <ul className="navbar-nav flex-wrap">
            <li className="nav-item">
              <Link to="/" className="nav-link">
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/products" className="nav-link">
                Products
              </Link>
            </li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
