import React, { useState, useEffect } from 'react';
import bannerService from '../../services/bannerService';

const IMAGE_BASE_URL = "http://localhost:8080/api/public/banners/image";

const BannerTwo = () => {
  const [banner, setBanner] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [imageLoaded, setImageLoaded] = useState(false);

  useEffect(() => {
    fetchBanner();
  }, []);

  const fetchBanner = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('üîÑ ƒêang t·∫£i banner ID 6...');

      const data = await bannerService.getActiveBanners();
      console.log('üì¢ T·∫•t c·∫£ banners:', data);

      const bannersArray = Array.isArray(data) ? data : [];

      // ‚úÖ CH·ªà T√åM BANNER C√ì ID 6
      const bannerSix = bannersArray.find(banner => {
        const bannerId = banner.bannerId || banner.id;
        return bannerId === 6;
      });

      console.log('üéØ Banner ID 6 t√¨m th·∫•y:', bannerSix);

      if (bannerSix) {
        setBanner(bannerSix);
      } else {
        setError('Kh√¥ng t√¨m th·∫•y banner ID 6');
      }

    } catch (err) {
      console.error('‚ùå L·ªói khi l·∫•y banner:', err);
      setError('Kh√¥ng th·ªÉ t·∫£i banner: ' + (err.message || 'L·ªói kh√¥ng x√°c ƒë·ªãnh'));
    } finally {
      setLoading(false);
    }
  };

  const getImageUrl = () => {
    if (!banner) return 'https://via.placeholder.com/1200x400/667eea/ffffff?text=Banner+ID+6';

    const imageName = banner.imageUrl || banner.image_url || banner.imageName;

    if (!imageName) {
      return 'https://via.placeholder.com/1200x400/667eea/ffffff?text=Banner+ID+6';
    }

    if (imageName.startsWith('http')) {
      return imageName;
    }

    return `${IMAGE_BASE_URL}/${imageName}`;
  };

  const handleImageLoad = () => {
    setImageLoaded(true);
  };

  const handleImageError = (e) => {
    console.error('‚ùå L·ªói t·∫£i ·∫£nh banner ID 6:', banner);
    e.target.src = 'https://via.placeholder.com/1200x400/667eea/ffffff?text=Banner+ID+6+Error';
  };

  // Loading state
  if (loading) {
    return (
      <section className="banner-two-section mb-4">
        <div className="container">
          <div 
            className="banner-placeholder rounded-3 d-flex align-items-center justify-content-center"
            style={{
              height: '200px',
              background: 'linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%)'
            }}
          >
            <div className="text-center">
              <div className="spinner-border text-primary mb-2" role="status">
                <span className="visually-hidden">Loading...</span>
              </div>
              <p className="text-muted mb-0">ƒêang t·∫£i banner ID 6...</p>
            </div>
          </div>
        </div>
      </section>
    );
  }

  // Error state
  if (error) {
    return (
      <section className="banner-two-section mb-4">
        <div className="container">
          <div 
            className="banner-error rounded-3 d-flex align-items-center justify-content-center"
            style={{
              height: '150px',
              background: 'linear-gradient(135deg, #fff5f5 0%, #fed7d7 100%)'
            }}
          >
            <div className="text-center">
              <div className="text-danger mb-2">
                <i className="fas fa-exclamation-triangle"></i>
              </div>
              <p className="text-danger mb-2 small">{error}</p>
              <button 
                onClick={fetchBanner}
                className="btn btn-sm btn-outline-danger"
              >
                <i className="fas fa-redo me-1"></i>
                Th·ª≠ l·∫°i
              </button>
            </div>
          </div>
        </div>
      </section>
    );
  }

  // Empty state - kh√¥ng t√¨m th·∫•y banner ID 6
  if (!banner) {
    return (
      <section className="banner-two-section mb-4">
        <div className="container">
          <div 
            className="banner-empty rounded-3 d-flex align-items-center justify-content-center"
            style={{
              height: '150px',
              background: 'linear-gradient(135deg, #f0f9ff 0%, #e0f2fe 100%)'
            }}
          >
            <div className="text-center text-muted">
              <i className="fas fa-image mb-2"></i>
              <p className="mb-0 small">Kh√¥ng t√¨m th·∫•y banner ID 6</p>
            </div>
          </div>
        </div>
      </section>
    );
  }

  // Main banner - CH·ªà HI·ªÇN TH·ªä H√åNH ·∫¢NH
  return (
    <section className="banner-two-section mb-4">
      <div className="container">
        <div className="banner-simple position-relative rounded-3 overflow-hidden shadow-sm">

          {/* Loading overlay */}
          {!imageLoaded && (
            <div 
              className="position-absolute top-0 start-0 w-100 h-100 d-flex align-items-center justify-content-center"
              style={{
                background: 'linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%)',
                zIndex: 1
              }}
            >
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Loading...</span>
              </div>
            </div>
          )}

          {/* Ch·ªâ hi·ªÉn th·ªã h√¨nh ·∫£nh banner */}
          <img
            src={getImageUrl()}
            alt={banner.title || 'Banner ID 6'}
            className="img-fluid w-100"
            style={{ 
              height: '200px',
              objectFit: 'cover',
              objectPosition: 'center'
            }}
            onLoad={handleImageLoad}
            onError={handleImageError}
          />
        </div>
      </div>
    </section>
  );
};

export default BannerTwo;
