      <div style={sidebarStyle}>
        <h3 style={{ color: "#fff", textAlign: "center", marginBottom: "30px" }}>üíÑ COSMETIC ADMIN</h3>
        
        <nav style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
          <Link to="/admin" style={linkStyle}>üìä Dashboard</Link>
          <Link to="/admin/products" style={linkStyle}>üì¶ Qu·∫£n l√Ω S·∫£n ph·∫©m</Link>
          <Link to="/admin/orders" style={linkStyle}>üõí Qu·∫£n l√Ω ƒê∆°n h√†ng</Link>
          <Link to="/admin/users" style={linkStyle}>üë• Kh√°ch h√†ng</Link>
        </nav>

        <button onClick={handleLogout} style={logoutButtonStyle}>
          üö™ ƒêƒÉng xu·∫•t
