          <span>Xin ch√†o, <strong>Admin</strong> üëë</span>
        </div>

        <Routes>
          <Route path="" element={<Dashboard />} />
          <Route path="products" element={<div>Trang qu·∫£n l√Ω s·∫£n ph·∫©m m·ªπ ph·∫©m</div>} />
