import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import productService from "../../services/productServices";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  /** ✅ Lấy tất cả sản phẩm */
  const fetchProducts = async () => {
    try {
      const res = await productServices.getAllProducts();

      // ✅ API trả object -> lấy res.data
      let list = res?.data ?? res;

      // ✅ Nếu không phải mảng => chuyển thành mảng
      if (!Array.isArray(list)) {
        list = [];
      }

      setProducts(list);
    } catch (error) {
      console.error("❌ Lỗi khi lấy danh sách sản phẩm:", error);
      setProducts([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  if (loading) return <p>Đang tải sản phẩm...</p>;

  if (!products.length) return <p>Không có sản phẩm nào</p>;

  return (
    <div className="product-list">
      <h2>Danh sách sản phẩm</h2>

      <div className="grid grid-cols-4 gap-4">
        {products.map((p) => (
          <div key={p.product_id} className="border p-3 rounded-md shadow">
            {/* ✅ Hình ảnh */}
            <Link to={`/products/${p.product_id}`}>
              <img
                src={p.image ?? "/no-image.png"}
                alt={p.name}
                className="w-full h-48 object-cover rounded"
              />
            </Link>

            {/* ✅ Tên sản phẩm */}
            <h3 className="text-lg font-semibold mt-2 line-clamp-1">
              {p.name}
            </h3>

            {/* ✅ Giá sản phẩm */}
            <p className="text-red-500 font-bold">{p.price?.toLocaleString()} đ</p>

            <Link
              to={`/products/${p.product_id}`}
              className="text-blue-600 underline"
            >
              Xem chi tiết
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
