import { useEffect, useState, useCallback } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import productService from "../../services/productService";
import categoryService from "../../services/categoryService";
import ProductCard from "../product/ProductCard";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const location = useLocation();
  const navigate = useNavigate();

  const [searchTerm, setSearchTerm] = useState("");
  const [filters, setFilters] = useState({
    categoryId: "",
    searchKeyword: "",
    sortBy: "price",
    sortOrder: "asc"
  });

  // 1. LẮNG NGHE DỮ LIỆU TỪ HEADER TRUYỀN SANG
  useEffect(() => {
    if (location.state) {
      const { searchKeyword, selectedCategory } = location.state;
      setFilters(prev => ({
        ...prev,
        searchKeyword: searchKeyword || "",
        categoryId: selectedCategory || ""
      }));
      if (searchKeyword) setSearchTerm(searchKeyword);

      window.history.replaceState({}, document.title);
    }
  }, [location.state]);

  // 2. Debounce tìm kiếm
  useEffect(() => {
    const delayDebounceFn = setTimeout(() => {
      setFilters(prev => ({ ...prev, searchKeyword: searchTerm }));
    }, 500);
    return () => clearTimeout(delayDebounceFn);
  }, [searchTerm]);

  // 3. Tải danh mục
  useEffect(() => {
    categoryService.getAllCategories().then(data => setCategories(data || []));
  }, []);

  // 4. HÀM TẢI SẢN PHẨM
  const fetchProducts = useCallback(async () => {
    setLoading(true);
    const PAGE_SIZE = 1000;
    try {
      let response;
      const { categoryId, searchKeyword, sortBy, sortOrder } = filters;

      if (searchKeyword) {
        response = await productService.searchProducts(searchKeyword, 0, PAGE_SIZE, sortBy, sortOrder);
      } else if (categoryId) {
        response = await productService.getProductsByCategory(categoryId, 0, PAGE_SIZE, sortBy, sortOrder);
      } else {
        response = await productService.getAllProducts(0, PAGE_SIZE, sortBy, sortOrder);
      }
      setProducts(response?.content || []);
    } catch (err) {
      setProducts([]);
    } finally {
      setTimeout(() => setLoading(false), 300);
    }
  }, [filters]);

  useEffect(() => {
    fetchProducts();
  }, [fetchProducts]);

  // 5. HÀM XÓA BỘ LỌC
  const handleClearFilters = () => {
    setSearchTerm("");
    setFilters({
      categoryId: "",
      searchKeyword: "",
      sortBy: "price",
      sortOrder: "asc"
    });
    // Xóa state điều hướng nếu có
    navigate("/product", { state: {}, replace: true });
  };

  return (
    <section className="container mt-5 pb-5">
      {/* Thanh bộ lọc */}
      <div className="row g-3 mb-4 bg-light p-3 rounded-4 align-items-center shadow-sm">
        <div className="col-md-3">
          <select 
            value={filters.categoryId} 
            onChange={(e) => setFilters(p => ({ ...p, categoryId: e.target.value }))}
            className="form-select rounded-pill border-0 shadow-sm custom-select"
          >
            <option value="">Tất cả danh mục</option>
            {categories.map(c => (
              <option key={c.id || c.categoryId} value={c.id || c.categoryId}>
                {c.categoryName || c.name}
              </option>
            ))}
          </select>
        </div>

        <div className="col-md-6">
          <div className="position-relative">
            <i className="fa fa-search position-absolute top-50 start-0 translate-middle-y ms-3 text-muted"></i>
            <input 
              type="text" 
              className="form-control rounded-pill border-0 shadow-sm ps-5 pe-4"
              placeholder="Tìm kiếm nhanh sản phẩm..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>

        <div className="col-md-3 text-end">
          <button 
            className="btn btn-outline-danger rounded-pill px-4 fw-bold border-0 shadow-sm"
            onClick={handleClearFilters}
          >
            <i className="fa fa-undo-alt me-2"></i>Xóa bộ lọc
          </button>
        </div>
      </div>

      {/* Hiển thị sản phẩm */}
      <div className={`row transition-all ${loading ? 'opacity-50' : ''}`}>
        {products.length > 0 ? (
          products.map(p => (
            <div key={p.id || p.productId} className="col-xl-3 col-lg-4 col-md-6 col-6 mb-4">
               <ProductCard product={p} />
            </div>
          ))
        ) : !loading && (
          <div className="text-center py-5">
            <i className="fa fa-search-minus fa-4x text-light mb-3"></i>
            <h4 className="text-muted">Không tìm thấy sản phẩm nào phù hợp</h4>
          </div>
        )}
      </div>

      <style>{`
        .transition-all { transition: all 0.3s ease; }

        /* Ẩn thanh cuộn cho toàn bộ trang nếu cần hoặc các vùng cụ thể */
        .custom-select {
            cursor: pointer;
        }

        /* Lưu ý: Thẻ select mặc định của trình duyệt (Browser Native) 
           không cho phép ẩn scrollbar của menu dropdown bằng CSS.
           Nếu danh mục quá dài, trình duyệt sẽ tự thêm scrollbar.
        */

        .form-control:focus, .form-select:focus {
            box-shadow: 0 0 0 0.25rem rgba(13, 110, 253, 0.1) !important;
        }

        .btn-outline-danger:hover {
            background-color: #fff1f1;
            color: #dc3545;
            transform: translateY(-2px);
        }
      `}</style>
    </section>
  );
};

export default ProductList;
