import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import productService from "../../services/productService";

const ProductDetail = () => {
  const { id } = useParams(); // lấy ID từ URL
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!id) return;

    const fetchProduct = async () => {
      try {
        const data = await productService.getProductById(id);
        setProduct(data);
      } catch (error) {
        console.error("❌ Không tìm thấy sản phẩm:", error);
        setProduct(null);
      } finally {
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  if (loading) return <h2>Loading...</h2>;
  if (!product) return <h2>❌ Không tìm thấy sản phẩm!</h2>;

  return (
    <div className="container my-5">
      <div className="row">
        {/* Ảnh sản phẩm */}
        <div className="col-md-6">
          <img
            src={productService.getProductImage(product.image)}
            alt={product.product_name}
            className="img-fluid rounded"
          />
        </div>

        {/* Thông tin sản phẩm */}
        <div className="col-md-6">
          <h2 className="mb-3">{product.product_name}</h2>
          <p className="h4 text-danger">
            {product.special_price
              ? `${product.special_price.toLocaleString()} ₫`
              : `${product.price.toLocaleString()} ₫`}
          </p>

          {product.discount > 0 && (
            <p className="text-success">Giảm {product.discount}% so với giá gốc</p>
          )}

          <p className="mt-4">{product.description}</p>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
