import React, { useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import productService from "../../services/productService";
import cartService from "../../services/cartService";
import Swal from "sweetalert2"; // Đảm bảo đã import Swal

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductDetail = () => {
  const { productId } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [addingToCart, setAddingToCart] = useState(false);

  // Lấy user trực tiếp từ localStorage để đảm bảo dữ liệu mới nhất
  const getStoredUser = () => JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchProductDetail = async () => {
      if (!productId || productId === 'undefined' || productId === 'null') {
        setError("Không tìm thấy ID sản phẩm");
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        const productData = await productService.getProductById(productId);
        setProduct(productData);
      } catch (err) {
        console.error("❌ Lỗi tải sản phẩm:", err);
        setError("Không thể tải thông tin sản phẩm.");
      } finally {
        setLoading(false);
      }
    };

    fetchProductDetail();
  }, [productId]);

  const handleAddToCart = async () => {
    const user = getStoredUser();

    // 1. Kiểm tra đăng nhập
    if (!user || !user.email) {
      Swal.fire({
        icon: 'warning',
        title: 'Bạn chưa đăng nhập',
        text: 'Vui lòng đăng nhập để mua sắm!',
        confirmButtonColor: '#ff6a00',
        showCancelButton: true,
        cancelButtonText: 'Để sau',
        confirmButtonText: 'Đăng nhập ngay'
      }).then((result) => {
        if (result.isConfirmed) navigate('/login');
      });
      return;
    }

    const productQuantity = product?.quantity || 0;
    if (productQuantity === 0) {
      Swal.fire('Hết hàng', 'Sản phẩm này hiện tại đã hết.', 'error');
      return;
    }

    try {
      setAddingToCart(true);

      // 2. Lấy giỏ hàng đang hoạt động của người dùng
      const cart = await cartService.getActiveCart(user.email);

      if (!cart || !cart.cartId) {
        throw new Error("Không tìm thấy giỏ hàng.");
      }

      // 3. Thêm vào giỏ hàng thông qua API
      // Truyền: cartId, productId, và quantity (mặc định là 1)
      await cartService.addToCart(cart.cartId, productId, 1);

      // ✅ Hiển thị thông báo thành công
      Swal.fire({
        icon: 'success',
        title: 'Đã thêm vào giỏ hàng!',
        text: product.productName || product.product_name,
        showConfirmButton: false,
        timer: 1500,
        position: 'center'
      });

      // ✅ QUAN TRỌNG: Bắn sự kiện để Header cập nhật lại cartCount
      window.dispatchEvent(new Event("cartUpdated"));

    } catch (error) {
      console.error('❌ Lỗi thêm vào giỏ:', error);
      Swal.fire({
        icon: 'error',
        title: 'Lỗi giỏ hàng',
        text: error.response?.data?.message || "Không thể kết nối đến máy chủ",
        confirmButtonColor: '#ff6a00'
      });
    } finally {
      setAddingToCart(false);
    }
  };

  if (loading) {
    return (
      <div className="container mt-5 text-center">
        <div className="spinner-border" style={{ color: "#ff6a00" }} role="status"></div>
        <p className="mt-2">Đang tải sản phẩm...</p>
      </div>
    );
  }

  if (error || !product) {
    return (
      <div className="container mt-5">
        <div className="alert alert-danger text-center shadow-sm">
          <h4>Thông tin lỗi</h4>
          <p>{error || "Sản phẩm không tồn tại."}</p>
          <button className="btn btn-primary mt-3" onClick={() => navigate('/')}>Quay lại trang chủ</button>
        </div>
      </div>
    );
  }

  const productName = product.productName || product.product_name;
  const productPrice = product.price;
  const productSpecialPrice = product.specialPrice || product.special_price;
  const productQuantity = product.quantity;
  const productDiscount = product.discount;

  return (
    <div className="container mt-4 mb-5">
      <nav aria-label="breadcrumb" className="mb-4">
        <ol className="breadcrumb">
          <li className="breadcrumb-item"><Link to="/">Trang chủ</Link></li>
          <li className="breadcrumb-item"><Link to="/product">Sản phẩm</Link></li>
          <li className="breadcrumb-item active">{productName}</li>
        </ol>
      </nav>

      <div className="row g-4">
        {/* Hình ảnh */}
        <div className="col-md-6">
          <div className="card border-0 shadow-sm overflow-hidden" style={{ borderRadius: "15px" }}>
            <img
              src={`${IMAGE_BASE_URL}/${product.image}`}
              alt={productName}
              onError={(e) => (e.target.src = "https://via.placeholder.com/500")}
              className="img-fluid"
              style={{ minHeight: "450px", objectFit: "contain", backgroundColor: "#fff" }}
            />
          </div>
        </div>

        {/* Thông tin chi tiết */}
        <div className="col-md-6">
          <div className="p-3">
            <h2 className="fw-bold mb-3">{productName}</h2>

            <div className="mb-3 d-flex align-items-center">
              {productSpecialPrice ? (
                <>
                  <span className="h3 fw-bold text-danger mb-0 me-2">
                    {productSpecialPrice.toLocaleString()} ₫
                  </span>
                  <span className="text-muted text-decoration-line-through me-2">
                    {productPrice.toLocaleString()} ₫
                  </span>
                  <span className="badge bg-danger">-{productDiscount}%</span>
                </>
              ) : (
                <span className="h3 fw-bold text-danger mb-0">
                  {productPrice?.toLocaleString()} ₫
                </span>
              )}
            </div>

            <div className="mb-4">
              <span className={`badge ${productQuantity > 0 ? 'bg-success-light text-success' : 'bg-danger-light text-danger'} p-2 px-3`}>
                <i className={`fa ${productQuantity > 0 ? 'fa-check-circle' : 'fa-times-circle'} me-1`}></i>
                {productQuantity > 0 ? `Còn hàng (Kho: ${productQuantity})` : 'Tạm hết hàng'}
              </span>
            </div>

            <div className="card bg-light border-0 mb-4" style={{ borderRadius: "10px" }}>
              <div className="card-body">
                <h6 className="fw-bold"><i className="fa fa-info-circle me-2"></i>Mô tả sản phẩm:</h6>
                <p className="text-muted small mb-0" style={{ whiteSpace: "pre-line" }}>
                  {product.description || "Thông tin đang được cập nhật..."}
                </p>
              </div>
            </div>

            <div className="d-grid gap-3 d-md-flex">
              <button 
                className="btn btn-lg flex-fill fw-bold text-white shadow-sm"
                style={{ backgroundColor: "#ff6a00", border: "none" }}
                onClick={handleAddToCart}
                disabled={productQuantity === 0 || addingToCart}
              >
                {addingToCart ? (
                  <><span className="spinner-border spinner-border-sm me-2"></span>Đang xử lý...</>
                ) : (
                  <><i className="fa fa-cart-plus me-2"></i>THÊM VÀO GIỎ HÀNG</>
                )}
              </button>

              <button className="btn btn-outline-secondary btn-lg px-4">
                <i className="fa fa-heart"></i>
              </button>
            </div>

            {!getStoredUser() && (
              <p className="mt-3 small text-muted text-center">
                * Bạn cần <Link to="/login" className="text-warning fw-bold">Đăng nhập</Link> để mua hàng.
              </p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
