import { useEffect, useState } from "react";
import { Link } from "react-router-dom";   // ‚úÖ import Link
import productService from "../../services/productService";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductList = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    productService
      .getAllProducts()
      .then((data) => {
        console.log("Products loaded:", data.content || data);
        setProducts(data.content || data);
      })
      .catch((err) => console.error("‚ùå L·ªói khi t·∫£i s·∫£n ph·∫©m:", err));
  }, []);

  return (
    <section className="padding-bottom">
      <header className="section-heading mb-4 text-center">
        <h3 className="title-section text-uppercase font-bold text-primary">
          üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m
        </h3>
      </header>

      <div className="row">
        {products.map((p) => (
          <div
            key={p.product_id || p.id}   // ‚úÖ d√πng id duy nh·∫•t
            className="col-xl-3 col-lg-3 col-md-4 col-6"
          >
            <div className="card card-product-grid shadow-sm">
              <Link to={`/product/${p.product_id || p.id}`}> {/* ‚úÖ Link ƒë·∫øn chi ti·∫øt */}
                <div className="img-wrap">
                  <img
                    src={`${IMAGE_BASE_URL}/${p.image}`}
                    alt={p.product_name}
                    onError={(e) =>
                      (e.target.src =
                        "https://cdn-icons-png.flaticon.com/512/679/679922.png")
                    }
                    style={{
                      width: "100%",
                      height: "250px",
                      objectFit: "cover",
                      borderRadius: "10px",
                    }}
                  />
                </div>
              </Link>

              <div className="info-wrap p-3">
                <h6 className="text-truncate">{p.product_name}</h6>
                <p className="text-muted small mb-1">
                  {p.description?.substring(0, 40)}...
                </p>

                <div className="price h5 text-danger mb-0">
                  {p.special_price
                    ? `${p.special_price.toLocaleString()} ‚Ç´`
                    : `${p.price.toLocaleString()} ‚Ç´`}
                </div>

                {p.discount > 0 && (
                  <small className="text-success">
                    Gi·∫£m {p.discount}% so v·ªõi gi√° g·ªëc
                  </small>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default ProductList;
