import { useEffect, useState } from "react";
import productService from "../../services/productService";

const API_IMAGE_URL = "http://localhost:8080/images"; // đường dẫn ảnh từ backend

const ProductList = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    productService
      .getAllProducts()
      .then((data) => {
        setProducts(data.content || data); // nếu backend trả về dạng Page
      })
      .catch((err) => console.error("❌ Lỗi khi tải sản phẩm:", err));
  }, []);

  return (
    <section className="padding-bottom">
      <header className="section-heading mb-4">
        <h3 className="title-section">Danh sách sản phẩm</h3>
      </header>

      <div className="row">
        {products.map((p) => (
          <div key={p.product_id} className="col-xl-3 col-lg-3 col-md-4 col-6">
            <div className="card card-product-grid shadow-sm">
              <div className="img-wrap">
                <img
                  src={`${API_IMAGE_URL}/${p.image}`}
                  alt={p.product_name}
                  onError={(e) =>
                    (e.target.src =
                      "https://cdn-icons-png.flaticon.com/512/679/679922.png")
                  }
                  style={{
                    width: "100%",
                    height: "250px",
                    objectFit: "cover",
                    borderRadius: "10px",
                  }}
                />
              </div>

              <div className="info-wrap p-3">
                <h6 className="text-truncate">{p.product_name}</h6>

                <p className="text-muted small mb-1">
                  {p.description?.substring(0, 40)}...
                </p>

                <div className="price h5 text-danger mb-0">
                  {p.special_price
                    ? `${p.special_price.toLocaleString()} ₫`
                    : `${p.price.toLocaleString()} ₫`}
                </div>

                {p.discount > 0 && (
                  <small className="text-success">
                    Giảm {p.discount}% so với giá gốc
                  </small>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default ProductList;
