import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import productService from '../../services/productService';
import postService from '../../services/postService';

const ProductSold = () => {
  const [topProducts, setTopProducts] = useState([]);
  const [allProducts, setAllProducts] = useState([]);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(0);

  const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

  useEffect(() => {
    fetchInitialData();
  }, []);

  const fetchInitialData = async () => {
    setLoading(true);
    try {
      const topRes = await productService.getTopSellingProducts(0, 8);
      setTopProducts(topRes.content || []);

      const allRes = await productService.getAllProducts(0, 8);
      setAllProducts(allRes.content || []);

      const externalPosts = await postService.getExternalPosts();
      setPosts(externalPosts.slice(0, 3));
    } catch (err) {
      console.error('Lỗi tải dữ liệu:', err);
    } finally {
      setLoading(false);
    }
  };

  const loadMoreProducts = async () => {
    const nextPage = page + 1;
    setLoading(true);
    try {
      const res = await productService.getAllProducts(nextPage, 8);
      setAllProducts([...allProducts, ...(res.content || [])]);
      setPage(nextPage);
    } catch (err) {
      console.error('Lỗi tải thêm sản phẩm:', err);
    } finally {
      setLoading(false);
    }
  };

  const ProductItem = ({ p, index, isTop }) => {
    const productId = p.productId || p.product_id;
    const discount = p.specialPrice ? Math.round(((p.price - p.specialPrice) / p.price) * 100) : 0;

    return (
      <div className="col-xl-3 col-lg-4 col-md-6 col-6 mb-4">
        <div className="card card-product-grid shadow-sm h-100 border-0 hover-up position-relative">
          {/* Badge giảm giá cho mỹ phẩm */}
          {discount > 0 && (
            <div className="position-absolute top-0 end-0 m-2 badge bg-danger shadow" style={{zIndex: 5}}>
              -{discount}%
            </div>
          )}

          {isTop && index < 3 && (
            <div className="position-absolute top-0 start-0 m-2 badge bg-dark text-white shadow" style={{zIndex: 5}}>
              Top {index + 1} Trending
            </div>
          )}

          <div className="img-wrap p-3 text-center bg-white">
            <Link to={`/product/${productId}`}>
              <img
                src={`${IMAGE_BASE_URL}/${p.image}`}
                alt={p.productName}
                onError={(e) => (e.target.src = "https://cdn-icons-png.flaticon.com/512/679/679922.png")}
                className="img-fluid"
                style={{ height: "220px", objectFit: "contain" }}
              />
            </Link>
          </div>

          <div className="info-wrap p-3 pt-0">
            <Link to={`/product/${productId}`} className="title text-dark fw-bold text-decoration-none d-block mb-1 text-truncate" title={p.productName}>
              {p.productName}
            </Link>

            <div className="rating-wrap mb-2">
              <ul className="rating-stars list-unstyled d-flex text-warning small mb-0">
                <li><i className="fa fa-star"></i></li>
                <li><i className="fa fa-star"></i></li>
                <li><i className="fa fa-star"></i></li>
                <li><i className="fa fa-star"></i></li>
                <li><i className="fa fa-star-half-alt"></i></li>
                <li className="text-muted ms-1">(4.5)</li>
              </ul>
            </div>

            <div className="price-wrap mb-2">
              <span className="price text-danger fw-bold h5">{(p.specialPrice || p.price).toLocaleString()} ₫</span>
              {p.specialPrice && <del className="text-muted ms-2 small">{p.price.toLocaleString()} ₫</del>}
            </div>

            <div className="d-flex justify-content-between align-items-center small text-muted mb-3 border-top pt-2">
              <span><i className="fa fa-shopping-cart me-1"></i>Đã bán: {p.sold || 0}</span>
            </div>

            <Link to={`/product/${productId}`} className="btn btn-primary w-100 rounded-pill btn-sm fw-bold">
               MUA NGAY
            </Link>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="main-content-wrapper bg-white pb-5">

      {/* SECTION 1: TOP TRENDING */}
      <section className="container pt-5">
        <div className="text-center mb-5">
          <h2 className="fw-bold text-uppercase" style={{ letterSpacing: '2px' }}>
            <span className="text-primary border-bottom border-3 border-primary pb-2">Top Selling</span> Cosmetics
          </h2>
          <p className="text-muted mt-3 italic">Những siêu phẩm chăm sóc da đang "làm mưa làm gió" cộng đồng làm đẹp</p>
        </div>
        <div className="row">
          {topProducts.map((p, i) => <ProductItem key={`top-${i}`} p={p} index={i} isTop={true} />)}
        </div>
      </section>

      {/* SECTION 2: DISCOVER ALL */}
      <section className="container mt-5 pt-5">
        <div className="d-flex justify-content-between align-items-center mb-4 border-start border-primary border-4 ps-3">
          <div>
            <h3 className="fw-bold text-dark mb-0 text-uppercase">Tất cả sản phẩm</h3>
            <p className="text-muted mb-0">Cam kết 100% chính hãng từ các thương hiệu hàng đầu</p>
          </div>
          <div className="d-none d-md-block">
             <select className="form-select form-select-sm rounded-pill">
                <option>Mới nhất</option>
                <option>Giá thấp đến cao</option>
                <option>Bán chạy nhất</option>
             </select>
          </div>
        </div>
        <div className="row">
          {allProducts.map((p, i) => <ProductItem key={`all-${i}`} p={p} isTop={false} />)}
        </div>
        <div className="text-center mt-5">
          <button className="btn btn-outline-primary px-5 rounded-pill fw-bold" onClick={loadMoreProducts} disabled={loading}>
            {loading ? <><span className="spinner-border spinner-border-sm me-2"></span>Đang tải</> : "XEM THÊM SẢN PHẨM"}
          </button>
        </div>
      </section>

      {/* --- SECTION BÀI VIẾT SKINCARE --- */}
      <section className="container mt-5 pt-5 border-top">
        <div className="text-center mb-5">
          <h3 className="fw-bold text-dark mb-2">
            <i className="fa fa-leaf me-2 text-success"></i>GÓC CHUYÊN GIA SKINCARE
          </h3>
          <p className="text-muted">Kiến thức chăm sóc da khoa học giúp bạn rạng rỡ mỗi ngày</p>
        </div>

        <div className="row">
          {posts.length > 0 ? (
            posts.slice(0, 3).map((post, idx) => (
              <div key={idx} className="col-md-4 mb-4">
                <div className="card border-0 shadow-sm h-100 overflow-hidden post-card-beauty">
                  <div className="position-relative overflow-hidden">
                    <img src={post.img} className="card-img-top transition-img" alt={post.title} style={{height: '220px', objectFit: 'cover'}} />
                    <div className="category-tag bg-primary text-white px-3 py-1 position-absolute bottom-0 start-0 m-3 rounded-pill small">Skincare Tips</div>
                  </div>
                  <div className="card-body p-4">
                    <div className="d-flex align-items-center mb-2">
                      <img src="https://i.pravatar.cc/30" className="rounded-circle me-2" alt="admin" />
                      <small className="text-muted">Bởi Admin • {post.date}</small>
                    </div>
                    <h6 className="card-title fw-bold mb-3 text-dark" style={{ lineHeight: '1.5', height: '48px', overflow: 'hidden' }}>
                      {post.title}
                    </h6>
                    <p className="card-text text-muted small mb-3 text-truncate-2">
                      {post.desc}
                    </p>
                    <a href={post.link} target="_blank" rel="noopener noreferrer" 
                       className="text-primary text-decoration-none fw-bold small">
                      ĐỌC TIẾP <i className="fa fa-arrow-right ms-1"></i>
                    </a>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div className="col-12 text-center py-5">
              <div className="spinner-border text-primary mb-3" role="status"></div>
              <p className="text-muted">Đang cập nhật kiến thức làm đẹp cho bạn...</p>
            </div>
          )}
        </div>
      </section>
    </div>
  );
};

export default ProductSold;
