import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import productService from "../../services/productService";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductDetail = () => {
  const { id } = useParams(); // Lấy id từ URL
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!id) return;

    const fetchProduct = async () => {
      try {
        const data = await productService.getProductById(id);
        console.log("Product loaded:", data);
        setProduct(data);
      } catch (error) {
        console.error("❌ Không tìm thấy sản phẩm:", error);
        setProduct(null);
      } finally {
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  if (loading) return <h2>Loading...</h2>;
  if (!product) return <h2>❌ Không tìm thấy sản phẩm!</h2>;

  return (
    <div className="container py-5">
      <div className="row">
        <div className="col-md-6">
          {product.image ? (
            <img
              src={`${IMAGE_BASE_URL}/${product.image}`}
              alt={product.name || product.product_name}
              onError={(e) =>
                (e.target.src =
                  "https://cdn-icons-png.flaticon.com/512/679/679922.png")
              }
              style={{ width: "100%", borderRadius: "10px" }}
            />
          ) : (
            <img
              src="https://cdn-icons-png.flaticon.com/512/679/679922.png"
              alt="No Image"
              style={{ width: "100%", borderRadius: "10px" }}
            />
          )}
        </div>

        <div className="col-md-6">
          <h2>{product.name || product.product_name}</h2>
          <p className="text-muted">
            {product.description || "Không có mô tả"}
          </p>
          <h4 className="text-danger">
            {product.special_price
              ? `${product.special_price.toLocaleString()} ₫`
              : `${product.price.toLocaleString()} ₫`}
          </h4>

          {product.discount > 0 && (
            <small className="text-success">
              Giảm {product.discount}% so với giá gốc
            </small>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
