import { useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import productService from "../../services/productService";
import cartService from "../../services/cartService";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductDetail = () => { 
  const { productId } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [addingToCart, setAddingToCart] = useState(false);

  const currentUser = JSON.parse(localStorage.getItem('user')) || { email: 'guest@example.com' };

  useEffect(() => {
    console.log("üîÑ Product ID t·ª´ URL:", productId, "Type:", typeof productId);

    const fetchProductDetail = async () => {
      if (!productId || productId === 'undefined' || productId === 'null') {
        setError("Kh√¥ng t√¨m th·∫•y ID s·∫£n ph·∫©m t·ª´ URL");
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        setError(null);
        console.log("üì° G·ªçi API v·ªõi ID:", productId);

        const productData = await productService.getProductById(productId);
        console.log("‚úÖ D·ªØ li·ªáu nh·∫≠n ƒë∆∞·ª£c:", productData);

        setProduct(productData);
      } catch (err) {
        console.error("‚ùå L·ªói khi t·∫£i chi ti·∫øt s·∫£n ph·∫©m:", err);
        setError(`Kh√¥ng th·ªÉ t·∫£i th√¥ng tin s·∫£n ph·∫©m: ${err.response?.data?.message || err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchProductDetail();
  }, [productId]);

 // Trong handleAddToCart
const handleAddToCart = async () => {
  // 1. Ki·ªÉm tra ƒëƒÉng nh·∫≠p th·∫≠t s·ª± t·ª´ localStorage
  const storedUser = JSON.parse(localStorage.getItem('user'));

  if (!storedUser || !storedUser.email) {
    Swal.fire({
      icon: 'warning',
      title: 'Ch∆∞a ƒëƒÉng nh·∫≠p',
      text: 'Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ th·ª±c hi·ªán ch·ª©c nƒÉng n√†y!',
      confirmButtonColor: '#ff6a00'
    });
    navigate('/login');
    return;
  }

  if (productQuantity === 0) {
    Swal.fire('H·∫øt h√†ng', 'S·∫£n ph·∫©m hi·ªán t·∫°i kh√¥ng c√≤n trong kho', 'error');
    return;
  }

  try {
    setAddingToCart(true);

    // 2. L·∫•y gi·ªè h√†ng c·ªßa user
    // L∆∞u √Ω: ƒê·∫£m b·∫£o cartService.getActiveCart tr·∫£ v·ªÅ ƒë√∫ng object c√≥ cartId
    let cart = await cartService.getActiveCart(storedUser.email);

    if (!cart || !cart.cartId) {
       throw new Error("Kh√¥ng t√¨m th·∫•y gi·ªè h√†ng c·ªßa ng∆∞·ªùi d√πng.");
    }

    // 3. G·ªçi API th√™m v√†o gi·ªè
    await cartService.addToCart(cart.cartId, productId, 1);

    // ‚úÖ TH√îNG B√ÅO TH√ÄNH C√îNG B·∫∞NG SWEETALERT2
    Swal.fire({
      icon: 'success',
      title: 'ƒê√£ th√™m v√†o gi·ªè!',
      showConfirmButton: false,
      timer: 1500
    });

    // ‚úÖ B·∫ÆN S·ª∞ KI·ªÜN ƒê·ªÇ HEADER C·∫¨P NH·∫¨T S·ªê L∆Ø·ª¢NG (cartCount)
    window.dispatchEvent(new Event("cartUpdated"));

  } catch (error) {
    console.error('‚ùå L·ªói chi ti·∫øt:', error);
    Swal.fire({
      icon: 'error',
      title: 'L·ªói gi·ªè h√†ng',
      text: error.response?.data?.message || "Kh√¥ng th·ªÉ th√™m v√†o gi·ªè h√†ng l√∫c n√†y",
    });
  } finally {
    setAddingToCart(false);
  }
};

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">ƒêang t·∫£i th√¥ng tin s·∫£n ph·∫©m...</p>
        </div>
      </div>
    );
  }

  if (error || !product) {
    return (
      <div className="container mt-4">
        <div className="alert alert-danger text-center">
          <h4>‚ùå Kh√¥ng th·ªÉ t·∫£i s·∫£n ph·∫©m</h4>
          <p>{error || "S·∫£n ph·∫©m kh√¥ng t·ªìn t·∫°i"}</p>
          <p className="small text-muted">Product ID t·ª´ URL: <code>"{productId}"</code></p>
          <div className="mt-3">
