import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../../services/userService";

const EditProfile = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    first_name: "",
    last_name: "",
    email: "",
    mobile_number: "",
  });
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);

  // ‚úÖ KI·ªÇM TRA AUTH
  useEffect(() => {
    const checkAuth = () => {
      try {
        const token = localStorage.getItem("token");
        const userData = localStorage.getItem("user");

        if (!token || !userData) {
          alert("‚ùå Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ ti·∫øp t·ª•c!");
          navigate("/login");
          return false;
        }

        const user = JSON.parse(userData);
        const userId = user.userId;

        console.log("üë§ User ID:", userId);

        if (typeof userId !== 'number' || userId <= 0) {
          alert("‚ö†Ô∏è T√†i kho·∫£n t·∫°m th·ªùi. Vui l√≤ng ƒëƒÉng nh·∫≠p v·ªõi t√†i kho·∫£n th·ª±c!");
          navigate("/login");
          return false;
        }

        return { token, user, userId };
      } catch (error) {
        console.error("‚ùå Auth check error:", error);
        return false;
      }
    };

    const loadUserData = async () => {
      const auth = checkAuth();
      if (!auth) return;

      const { user: userData, userId } = auth;
      setLoading(true);

      try {
        console.log("üì° Loading user data from API for ID:", userId);
        const userProfile = await userService.getUserById(userId);

        console.log("‚úÖ User data loaded from API:", userProfile);

        // ‚úÖ Set form data v·ªõi d·ªØ li·ªáu t·ª´ database
        setForm({
          first_name: userProfile.first_name || userProfile.firstName || "",
          last_name: userProfile.last_name || userProfile.lastName || "",
          email: userProfile.email || "",
          mobile_number: userProfile.mobile_number || userProfile.mobileNumber || ""
        });

      } catch (error) {
        console.error("‚ùå Failed to load user data:", error);
        alert("‚ùå Kh√¥ng th·ªÉ t·∫£i th√¥ng tin ng∆∞·ªùi d√πng!");

        // Fallback to localStorage data
        setForm({
          first_name: userData.first_name || userData.firstName || "",
          last_name: userData.last_name || userData.lastName || "",
          email: userData.email || "",
          mobile_number: userData.mobile_number || userData.mobileNumber || ""
        });
      } finally {
        setLoading(false);
      }
    };

    loadUserData();
  }, [navigate]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validation
    if (!form.first_name.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p h·ªç!");
      return;
    }

    if (!form.last_name.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p t√™n!");
      return;
    }

    if (!form.email.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p email!");
      return;
    }

    if (!/\S+@\S+\.\S+/.test(form.email)) {
      alert("‚ùå Email kh√¥ng h·ª£p l·ªá!");
      return;
    }

    setSaving(true);

    try {
      // ‚úÖ L·∫•y user ID t·ª´ localStorage
      const userData = JSON.parse(localStorage.getItem("user"));
      const userId = userData.userId;

      if (typeof userId !== 'number' || userId <= 0) {
        alert("‚ùå User ID kh√¥ng h·ª£p l·ªá!");
        return;
      }

      console.log("üíæ Saving to database with user ID:", userId);

      // ‚úÖ Chu·∫©n b·ªã d·ªØ li·ªáu ƒë·ªÉ g·ª≠i l√™n API
      const updateData = {
        first_name: form.first_name,
        last_name: form.last_name,
        email: form.email,
        mobile_number: form.mobile_number
      };

      console.log("üì§ Sending update data:", updateData);

      // ‚úÖ G·ªçi API update
      const response = await userService.updateUser(userId, updateData);

      console.log("‚úÖ DATABASE UPDATE SUCCESS:", response);

      // ‚úÖ C·∫≠p nh·∫≠t localStorage v·ªõi d·ªØ li·ªáu m·ªõi
      const updatedUser = { 
        ...userData,
        first_name: form.first_name,
        last_name: form.last_name,
        email: form.email,
        mobile_number: form.mobile_number,
        firstName: form.first_name, // Gi·ªØ camelCase cho t∆∞∆°ng th√≠ch
        lastName: form.last_name,
        mobileNumber: form.mobile_number
      };
      localStorage.setItem("user", JSON.stringify(updatedUser));

      alert("‚úÖ C·∫≠p nh·∫≠t h·ªì s∆° th√†nh c√¥ng!");

      // Quay v·ªÅ trang tr∆∞·ªõc
      navigate(-1);

    } catch (error) {
      console.error("‚ùå UPDATE FAILED:", error);

      let errorMessage = "Kh√¥ng th·ªÉ c·∫≠p nh·∫≠t th√¥ng tin";

      if (error.response) {
        const status = error.response.status;
        const data = error.response.data;

        console.log("üö® API Error:", { status, data });

        switch (status) {
          case 500:
            errorMessage = "L·ªói server. Vui l√≤ng th·ª≠ l·∫°i sau.";
            break;
          case 404:
            errorMessage = "Kh√¥ng t√¨m th·∫•y ng∆∞·ªùi d√πng";
            break;
          case 401:
            errorMessage = "Phi√™n ƒëƒÉng nh·∫≠p ƒë√£ h·∫øt h·∫°n";
            break;
          case 400:
            errorMessage = "D·ªØ li·ªáu kh√¥ng h·ª£p l·ªá: " + (data?.message || "Vui l√≤ng ki·ªÉm tra l·∫°i th√¥ng tin");
            break;
          default:
            errorMessage = data?.message || `L·ªói: ${status}`;
        }
      } else if (error.request) {
        errorMessage = "Kh√¥ng th·ªÉ k·∫øt n·ªëi ƒë·∫øn server. Vui l√≤ng ki·ªÉm tra k·∫øt n·ªëi m·∫°ng.";
      }

      alert(`‚ùå ${errorMessage}`);
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <section className="section-content padding-y">
        <div className="card mx-auto shadow" style={{ maxWidth: "520px", marginTop: "40px" }}>
          <article className="card-body text-center py-5">
            <div className="spinner-border text-primary mb-3" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p className="mb-0">ƒêang t·∫£i th√¥ng tin h·ªì s∆°...</p>
          </article>
        </div>
      </section>
    );
  }

  return (
    <section className="section-content padding-y">
      <div className="card mx-auto shadow" style={{ maxWidth: "520px", marginTop: "40px" }}>
        <article className="card-body">
          <header className="mb-4 text-center">
            <h4 className="card-title">Ch·ªânh s·ª≠a h·ªì s∆°</h4>
          </header>

          <form onSubmit={handleSubmit}>
            {/* T√™n */}
            <div className="form-row">
              <div className="col form-group">
                <label>First name</label>
                <input
                  name="first_name"
                  type="text"
                  className="form-control"
                  value={form.first_name}
                  onChange={handleChange}
                  required
                  disabled={saving}
                />
              </div>
              <div className="col form-group">
                <label>Last name</label>
                <input
                  name="last_name"
                  type="text"
                  className="form-control"
                  value={form.last_name}
                  onChange={handleChange}
                  required
                  disabled={saving}
                />
              </div>
            </div>

            {/* Email */}
            <div className="form-group">
              <label>Email</label>
              <input
