import { useEffect, useState } from "react";
import { Link, useLocation  } from "react-router-dom";
import productService from "../../services/productService";
import categoryService from "../../services/categoryService";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [pageNumber, setPageNumber] = useState(0);
  const [pageSize, setPageSize] = useState(8);
  const [totalPages, setTotalPages] = useState(0);
  const [loading, setLoading] = useState(false);
  // const location = useLocation();


  // State cho b·ªô l·ªçc
  const [filters, setFilters] = useState({
    categoryId: "",
    searchKeyword: ""
  });


  // üß© H√†m t·∫£i danh m·ª•c
  const fetchCategories = async () => {
    try {
      const response = await categoryService.getAllCategories();
      setCategories(response || []);
    } catch (err) {
      console.error("‚ùå L·ªói khi t·∫£i danh m·ª•c:", err);
    }
  };

  // üß© H√†m t·∫£i s·∫£n ph·∫©m c√≥ ph√¢n trang v√† l·ªçc
  const fetchProducts = async (page = 0, size = 8, filterParams = filters) => {
    try {
      setLoading(true);

      let response;

      // Logic g·ªçi API theo ƒëi·ªÅu ki·ªán l·ªçc
      if (filterParams.categoryId && filterParams.searchKeyword) {
        // L·ªçc theo c·∫£ danh m·ª•c v√† t·ª´ kh√≥a
        response = await productService.getProductsByCategoryAndSearch(
          filterParams.categoryId, 
          filterParams.searchKeyword, 
          page + 1, 
          size
        );
      } else if (filterParams.categoryId) {
        // L·ªçc theo danh m·ª•c
        response = await productService.getProductsByCategory(
          filterParams.categoryId, 
          page + 1, 
          size
        );
      } else if (filterParams.searchKeyword) {
        // T√¨m ki·∫øm theo t·ª´ kh√≥a
        response = await productService.searchProducts(
          filterParams.searchKeyword, 
          page + 1, 
          size
        );
      } else {
        // L·∫•y t·∫•t c·∫£ s·∫£n ph·∫©m
        response = await productService.getAllProducts(page + 1, size);
      }

      const data = response || {};
      const content = data.content || [];

      setProducts(content);
      setTotalPages(data.totalPages || 0);

    } catch (err) {
      console.error("‚ùå L·ªói khi t·∫£i s·∫£n ph·∫©m:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCategories();
    fetchProducts(pageNumber, pageSize);
  }, []);

   // ‚úÖ L·∫•y search parameter t·ª´ URL>>>>
  // useEffect(() => {
  //   const searchParams = new URLSearchParams(location.search);
  //   const searchKeyword = searchParams.get('search');
  //   const categoryId = searchParams.get('category');

  //   if (searchKeyword || categoryId) {
  //     setFilters({
  //       categoryId: categoryId || "",
  //       search: searchKeyword || ""
  //     });
  //   }
  // }, [location.search]);

  // üéØ X·ª≠ l√Ω khi thay ƒë·ªïi b·ªô l·ªçc
  useEffect(() => {
    setPageNumber(0); // Reset v·ªÅ trang ƒë·∫ßu khi thay ƒë·ªïi b·ªô l·ªçc
    fetchProducts(0, pageSize, filters);
  }, [filters]);

  // üß≠ X·ª≠ l√Ω khi chuy·ªÉn trang
  const handlePageChange = (newPage) => {
    if (newPage >= 0 && newPage < totalPages) {
      setPageNumber(newPage);
      fetchProducts(newPage, pageSize, filters);
    }
  };

  // üîç X·ª≠ l√Ω thay ƒë·ªïi danh m·ª•c
  const handleCategoryChange = (e) => {
    const categoryId = e.target.value;
    setFilters(prev => ({
      ...prev,
      categoryId: categoryId
    }));
  };

  // üîç X·ª≠ l√Ω t√¨m ki·∫øm theo t√™n
  const handleSearchChange = (e) => {
    const keyword = e.target.value;
    setFilters(prev => ({
      ...prev,
      searchKeyword: keyword
    }));
  };

  // üßπ X√≥a t·∫•t c·∫£ b·ªô l·ªçc
  const handleClearFilters = () => {
    setFilters({
      categoryId: "",
      searchKeyword: ""
    });
    setPageNumber(0);
  };

  return (
    <section className="padding-bottom container mt-5">
      <header className="section-heading mb-4 text-center">
        <h3 className="title-section text-uppercase fw-bold text-primary">
          üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m
        </h3>
        <p className="text-muted">Kh√°m ph√° danh s√°ch s·∫£n ph·∫©m ƒëa d·∫°ng</p>
      </header>

      {/* üéõÔ∏è B·ªò L·ªåC V√Ä T√åM KI·∫æM */}
      <div className="card shadow-sm mb-4">
        <div className="card-body">
          <div className="row g-3">
            {/* L·ªçc theo danh m·ª•c */}
            <div className="col-md-4">
              <label className="form-label fw-semibold">üìÇ L·ªçc theo danh m·ª•c</label>
              <select
                value={filters.categoryId}
                onChange={handleCategoryChange}
                className="form-select"
              >
                <option value="">T·∫•t c·∫£ danh m·ª•c</option>
                {categories.map((category) => (
                  <option key={category.categoryId} value={category.categoryId}>
                    {category.categoryName || category.category_name}
                  </option>
                ))}
              </select>
            </div>

            {/* T√¨m ki·∫øm theo t√™n */}
            <div className="col-md-5">
              <label className="form-label fw-semibold">üîç T√¨m ki·∫øm s·∫£n ph·∫©m</label>
              <input
                type="text"
                value={filters.searchKeyword}
                onChange={handleSearchChange}
                placeholder="Nh·∫≠p t√™n s·∫£n ph·∫©m..."
                className="form-control"
              />
            </div>

            {/* N√∫t x√≥a l·ªçc */}
            <div className="col-md-3 d-flex align-items-end">
              <button
                onClick={handleClearFilters}
                className="btn btn-outline-secondary w-100"
                disabled={!filters.categoryId && !filters.searchKeyword}
              >
                üßπ X√≥a l·ªçc
              </button>
            </div>
          </div>

          {/* Hi·ªÉn th·ªã th√¥ng tin l·ªçc hi·ªán t·∫°i */}
          {(filters.categoryId || filters.searchKeyword) && (
            <div className="mt-3 p-2 bg-light rounded">
              <small className="text-muted">
                <strong>B·ªô l·ªçc hi·ªán t·∫°i:</strong>
                {filters.categoryId && ` Danh m·ª•c: ${
                  categories.find(c => c.categoryId == filters.categoryId)?.categoryName
                }`}
                {filters.searchKeyword && ` T·ª´ kh√≥a: "${filters.searchKeyword}"`}
              </small>
            </div>
          )}
        </div>
      </div>

      {/* Th√¥ng tin ph√¢n trang */}
      <div className="d-flex justify-content-between align-items-center mb-3">
        <div className="text-muted small">
          {totalPages > 0 ? (
            <>üìÑ Trang {pageNumber + 1} / {totalPages} ‚Ä¢ {products.length} s·∫£n ph·∫©m</>
          ) : (
            "üìÑ ƒêang t·∫£i..."
          )}
        </div>

        <div className="d-flex align-items-center">
          <span className="fw-semibold text-primary">üìè 8 s·∫£n ph·∫©m / trang</span>
        </div>
      </div>

      {/* üß© Hi·ªÉn th·ªã s·∫£n ph·∫©m */}
      {loading ? (
        <div className="row">
          {[...Array(8)].map((_, index) => (
            <div key={index} className="col-xl-3 col-lg-4 col-md-6 col-12 mb-4">
              <div className="card card-product-grid shadow-sm h-100">
                <div className="img-wrap">
                  <div 
                    style={{
                      width: "100%",
                      height: "250px",
                      backgroundColor: "#f8f9fa",
                      borderRadius: "10px",
                      display: "flex",
                      alignItems: "center",
                      justifyContent: "center"
                    }}
                  >
                    <div className="spinner-border text-primary" role="status">
                      <span className="visually-hidden">Loading...</span>
                    </div>
                  </div>
                </div>

                <div className="info-wrap p-3">
                  <div className="placeholder-glow">
                    <span className="placeholder col-8 mb-2"></span>
                    <span className="placeholder col-6 mb-2"></span>
                    <span className="placeholder col-4"></span>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="row">
          {products.map((p) => {
            const productId = p.productId || p.product_id;

            return (
              <div
                key={productId}
                className="col-xl-3 col-lg-4 col-md-6 col-12 mb-4"
              >
                <div className="card card-product-grid shadow-sm h-100 position-relative">
                  {/* Badge s·ªë l∆∞·ª£ng b√°n */}
                  {(p.sold && p.sold > 0) && (
                    <div 
                      className="position-absolute top-0 end-0 m-2 badge bg-success"
                      style={{ zIndex: 10 }}
                    >
                      üì¶ ƒê√£ b√°n: {p.sold || 0}
                    </div>
                  )}

                  <div className="img-wrap position-relative">
                    <Link
                      to={`/product/${productId}`}
                      className="text-decoration-none"
                    >
                      <img
                        src={`${IMAGE_BASE_URL}/${p.image}`}
                        alt={p.productName || p.product_name}
                        onError={(e) =>
                          (e.target.src =
                            "https://cdn-icons-png.flaticon.com/512/679/679922.png")
                        }
                        style={{
                          width: "100%",
                          height: "250px",
                          objectFit: "cover",
                          borderRadius: "10px",
                        }}
                      />
                    </Link>

                    {/* Badge gi·∫£m gi√° */}
                    {p.discount > 0 && (
                      <div className="position-absolute bottom-0 start-0 m-2 badge bg-danger">
                        üî• Gi·∫£m {p.discount}%
                      </div>
                    )}
                  </div>

                  <div className="info-wrap p-3 d-flex flex-column">
                    <Link
                      to={`/product/${productId}`}
                      className="text-decoration-none"
                    >
                      <h6 className="text-truncate text-dark fw-bold">
                        {p.productName || p.product_name}
                      </h6>
                    </Link>

                    <p className="text-muted small mb-2 flex-grow-1">
                      {p.description?.substring(0, 40) ||
                        "ƒêang c·∫≠p nh·∫≠t m√¥ t·∫£..."}
                    </p>

                    {/* Th√¥ng tin l∆∞·ª£t xem */}
                    <div className="mb-2">
                      <small className="text-muted">
                        üëÅÔ∏è {p.views || 0} l∆∞·ª£t xem
                      </small>
                    </div>

                    <div className="price-wrapper mb-1">
                      {p.specialPrice ? (
                        <>
                          <span className="price h5 text-danger mb-0">
                            {p.specialPrice.toLocaleString()} ‚Ç´
                          </span>
                          <span className="text-muted text-decoration-line-through small ms-2">
                            {p.price.toLocaleString()} ‚Ç´
                          </span>
                        </>
                      ) : (
                        <span className="price h5 text-danger mb-0">
                          {p.price.toLocaleString()} ‚Ç´
                        </span>
                      )}
                    </div>

                    {/* Tr·∫°ng th√°i t·ªìn kho */}
                    <small
                      className={`mt-1 ${
                        p.quantity === 0 ? "text-danger" : "text-success"
                      }`}
                    >
                      {p.quantity === 0
                        ? "‚ùå H·∫øt h√†ng"
                        : `üì¶ C√≤n ${p.quantity} s·∫£n ph·∫©m`}
                    </small>

                    {/* N√∫t h√†nh ƒë·ªông */}
                    <div className="d-grid gap-2 mt-3">
                      <Link 
                        to={`/product/${productId}`}
                        className={`btn btn-primary btn-sm ${
                          p.quantity === 0 ? 'disabled' : ''
                        }`}
                      >
                        {p.quantity === 0 ? 'H·∫øt h√†ng' : 'üõí Mua ngay'}
                      </Link>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}

      {/* üì≠ Kh√¥ng c√≥ s·∫£n ph·∫©m */}
      {!loading && products.length === 0 && (
        <div className="text-center py-5">
          <h4>üì≠ Kh√¥ng t√¨m th·∫•y s·∫£n ph·∫©m</h4>
          <p className="text-muted">
            {filters.categoryId || filters.searchKeyword 
              ? "Kh√¥ng c√≥ s·∫£n ph·∫©m n√†o ph√π h·ª£p v·ªõi b·ªô l·ªçc c·ªßa b·∫°n."
              : "Hi·ªán t·∫°i kh√¥ng c√≥ s·∫£n ph·∫©m n√†o ƒë·ªÉ hi·ªÉn th·ªã."
            }
          </p>
          {(filters.categoryId || filters.searchKeyword) && (
            <button
              onClick={handleClearFilters}
              className="btn btn-primary mt-2"
            >
              Hi·ªÉn th·ªã t·∫•t c·∫£ s·∫£n ph·∫©m
            </button>
          )}
        </div>
      )}

      {/* üìë PH√ÇN TRANG - Ch·ªâ hi·ªÉn th·ªã khi c√≥ nhi·ªÅu h∆°n 1 trang */}
      {totalPages > 1 && (
        <div className="d-flex justify-content-center align-items-center mt-4">
          <button
            className="btn btn-outline-primary me-2"
            disabled={pageNumber === 0}
            onClick={() => handlePageChange(pageNumber - 1)}
          >
            ‚Üê Tr∆∞·ªõc
          </button>

          {Array.from({ length: Math.min(3, totalPages) }, (_, i) => {
            let pageToShow;

            if (totalPages <= 3) {
              pageToShow = i;
            } else if (pageNumber === 0) {
              pageToShow = i;
            } else if (pageNumber === totalPages - 1) {
              pageToShow = totalPages - 3 + i;
            } else {
              pageToShow = pageNumber - 1 + i;
            }

            if (pageToShow >= 0 && pageToShow < totalPages) {
              return (
                <button
                  key={pageToShow}
                  className={`btn mx-1 ${
                    pageToShow === pageNumber
                      ? "btn-primary text-white"
                      : "btn-outline-primary"
                  }`}
                  onClick={() => handlePageChange(pageToShow)}
                >
                  {pageToShow + 1}
                </button>
              );
            }
            return null;
          })}

          <button
            className="btn btn-outline-primary ms-2"
            disabled={pageNumber === totalPages - 1}
            onClick={() => handlePageChange(pageNumber + 1)}
          >
            Sau ‚Üí
          </button>
        </div>
      )}
    </section>
  );
};

export default ProductList;
