import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../../services/userService";

const EditProfile = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    first_name: "",
    last_name: "",
    email: "",
    mobile_number: "",
  });
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    const loadUserData = async () => {
      try {
        const token = localStorage.getItem("token");
        const userData = localStorage.getItem("user");

        if (!token || !userData) {
          alert("‚ùå Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ ti·∫øp t·ª•c!");
          navigate("/login");
          return;
        }

        const user = JSON.parse(userData);
        const currentUserId = user.userId;

        console.log("üë§ Current User ID:", currentUserId, `(type: ${typeof currentUserId})`);
        setUserId(currentUserId);

        // ‚úÖ Ch·ªâ load t·ª´ API n·∫øu user ID h·ª£p l·ªá
        if (typeof currentUserId === 'number' && currentUserId > 0) {
          setLoading(true);
          console.log("üì° Loading user data from API...");

          const userProfile = await userService.getUserById(currentUserId);
          console.log("‚úÖ API Response:", userProfile);

          setForm({
            first_name: userProfile.first_name || userProfile.firstName || "",
            last_name: userProfile.last_name || userProfile.lastName || "",
            email: userProfile.email || "",
            mobile_number: userProfile.mobile_number || userProfile.mobileNumber || ""
          });
        } else {
          // D√πng data t·ª´ localStorage
          setForm({
            first_name: user.first_name || user.firstName || "",
            last_name: user.last_name || user.lastName || "",
            email: user.email || "",
            mobile_number: user.mobile_number || user.mobileNumber || ""
          });
        }

      } catch (error) {
        console.error("‚ùå Failed to load user data:", error);
        alert("‚ùå Kh√¥ng th·ªÉ t·∫£i th√¥ng tin ng∆∞·ªùi d√πng!");
      } finally {
        setLoading(false);
      }
    };

    loadUserData();
  }, [navigate]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validation
    if (!form.first_name.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p h·ªç!");
      return;
    }

    if (!form.last_name.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p t√™n!");
      return;
    }

    if (!form.email.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p email!");
      return;
    }

    if (!/\S+@\S+\.\S+/.test(form.email)) {
      alert("‚ùå Email kh√¥ng h·ª£p l·ªá!");
      return;
    }

    setSaving(true);

    try {
      const userData = JSON.parse(localStorage.getItem("user"));
      const currentUserId = userData.userId;

      console.log("üöÄ STARTING UPDATE...");
      console.log("üì¶ Data to update:", form);
      console.log("üîë User ID:", currentUserId);

      // ‚úÖ TEST: Th·ª≠ v·ªõi c√°c endpoint kh√°c nhau
      let response;

      try {
        // Th·ª≠ endpoint 1: /public/users/{id}
        console.log("üîÑ Trying endpoint: /public/users/" + currentUserId);
        response = await userService.updateUser(currentUserId, form);
      } catch (error1) {
        console.log("‚ùå Endpoint 1 failed, trying endpoint 2...");

        // Th·ª≠ endpoint 2: /users/{id} 
        try {
          // T·∫°m th·ªùi override updateUser ƒë·ªÉ test endpoint kh√°c
          const token = localStorage.getItem("token");
          const testResponse = await fetch(`http://localhost:8080/api/users/${currentUserId}`, {
            method: 'PUT',
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            },
            body: JSON.stringify(form)
          });

          if (testResponse.ok) {
            response = await testResponse.json();
            console.log("‚úÖ Endpoint 2 success!");
          } else {
            throw new Error(`HTTP ${testResponse.status}`);
          }
        } catch (error2) {
          console.log("‚ùå All endpoints failed");
          throw error1; // Throw l·ªói g·ªëc
        }
      }

      console.log("‚úÖ UPDATE SUCCESS:", response);

      // ‚úÖ C·∫≠p nh·∫≠t localStorage
      const updatedUser = { 
        ...userData,
        first_name: form.first_name,
        last_name: form.last_name,
        email: form.email,
        mobile_number: form.mobile_number
      };
      localStorage.setItem("user", JSON.stringify(updatedUser));

      alert("‚úÖ C·∫≠p nh·∫≠t h·ªì s∆° th√†nh c√¥ng!");
      navigate("/profile"); // Quay v·ªÅ trang profile

    } catch (error) {
      console.error("‚ùå UPDATE FAILED:", error);

      // ‚úÖ Debug chi ti·∫øt l·ªói
      if (error.response) {
        console.log("üîç Error Details:", {
          status: error.response.status,
          statusText: error.response.statusText,
          data: error.response.data,
          headers: error.response.headers
        });

        // Hi·ªÉn th·ªã l·ªói t·ª´ server n·∫øu c√≥
        const serverMessage = error.response.data?.message || error.response.data;
        alert(`‚ùå L·ªói server: ${serverMessage || 'Vui l√≤ng th·ª≠ l·∫°i sau'}`);
      } else if (error.request) {
        console.log("üîç No response received:", error.request);
        alert("‚ùå Kh√¥ng nh·∫≠n ƒë∆∞·ª£c ph·∫£n h·ªìi t·ª´ server");
      } else {
        console.log("üîç Request error:", error.message);
        alert(`‚ùå L·ªói: ${error.message}`);
      }
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <section className="section-content padding-y">
        <div className="card mx-auto shadow" style={{ maxWidth: "520px", marginTop: "40px" }}>
          <article className="card-body text-center py-5">
            <div className="spinner-border text-primary mb-3" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p className="mb-0">ƒêang t·∫£i th√¥ng tin h·ªì s∆°...</p>
          </article>
        </div>
      </section>
    );
  }

  return (
    <section className="section-content padding-y">
      <div className="card mx-auto shadow" style={{ maxWidth: "520px", marginTop: "40px" }}>
        <article className="card-body">
          <header className="mb-4 text-center">
            <h4 className="card-title">Ch·ªânh s·ª≠a h·ªì s∆°</h4>
            {userId && (
              <small className="text-muted d-block mt-1">
                User ID: <strong>{userId}</strong>
              </small>
            )}
          </header>

          <form onSubmit={handleSubmit}>
            <div className="form-row">
              <div className="col form-group">
                <label>First name *</label>
                <input
                  name="first_name"
                  type="text"
                  className="form-control"
                  value={form.first_name}
                  onChange={handleChange}
                  required
                  disabled={saving}
                />
              </div>
              <div className="col form-group">
                <label>Last name *</label>
                <input
                  name="last_name"
                  type="text"
                  className="form-control"
                  value={form.last_name}
                  onChange={handleChange}
                  required
                  disabled={saving}
                />
              </div>
            </div>

            <div className="form-group">
              <label>Email *</label>
              <input
                name="email"
                type="email"
                className="form-control"
                value={form.email}
                onChange={handleChange}
                required
                disabled={saving}
              />
            </div>

            <div className="form-group">
              <label>Mobile number</label>
              <input
                name="mobile_number"
                type="text"
                className="form-control"
                value={form.mobile_number}
                onChange={handleChange}
                disabled={saving}
                placeholder="+84 123 456 789"
              />
            </div>

            <div className="form-group d-flex gap-2">
