import React, { useEffect, useState } from "react";
import productService from "../../services/productService";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const data = await productService.getAllProducts();
        // Nếu data là object, lấy mảng products hoặc gán mảng rỗng
        const productsArray = Array.isArray(data) ? data : data.products || [];
        setProducts(productsArray);
      } catch (error) {
        console.error("❌ Lỗi khi tải sản phẩm:", error);
        setProducts([]);
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  if (loading) {
    return <div>Đang tải sản phẩm...</div>;
  }

  if (!products.length) {
    return <div>Không có sản phẩm nào.</div>;
  }

  return (
    <div className="product-list grid grid-cols-1 md:grid-cols-3 gap-4">
      {products.map((product) => (
        <div key={product.productId || product_id} className="product-card border p-4 rounded">
          <img
            src={productService.getProductImage(product.image)}
            alt={product.productName}
            className="w-full h-48 object-cover mb-2"
          />
          <h3 className="text-lg font-bold">{product.productName}</h3>
          <p className="text-gray-600">{product.description}</p>
          <p className="text-red-500 font-semibold">
            Giá: {product.specialPrice?.toLocaleString()} VND
          </p>
        </div>
      ))}
    </div>
  );
};

export default ProductList;
