import React, { useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import productService from "../../services/productService";
import cartService from "../../services/cartService";
import Swal from "sweetalert2"; // ƒê·∫£m b·∫£o ƒë√£ import Swal

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductDetail = () => {
  const { productId } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [addingToCart, setAddingToCart] = useState(false);

  // L·∫•y user tr·ª±c ti·∫øp t·ª´ localStorage ƒë·ªÉ ƒë·∫£m b·∫£o d·ªØ li·ªáu m·ªõi nh·∫•t
  const getStoredUser = () => JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchProductDetail = async () => {
      if (!productId || productId === 'undefined' || productId === 'null') {
        setError("Kh√¥ng t√¨m th·∫•y ID s·∫£n ph·∫©m");
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        const productData = await productService.getProductById(productId);
        setProduct(productData);
      } catch (err) {
        console.error("‚ùå L·ªói t·∫£i s·∫£n ph·∫©m:", err);
        setError("Kh√¥ng th·ªÉ t·∫£i th√¥ng tin s·∫£n ph·∫©m.");
      } finally {
        setLoading(false);
      }
    };

    fetchProductDetail();
  }, [productId]);

  const handleAddToCart = async () => {
  // 1. L·∫•y th√¥ng tin User m·ªõi nh·∫•t
  const user = JSON.parse(localStorage.getItem('user'));

  if (!user || !user.email) {
    Swal.fire('Th√¥ng b√°o', 'Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ mua h√†ng', 'warning');
    navigate('/login');
    return;
  }

  try {
    setAddingToCart(true);

    // 2. L·∫•y gi·ªè h√†ng (H√†m n√†y trong cartService m·ªõi ƒë√£ t·ª± ƒë·ªông t·∫°o n·∫øu ch∆∞a c√≥)
    const cart = await cartService.getActiveCart(user.email);
    console.log("üì¶ Gi·ªè h√†ng hi·ªán t·∫°i:", cart);

    if (!cart || !cart.cartId) {
      throw new Error("Kh√¥ng l·∫•y ƒë∆∞·ª£c m√£ gi·ªè h√†ng (CartID)");
    }

    // 3. Chu·∫©n h√≥a ID s·∫£n ph·∫©m (Chuy·ªÉn v·ªÅ s·ªë nguy√™n)
    const cleanProductId = parseInt(productId);
    if (isNaN(cleanProductId)) {
      throw new Error("ID s·∫£n ph·∫©m kh√¥ng h·ª£p l·ªá");
    }

    // 4. G·ªçi API th√™m v√†o gi·ªè
    console.log(`üì° G·ª≠i API: CartID=${cart.cartId}, ProductID=${cleanProductId}`);
    await cartService.addToCart(cart.cartId, cleanProductId, 1);

    // 5. Th√†nh c√¥ng
    Swal.fire({
      icon: 'success',
      title: 'ƒê√£ th√™m v√†o gi·ªè h√†ng!',
      showConfirmButton: false,
      timer: 1500
    });

    // C·∫≠p nh·∫≠t s·ªë l∆∞·ª£ng tr√™n Header ngay l·∫≠p t·ª©c
    window.dispatchEvent(new Event("cartUpdated"));

  } catch (error) {
    console.error('‚ùå L·ªói chi ti·∫øt:', error);

    // B·∫Øt l·ªói c·ª• th·ªÉ t·ª´ Server
    const serverMessage = error.response?.data?.message || error.message;

    Swal.fire({
      icon: 'error',
      title: 'Th·∫•t b·∫°i',
      text: `L·ªói: ${serverMessage}`,
      confirmButtonColor: '#ff6a00'
    });
  } finally {
    setAddingToCart(false);
  }
};

  if (loading) {
    return (
      <div className="container mt-5 text-center">
        <div className="spinner-border" style={{ color: "#ff6a00" }} role="status"></div>
        <p className="mt-2">ƒêang t·∫£i s·∫£n ph·∫©m...</p>
      </div>
    );
  }

  if (error || !product) {
    return (
      <div className="container mt-5">
        <div className="alert alert-danger text-center shadow-sm">
          <h4>Th√¥ng tin l·ªói</h4>
          <p>{error || "S·∫£n ph·∫©m kh√¥ng t·ªìn t·∫°i."}</p>
          <button className="btn btn-primary mt-3" onClick={() => navigate('/')}>Quay l·∫°i trang ch·ªß</button>
        </div>
      </div>
    );
  }

  const productName = product.productName || product.product_name;
  const productPrice = product.price;
  const productSpecialPrice = product.specialPrice || product.special_price;
  const productQuantity = product.quantity;
  const productDiscount = product.discount;

  return (
    <div className="container mt-4 mb-5">
      <nav aria-label="breadcrumb" className="mb-4">
        <ol className="breadcrumb">
          <li className="breadcrumb-item"><Link to="/">Trang ch·ªß</Link></li>
          <li className="breadcrumb-item"><Link to="/product">S·∫£n ph·∫©m</Link></li>
          <li className="breadcrumb-item active">{productName}</li>
        </ol>
      </nav>

      <div className="row g-4">
        {/* H√¨nh ·∫£nh */}
        <div className="col-md-6">
          <div className="card border-0 shadow-sm overflow-hidden" style={{ borderRadius: "15px" }}>
            <img
              src={`${IMAGE_BASE_URL}/${product.image}`}
              alt={productName}
              onError={(e) => (e.target.src = "https://via.placeholder.com/500")}
              className="img-fluid"
              style={{ minHeight: "450px", objectFit: "contain", backgroundColor: "#fff" }}
            />
          </div>
        </div>

        {/* Th√¥ng tin chi ti·∫øt */}
        <div className="col-md-6">
          <div className="p-3">
