      setCart(cartData);
    } catch (error) {
      console.error('‚ùå L·ªói khi t·∫£i gi·ªè h√†ng:', error);
      alert('‚ùå L·ªói khi t·∫£i gi·ªè h√†ng: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  const updateQuantity = async (productId, newQuantity) => {
    if (newQuantity < 1) return;

    try {
      setUpdating(true);
      await cartService.updateQuantity(cart.cartId, productId, newQuantity);
      await loadCart(); // T·∫£i l·∫°i gi·ªè h√†ng
    } catch (error) {
      console.error('‚ùå L·ªói khi c·∫≠p nh·∫≠t s·ªë l∆∞·ª£ng:', error);
      alert('‚ùå L·ªói khi c·∫≠p nh·∫≠t s·ªë l∆∞·ª£ng: ' + (error.response?.data?.message || error.message));
    } finally {
      setUpdating(false);
    }
  };

  const removeFromCart = async (productId) => {
    if (!window.confirm('B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a s·∫£n ph·∫©m n√†y?')) return;

    try {
      setUpdating(true);
      await cartService.removeFromCart(cart.cartId, productId);
      await loadCart(); // T·∫£i l·∫°i gi·ªè h√†ng
    } catch (error) {
      console.error('‚ùå L·ªói khi x√≥a s·∫£n ph·∫©m:', error);
      alert('‚ùå L·ªói khi x√≥a s·∫£n ph·∫©m: ' + (error.response?.data?.message || error.message));
    } finally {
      setUpdating(false);
    }
  };

  const clearCart = async () => {
    if (!window.confirm('B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a to√†n b·ªô gi·ªè h√†ng?')) return;

    try {
      setUpdating(true);
      await cartService.clearCart(currentUser.email, cart.cartId);
      await loadCart(); // T·∫£i l·∫°i gi·ªè h√†ng
    } catch (error) {
      console.error('‚ùå L·ªói khi x√≥a gi·ªè h√†ng:', error);
      alert('‚ùå L·ªói khi x√≥a gi·ªè h√†ng: ' + (error.response?.data?.message || error.message));
    } finally {
      setUpdating(false);
    }
  };

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">ƒêang t·∫£i gi·ªè h√†ng...</p>
        </div>
      </div>
    );
  }

  if (!cart || !cart.products || cart.products.length === 0) {
    return (
      <div className="container mt-4">
        <div className="empty-cart text-center py-5">
          <div className="empty-cart-icon mb-4">
            <i className="fas fa-shopping-cart fa-4x text-muted"></i>
          </div>
          <h3 className="text-muted mb-3">Gi·ªè h√†ng tr·ªëng</h3>
          <p className="text-muted mb-4">B·∫°n ch∆∞a c√≥ s·∫£n ph·∫©m n√†o trong gi·ªè h√†ng</p>
          <Link to="/products" className="btn btn-primary btn-lg">
            <i className="fas fa-shopping-bag me-2"></i>
            Mua s·∫Øm ngay
          </Link>
        </div>
      </div>
    );
  }

  const totalAmount = cartService.calculateTotal(cart.products);
  const totalItems = cart.products.reduce((total, item) => total + (item.quantity || 1), 0);

  return (
    <div className="container mt-4">
      {/* Breadcrumb */}
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to="/" className="text-decoration-none">üè† Trang ch·ªß</Link>
          </li>
          <li className="breadcrumb-item active" aria-current="page">
            Gi·ªè h√†ng
          </li>
        </ol>
      </nav>

      <div className="row">
        <div className="col-12">
          <h1 className="h2 text-primary mb-4">
            <i className="fas fa-shopping-cart me-2"></i>
            Gi·ªè h√†ng c·ªßa b·∫°n
