                      </div>
                    </div>

                    {/* Th√†nh ti·ªÅn */}
                    <div className="col-2 col-md-2 text-end">
                      <div className="item-total">
                        <strong className="text-warning">
                          {((item.specialPrice || item.price || 0) * (item.quantity || 1)).toLocaleString()}‚Ç´
                        </strong>
                      </div>
                      <button
                        className="btn btn-link text-danger p-0 mt-1"
                        onClick={() => removeFromCart(item.productId)}
                        disabled={updating}
                        title="X√≥a s·∫£n ph·∫©m"
                      >
                        <i className="fas fa-times"></i>
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Ti·∫øp t·ª•c mua s·∫Øm */}
          <div className="text-center">
            <Link to="/products" className="btn btn-outline-primary">
              <i className="fas fa-arrow-left me-2"></i>
              Ti·∫øp t·ª•c mua s·∫Øm
            </Link>
          </div>
        </div>

        {/* T·ªïng thanh to√°n */}
        <div className="col-lg-4">
          <div className="card shadow-sm sticky-top" style={{ top: '100px' }}>
            <div className="card-header bg-white">
              <h5 className="mb-0">T·ªïng thanh to√°n</h5>
            </div>
            <div className="card-body">
              {/* Th√¥ng tin gi·ªè h√†ng */}
              <div className="cart-summary mb-3">
                <div className="d-flex justify-content-between mb-2">
                  <span>S·ªë s·∫£n ph·∫©m:</span>
                  <span>{totalItems}</span>
                </div>
                <div className="d-flex justify-content-between mb-2">
                  <span>T·∫°m t√≠nh:</span>
                  <span>{totalAmount.toLocaleString()}‚Ç´</span>
                </div>
                <div className="d-flex justify-content-between mb-2">
                  <span>Ph√≠ v·∫≠n chuy·ªÉn:</span>
                  <span className="text-success">Mi·ªÖn ph√≠</span>
                </div>

                <hr />

                <div className="d-flex justify-content-between mb-3">
                  <strong>T·ªïng c·ªông:</strong>
                  <strong className="text-danger h5">
                    {totalAmount.toLocaleString()}‚Ç´
                  </strong>
                </div>
              </div>

              {/* N√∫t thanh to√°n */}
              <Link 
                to="/checkout" 
                className="btn btn-warning w-100 py-3 fw-bold mb-3"
              >
                <i className="fas fa-credit-card me-2"></i>
                THANH TO√ÅN NGAY
              </Link>

              <div className="text-center">
                <small className="text-muted">
                  <i className="fas fa-lock me-1"></i>
                  Thanh to√°n an to√†n
                </small>
              </div>
            </div>
          </div>

          {/* ∆Øu ƒë√£i ƒë·∫∑c bi·ªát */}
          <div className="card shadow-sm mt-3">
            <div className="card-body">
              <h6 className="text-success">
                <i className="fas fa-gift me-2"></i>
                ∆Øu ƒë√£i ƒë·∫∑c bi·ªát
              </h6>
              <ul className="list-unstyled small">
                <li><i className="fas fa-shipping-fast text-primary me-2"></i>Mi·ªÖn ph√≠ v·∫≠n chuy·ªÉn</li>
                <li><i className="fas fa-shield-alt text-success me-2"></i>B·∫£o h√†nh ch√≠nh h√£ng</li>
                <li><i className="fas fa-undo text-info me-2"></i>ƒê·ªïi tr·∫£ trong 30 ng√†y</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cart;
