import React, { useState, useEffect, useCallback } from "react";
import api from "../../services/api";
import { Eye, Package, CreditCard, MapPin, Calendar, User, Clock, CheckCircle, Truck, XCircle } from "lucide-react";
import Swal from "sweetalert2";

const OrderListAD = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [pageConfig, setPageConfig] = useState({ pageNumber: 0, totalPages: 0, totalElements: 0 });

  const fetchOrders = useCallback(async (page = 0) => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");
      const res = await api.get(`/admin/orders?pageNumber=${page}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOrders(res.data.content || []);
      setPageConfig({
        pageNumber: res.data.pageNumber,
        totalPages: res.data.totalPages,
        totalElements: res.data.totalElements
      });
    } catch (err) {
      console.error("Lỗi tải đơn hàng:", err);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => { fetchOrders(0); }, [fetchOrders]);

const handleViewOrder = async (orderId) => {
  try {
    const token = localStorage.getItem("token");
    // Gọi endpoint debug đã có orderItems hoặc endpoint admin mới
    const res = await api.get(`/admin/debug/orders/${orderId}`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    setSelectedOrder(res.data);
    setShowModal(true);
  } catch (err) {
    alert("Không thể lấy chi tiết đơn hàng!");
  }
};

  const updateStatus = async (orderId, newStatus) => {
    try {
      const token = localStorage.getItem("token");
      await api.put(`/admin/orders/${orderId}/status?status=${newStatus}`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      Swal.fire("Thành công", `Đơn hàng chuyển sang: ${newStatus}`, "success");
      fetchOrders(pageConfig.pageNumber);
      setShowModal(false);
    } catch (err) {
      Swal.fire("Lỗi", "Cập nhật trạng thái thất bại", "error");
    }
  };

  const getStatusBadge = (status) => {
    const statuses = {
      'PENDING': 'bg-warning text-dark',
      'SHIPPED': 'bg-info text-white',
      'DELIVERED': 'bg-success text-white',
      'CANCELLED': 'bg-danger text-white'
    };
    return <span className={`badge rounded-pill ${statuses[status] || 'bg-secondary'}`}>{status}</span>;
  };

  return (
    <div className="card border-0 shadow-sm text-start">
      <div className="card-header bg-white py-3">
        <h5 className="mb-0 fw-bold text-uppercase text-primary"><Package className="me-2"/>Quản lý đơn hàng</h5>
      </div>

      <div className="table-responsive">
        <table className="table table-hover align-middle mb-0">
          <thead className="bg-light small">
            <tr>
              <th className="ps-4">ID</th>
              <th>Khách hàng</th>
              <th>Ngày đặt</th>
              <th>Tổng tiền</th>
              <th>Trạng thái</th>
              <th className="text-center">Thao tác</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan="6" className="text-center py-5">Đang tải...</td></tr>
            ) : orders.map(o => (
              <tr key={o.orderId}>
                <td className="ps-4 fw-bold">#{o.orderId}</td>
                <td className="small">{o.email}</td>
                <td className="small">{o.orderDate}</td>
                <td className="fw-bold text-danger">{o.totalAmount?.toLocaleString()}đ</td>
                <td>{getStatusBadge(o.orderStatus)}</td>
                <td className="text-center">
                  <button className="btn btn-sm btn-outline-primary" onClick={() => handleViewOrder(o.orderId)}>
                    <Eye size={16}/>
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

{/* MODAL CHI TIẾT */}
{showModal && selectedOrder && (
  <div className="modal d-block" style={{ background: 'rgba(0,0,0,0.5)', zIndex: 1050 }}>
    <div className="modal-dialog modal-lg modal-dialog-centered">
      <div className="modal-content border-0 shadow-lg">
        <div className="modal-header bg-primary text-white">
          <h6 className="modal-title fw-bold">
            <Package size={18} className="me-2"/>
            CHI TIẾT ĐƠN HÀNG #{selectedOrder.orderId}
          </h6>
          <button type="button" className="btn-close btn-close-white" onClick={() => setShowModal(false)}></button>
        </div>
        <div className="modal-body p-4">
          {/* Thông tin chung */}
          <div className="row g-3 mb-4">
            <DetailBox icon={<User size={14}/>} label="Khách hàng" value={selectedOrder.email} />
            <DetailBox icon={<MapPin size={14}/>} label="Địa chỉ" value={selectedOrder.shippingAddress || "Chưa có địa chỉ"} />
            {/* SỬA Ở ĐÂY: Lấy trực tiếp paymentMethod */}
            <DetailBox icon={<CreditCard size={14}/>} label="Thanh toán" value={selectedOrder.paymentMethod || "COD"} />
          </div>

          <h6 className="fw-bold mb-3"><Clock size={16} className="me-2"/>Danh sách sản phẩm</h6>
          <div className="table-responsive border rounded mb-4">
            <table className="table table-hover align-middle mb-0">
              <thead className="table-light small">
                <tr>
                  <th className="ps-3">Sản phẩm</th>
                  <th className="text-center">Số lượng</th>
                  <th className="text-end pe-3">Đơn giá</th>
                </tr>
              </thead>
              <tbody>
                {selectedOrder.orderItems && selectedOrder.orderItems.length > 0 ? (
                  selectedOrder.orderItems.map((item, i) => (
                    <tr key={i}>
                      <td className="ps-3">
                        <div className="d-flex align-items-center gap-3">
                          <img 
                            src={`http://localhost:8080/api/public/products/image/${item.image}`} 
                            alt="" 
                            style={{ width: '45px', height: '45px', objectFit: 'cover' }} 
                            className="rounded border shadow-sm"
                            onError={(e) => e.target.src = 'https://placehold.co/45?text=No+Img'} 
                          />
                          <div>
                            <div className="fw-bold small">{item.productName}</div>
                            <div className="text-muted" style={{fontSize: '11px'}}>ID: {item.productId}</div>
                          </div>
                        </div>
                      </td>
                      <td className="text-center fw-semibold">x{item.quantity}</td>
                      <td className="text-end pe-3 fw-bold text-danger">
                        {item.orderedProductPrice?.toLocaleString()}đ
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr><td colSpan="3" className="text-center py-3 text-muted">Không có dữ liệu sản phẩm</td></tr>
                )}
              </tbody>
              <tfoot className="table-light">
                <tr>
                  <td colSpan="2" className="text-end fw-bold">Tổng cộng:</td>
                  <td className="text-end pe-3 fw-bold text-primary fs-5">
                    {selectedOrder.totalAmount?.toLocaleString()}đ
                  </td>
                </tr>
              </tfoot>
            </table>
          </div>

          {/* ACTION BUTTONS */}
          <div className="d-flex gap-2 justify-content-center pt-3 border-top">
             <button className="btn btn-info text-white px-4" onClick={() => updateStatus(selectedOrder.orderId, 'SHIPPED')}>
               <Truck size={16} className="me-2"/> Giao hàng
             </button>
             <button className="btn btn-success text-white px-4" onClick={() => updateStatus(selectedOrder.orderId, 'DELIVERED')}>
               <CheckCircle size={16} className="me-2"/> Hoàn tất
             </button>
             <button className="btn btn-danger text-white px-4" onClick={() => updateStatus(selectedOrder.orderId, 'CANCELLED')}>
               <XCircle size={16} className="me-2"/> Hủy đơn
             </button>
          </div>
        </div>
      </div>
    </div>
  </div>
)}
    </div>
  );
};

const DetailBox = ({ icon, label, value }) => (
  <div className="col-md-4">
    <div className="p-2 bg-light rounded shadow-sm h-100">
      <div className="d-flex align-items-center gap-1 text-muted fw-bold" style={{fontSize: '10px'}}>
        {icon} {label.toUpperCase()}
      </div>
      <div className="small fw-semibold text-truncate">{value}</div>
    </div>
  </div>
);

export default OrderListAD;
