import React, { useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import productService from "../../services/productService";
import cartService from "../../services/cartService";
import Swal from "sweetalert2";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductDetail = () => {
  const { productId } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [addingToCart, setAddingToCart] = useState(false);

  // L·∫•y User v√† Token m·ªõi nh·∫•t t·ª´ Storage
  const getStoredData = () => ({
    user: JSON.parse(localStorage.getItem('user')),
    token: localStorage.getItem('token')
  });

  useEffect(() => {
    const fetchProductDetail = async () => {
      if (!productId || productId === 'undefined' || productId === 'null') {
        setError("Kh√¥ng t√¨m th·∫•y ID s·∫£n ph·∫©m");
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        const productData = await productService.getProductById(productId);
        setProduct(productData);
      } catch (err) {
        console.error("‚ùå L·ªói t·∫£i s·∫£n ph·∫©m:", err);
        setError("Kh√¥ng th·ªÉ t·∫£i th√¥ng tin s·∫£n ph·∫©m.");
      } finally {
        setLoading(false);
      }
    };

    fetchProductDetail();
  }, [productId]);

  const handleAddToCart = async () => {
    const { user, token } = getStoredData();

    // 1. Ki·ªÉm tra ƒëƒÉng nh·∫≠p v√† Token
    if (!user || !user.email || !token) {
      Swal.fire({
        icon: 'warning',
        title: 'Y√™u c·∫ßu ƒëƒÉng nh·∫≠p',
        text: 'Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ th·ª±c hi·ªán ch·ª©c nƒÉng n√†y!',
        confirmButtonColor: '#ff6a00'
      });
      navigate('/login');
      return;
    }

    try {
      setAddingToCart(true);

      // 2. L·∫•y gi·ªè h√†ng active
      // L∆∞u √Ω: N·∫øu b∆∞·ªõc n√†y b·ªã 401, Interceptor trong api.js s·∫Ω x·ª≠ l√Ω
      console.log("üì° ƒêang l·∫•y gi·ªè h√†ng cho:", user.email);
      const cart = await cartService.getActiveCart(user.email);

      if (!cart || !cart.cartId) {
        throw new Error("T√†i kho·∫£n ch∆∞a ƒë∆∞·ª£c kh·ªüi t·∫°o gi·ªè h√†ng.");
      }

      // 3. Chu·∫©n h√≥a ID s·∫£n ph·∫©m
      const cleanProductId = parseInt(productId);

      // 4. G·ªçi API th√™m v√†o gi·ªè
      console.log(`üöÄ G·ª≠i y√™u c·∫ßu th√™m: CartID ${cart.cartId}, ProductID ${cleanProductId}`);
      await cartService.addToCart(cart.cartId, cleanProductId, 1);

      // 5. Th√¥ng b√°o th√†nh c√¥ng
      Swal.fire({
        icon: 'success',
        title: 'ƒê√£ th√™m v√†o gi·ªè h√†ng!',
        showConfirmButton: false,
        timer: 1500
      });

      // B·∫Øn s·ª± ki·ªán c·∫≠p nh·∫≠t Header
      window.dispatchEvent(new Event("cartUpdated"));

    } catch (error) {
      console.error('‚ùå L·ªói chi ti·∫øt:', error);

      // X·ª≠ l√Ω ri√™ng cho l·ªói 401 Unauthorized
      if (error.response?.status === 401) {
        Swal.fire({
          icon: 'error',
          title: 'Phi√™n l√†m vi·ªác h·∫øt h·∫°n',
          text: 'Vui l√≤ng ƒëƒÉng nh·∫≠p l·∫°i ƒë·ªÉ ti·∫øp t·ª•c mua h√†ng.',
          confirmButtonColor: '#ff6a00'
        }).then(() => {
          localStorage.removeItem('token'); // X√≥a token l·ªói
          navigate('/login');
        });
      } else {
        const msg = error.response?.data?.message || error.message;
        Swal.fire('Th·∫•t b·∫°i', `Kh√¥ng th·ªÉ th√™m v√†o gi·ªè: ${msg}`, 'error');
      }
    } finally {
      setAddingToCart(false);
    }
  };

  // C√°c ph·∫ßn render giao di·ªán (Loading, Error, UI)
  if (loading) {
    return (
      <div className="container mt-5 text-center">
        <div className="spinner-border text-primary" role="status"></div>
        <p className="mt-2">ƒêang t·∫£i s·∫£n ph·∫©m...</p>
      </div>
    );
  }

  if (error || !product) {
    return (
      <div className="container mt-5">
        <div className="alert alert-danger text-center shadow-sm">
          <h4>L·ªói hi·ªÉn th·ªã</h4>
          <p>{error || "S·∫£n ph·∫©m kh√¥ng t·ªìn t·∫°i."}</p>
          <button className="btn btn-primary mt-2" onClick={() => navigate('/')}>Quay l·∫°i</button>
        </div>
      </div>
    );
  }

  const productName = product.productName || product.product_name;
  const productPrice = product.price;
  const productSpecialPrice = product.specialPrice || product.special_price;
  const productQuantity = product.quantity;

  return (
    <div className="container mt-4 mb-5">
      <nav aria-label="breadcrumb" className="mb-4">
        <ol className="breadcrumb">
          <li className="breadcrumb-item"><Link to="/">Trang ch·ªß</Link></li>
          <li className="breadcrumb-item"><Link to="/product">S·∫£n ph·∫©m</Link></li>
          <li className="breadcrumb-item active text-truncate" style={{maxWidth: '200px'}}>{productName}</li>
        </ol>
      </nav>

      <div className="row g-4">
        {/* H√¨nh ·∫£nh s·∫£n ph·∫©m */}
        <div className="col-md-6 text-center">
          <div className="card border-0 shadow-sm p-2" style={{ borderRadius: "15px" }}>
            <img
              src={`${IMAGE_BASE_URL}/${product.image}`}
              alt={productName}
              onError={(e) => (e.target.src = "https://cdn-icons-png.flaticon.com/512/679/679922.png")}
              className="img-fluid rounded"
              style={{ maxHeight: "450px", objectFit: "contain" }}
            />
          </div>
        </div>

        {/* Th√¥ng tin chi ti·∫øt */}
        <div className="col-md-6">
          <div className="ps-md-4">
