        console.log('‚ùå Response data l√† null/undefined');
        setError('Kh√¥ng c√≥ d·ªØ li·ªáu tr·∫£ v·ªÅ');
        return;
      }

      // Debug chi ti·∫øt c·∫•u tr√∫c response
      console.log('üîç Ki·ªÉm tra c·∫•u tr√∫c response:');
      console.log('- C√≥ content array?', Array.isArray(responseData.content));
      console.log('- Content length:', responseData.content?.length);
      console.log('- C√≥ totalElements?', responseData.totalElements);
      console.log('- To√†n b·ªô response keys:', Object.keys(responseData));

      const bannersArray = Array.isArray(responseData.content) ? responseData.content : [];
      console.log('üìã Danh s√°ch banners t√¨m th·∫•y:', bannersArray);

      // üî¥ DEBUG CHI TI·∫æT T·ª™NG BANNER
