import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../../services/userService";

const EditProfile = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    email: "",
    mobile_number: "",
  });
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [userId, setUserId] = useState(null);

  // ‚úÖ KI·ªÇM TRA AUTH - CHO PH√âP USER ID = 0
  useEffect(() => {
    const checkAuth = () => {
      try {
        const token = localStorage.getItem("token");
        const userData = localStorage.getItem("user");

        if (!token || !userData) {
          alert("‚ùå Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ ti·∫øp t·ª•c!");
          navigate("/login");
          return false;
        }

        const user = JSON.parse(userData);
        const userId = user.userId;

        console.log("üë§ User ID from localStorage:", userId, `(type: ${typeof userId})`);

        // ‚úÖ CHO PH√âP C·∫¢ USER ID = 0 (v√¨ database b·∫Øt ƒë·∫ßu t·ª´ 1)
        if (userId === undefined || userId === null) {
          alert("‚ö†Ô∏è Kh√¥ng t√¨m th·∫•y th√¥ng tin ng∆∞·ªùi d√πng!");
          navigate("/login");
          return false;
        }

        setUserId(userId);
        return { token, user, userId };
      } catch (error) {
        console.error("‚ùå Auth check error:", error);
        return false;
      }
    };

    const loadUserData = async () => {
      const auth = checkAuth();
      if (!auth) return;

      const { user: userData, userId } = auth;
      setLoading(true);

      try {
        console.log("üì° Loading user data from API for ID:", userId);

        // ‚úÖ CH·ªà G·ªåI API N·∫æU USER ID H·ª¢P L·ªÜ (number v√† >= 0)
        if (typeof userId === 'number' && userId >= 0) {
          const userProfile = await userService.getUserById(userId);
          console.log("‚úÖ User data loaded from API:", userProfile);

          setForm({
            firstName: userProfile.firstName || userProfile.firstName || userData.firstName || userData.firstName || "",
            lastName: userProfile.lastName || userProfile.lastName || userData.lastName || userData.lastName || "",
            email: userProfile.email || userData.email || "",
            mobile_number: userProfile.mobile_number || userProfile.mobileNumber || userData.mobile_number || userData.mobileNumber || ""
          });
        } else {
          // ‚úÖ TEMP ID: D√ôNG DATA T·ª™ LOCALSTORAGE
          console.log("üìã Using localStorage data");
          setForm({
            firstName: userData.firstName || userData.firstName || "",
            lastName: userData.lastName || userData.lastName || "",
            email: userData.email || "",
            mobile_number: userData.mobile_number || userData.mobileNumber || ""
          });
        }

      } catch (error) {
        console.error("‚ùå Failed to load user data:", error);

        // Fallback to localStorage data
        setForm({
          firstName: userData.firstName || userData.firstName || "",
          lastName: userData.lastName || userData.lastName || "",
          email: userData.email || "",
          mobile_number: userData.mobile_number || userData.mobileNumber || ""
        });
      } finally {
        setLoading(false);
      }
    };

    loadUserData();
  }, [navigate]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validation
    if (!form.firstName.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p h·ªç!");
      return;
    }

    if (!form.lastName.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p t√™n!");
      return;
    }

    if (!form.email.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p email!");
      return;
    }

    if (!/\S+@\S+\.\S+/.test(form.email)) {
      alert("‚ùå Email kh√¥ng h·ª£p l·ªá!");
      return;
    }

    setSaving(true);

    try {
      // ‚úÖ L·∫•y user data t·ª´ localStorage
      const userData = JSON.parse(localStorage.getItem("user"));
      const currentUserId = userData.userId;

      console.log("üîç DEBUG UPDATE:");
      console.log("User ID:", currentUserId, "Type:", typeof currentUserId);
      console.log("Form Data:", form);
      console.log("Token exists:", !!localStorage.getItem("token"));

      // ‚úÖ CHO PH√âP USER ID = 0
      if (currentUserId === undefined || currentUserId === null) {
        alert("‚ùå User ID kh√¥ng h·ª£p l·ªá!");
        return;
      }

      console.log("üíæ Saving to database with user ID:", currentUserId);

      // ‚úÖ Chu·∫©n b·ªã d·ªØ li·ªáu ƒë·ªÉ g·ª≠i l√™n API
      const updateData = {
        firstName: form.firstName,
        lastName: form.lastName,
        email: form.email,
        mobile_number: form.mobile_number
      };

      console.log("üì§ Sending update data:", updateData);

      // ‚úÖ G·ªçi API update - CHO PH√âP USER ID = 0
      if (typeof currentUserId === 'number' && currentUserId >= 0) {
        const response = await userService.updateUser(currentUserId, updateData);
        console.log("‚úÖ DATABASE UPDATE SUCCESS:", response);

        // ‚úÖ C·∫≠p nh·∫≠t localStorage v·ªõi d·ªØ li·ªáu m·ªõi
        const updatedUser = { 
          ...userData,
          firstName: form.firstName,
          lastName: form.lastName,
          email: form.email,
          mobile_number: form.mobile_number,
          firstName: form.firstName,
          lastName: form.lastName,
          mobileNumber: form.mobile_number
        };
        localStorage.setItem("user", JSON.stringify(updatedUser));

        alert("‚úÖ C·∫≠p nh·∫≠t h·ªì s∆° th√†nh c√¥ng!");

        // Quay v·ªÅ trang tr∆∞·ªõc
        navigate(-1);
      } else {
        alert("‚ö†Ô∏è Ch·ªâ c√≥ th·ªÉ c·∫≠p nh·∫≠t v·ªõi t√†i kho·∫£n ƒë√£ ƒëƒÉng k√Ω!");
      }

    } catch (error) {
      console.error("‚ùå UPDATE FAILED:", error);

      let errorMessage = "Kh√¥ng th·ªÉ c·∫≠p nh·∫≠t th√¥ng tin";

      if (error.response) {
        const status = error.response.status;
        const data = error.response.data;

        console.log("üö® API Error:", { status, data });

        switch (status) {
          case 500:
            errorMessage = "L·ªói server. Vui l√≤ng th·ª≠ l·∫°i sau.";
            break;
          case 404:
            errorMessage = "Kh√¥ng t√¨m th·∫•y ng∆∞·ªùi d√πng";
            break;
