import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import userService from "../../services/userService";

const EditProfile = () => {
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    email: "",
    mobile_number: ""
  });
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState({ type: "", text: "" });
  const navigate = useNavigate();

  useEffect(() => {
    console.log("üîÑ EditProfile Component Mounted");

    const storedUser = localStorage.getItem("user");
    const storedToken = localStorage.getItem("token");

    console.log("üîç DEBUG STORAGE:", {
      user: storedUser ? JSON.parse(storedUser) : "NO USER",
      token: storedToken ? `EXISTS (${storedToken.length} chars)` : "NO TOKEN"
    });

    if (!storedUser) {
      console.log("‚ùå NO USER - REDIRECTING TO LOGIN");
      setMessage({ 
        type: "error", 
        text: "Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ ch·ªânh s·ª≠a h·ªì s∆°" 
      });
      setTimeout(() => navigate("/login"), 2000);
      return;
    }

    const initializeUser = async () => {
      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);

        const userId = parsedUser.userId || parsedUser.user_id;
        console.log("üë§ User ID from storage:", userId);

        if (!userId) {
          setMessage({ type: "error", text: "Kh√¥ng t√¨m th·∫•y ID ng∆∞·ªùi d√πng" });
          return;
        }

        setLoading(true);

        // TH·ª¨ load t·ª´ API n·∫øu c√≥ token
        const storedToken = localStorage.getItem("token");
        if (storedToken) {
          try {
            console.log("üì° Loading user data from API...");
            const userData = await userService.getUserProfile(userId);
            console.log("‚úÖ API data loaded:", userData);
            setFormData({
              first_name: userData.first_name || "",
              last_name: userData.last_name || "",
              email: userData.email || "",
              mobile_number: userData.mobile_number || ""
            });
          } catch (apiError) {
            console.error("‚ùå API load failed, using localStorage:", apiError);
            // Fallback to localStorage
