// src/pages/order/OrderList.js
import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import orderService from '../../services/orderService';

const OrderList = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [cancelling, setCancelling] = useState(null);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showDetail, setShowDetail] = useState(false);
  const navigate = useNavigate();

  // Láº¥y thÃ´ng tin user tá»« localStorage
  const getCurrentUser = () => {
    try {
      return JSON.parse(localStorage.getItem('user'));
    } catch (error) {
      return null;
    }
  };

  const currentUser = getCurrentUser();

  useEffect(() => {
    if (!currentUser) {
      alert('âŒ Vui lÃ²ng Ä‘Äƒng nháº­p Ä‘á»ƒ xem Ä‘Æ¡n hÃ ng');
      navigate('/login');
      return;
    }
    loadOrders();
  }, []);

  const loadOrders = async () => {
    try {
      setLoading(true);
      console.log("ðŸ”„ Äang táº£i danh sÃ¡ch Ä‘Æ¡n hÃ ng cho user:", currentUser.email);

      const ordersData = await orderService.getUserOrders(currentUser.email);
      console.log("ðŸ“¦ Danh sÃ¡ch Ä‘Æ¡n hÃ ng:", ordersData);

      // Sáº¯p xáº¿p Ä‘Æ¡n hÃ ng má»›i nháº¥t lÃªn Ä‘áº§u
      const sortedOrders = Array.isArray(ordersData) 
        ? ordersData.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
        : [];

      setOrders(sortedOrders);

    } catch (error) {
      console.error('âŒ Lá»—i khi táº£i danh sÃ¡ch Ä‘Æ¡n hÃ ng:', error);
      alert('âŒ Lá»—i khi táº£i Ä‘Æ¡n hÃ ng: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  const handleCancelOrder = async (orderId, reason = 'NgÆ°á»i dÃ¹ng yÃªu cáº§u há»§y') => {
    if (!window.confirm('Báº¡n cÃ³ cháº¯c muá»‘n há»§y Ä‘Æ¡n hÃ ng nÃ y?')) return;

    try {
      setCancelling(orderId);

      await orderService.cancelOrder(currentUser.email, orderId, reason);
      alert('âœ… ÄÃ£ há»§y Ä‘Æ¡n hÃ ng thÃ nh cÃ´ng');

      // Load láº¡i danh sÃ¡ch Ä‘Æ¡n hÃ ng
      await loadOrders();

    } catch (error) {
      console.error('âŒ Lá»—i khi há»§y Ä‘Æ¡n hÃ ng:', error);
      alert('âŒ Lá»—i khi há»§y Ä‘Æ¡n hÃ ng: ' + (error.response?.data?.message || error.message));
    } finally {
      setCancelling(null);
    }
  };

  const handleViewDetail = async (order) => {
    try {
