// src/pages/home/Banner.js
import React, { useState, useEffect } from 'react';
import { useGetList } from 'react-admin';

const Banner = () => {
    const [currentSlide, setCurrentSlide] = useState(0);
    const { data: banners, isLoading } = useGetList('banners', {
        filter: { isActive: true },
        sort: { field: 'displayOrder', order: 'ASC' }
    });

    // Auto slide change
    useEffect(() => {
        if (!banners || banners.length <= 1) return;

        const interval = setInterval(() => {
            setCurrentSlide((prev) => (prev + 1) % banners.length);
        }, 5000);

        return () => clearInterval(interval);
    }, [banners]);

    const gotoSlide = (index) => {
        setCurrentSlide(index);
    };

    if (isLoading) return <div>Loading banners...</div>;
    if (!banners || banners.length === 0) return null;

    return (
        <div className="banner-container position-relative">
            {/* Banner Slides */}
            <div className="banner-slides">
                {banners.map((banner, index) => (
                    <div
                        key={banner.bannerId}
                        className={`banner-slide ${index === currentSlide ? 'active' : ''}`}
                        style={{
                            display: index === currentSlide ? 'block' : 'none',
                            backgroundColor: banner.backgroundColor || '#f8f9fa'
                        }}
                    >
                        <div className="banner-content">
                            {/* Banner Image */}
                            <img
                                src={banner.imageUrl}
                                alt={banner.title}
                                className="banner-image"
                                style={{ width: '100%', height: '400px', objectFit: 'cover' }}
                            />

                            {/* Banner Text Overlay */}
                            <div className="banner-text position-absolute top-50 start-0 translate-middle-y p-4">
                                <h2 className="banner-title">{banner.title}</h2>
                                <p className="banner-description">{banner.description}</p>
                                {banner.buttonText && (
                                    <button 
                                        className="btn btn-primary banner-button"
                                        onClick={() => banner.targetUrl && window.open(banner.targetUrl, '_blank')}
                                    >
                                        {banner.buttonText}
                                    </button>
                                )}
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            {/* Dots Indicator - CHỈ HIỆN KHI CÓ NHIỀU HƠN 1 BANNER */}
            {banners.length > 1 && (
                <div className="banner-dots position-absolute bottom-0 start-50 translate-middle-x mb-3">
                    {banners.map((_, index) => (
                        <button
                            key={index}
                            className={`banner-dot btn btn-sm rounded-circle mx-1 ${
                                index === currentSlide ? 'btn-primary' : 'btn-outline-primary'
                            }`}
                            onClick={() => gotoSlide(index)}
                            style={{
                                width: '12px',
                                height: '12px'
                            }}
                        />
                    ))}
                </div>
            )}

            {/* Navigation Arrows - CHỈ HIỆN KHI CÓ NHIỀU HƠN 1 BANNER */}
            {banners.length > 1 && (
                <>
                    <button
                        className="banner-arrow banner-prev position-absolute top-50 start-0 translate-middle-y btn btn-light"
                        onClick={() => setCurrentSlide((prev) => (prev - 1 + banners.length) % banners.length)}
                    >
                        ‹
                    </button>
                    <button
                        className="banner-arrow banner-next position-absolute top-50 end-0 translate-middle-y btn btn-light"
                        onClick={() => setCurrentSlide((prev) => (prev + 1) % banners.length)}
                    >
                        ›
                    </button>
                </>
            )}
        </div>
    );
};

export default Banner;
