import React, { useEffect, useState, useCallback, useMemo } from "react";
import { ShoppingCart, Package, DollarSign, TrendingUp, AlertTriangle, PieChart } from "lucide-react";
import {
  Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement,
  Title, Tooltip, Legend, Filler, ArcElement
} from "chart.js";
import { Line, Doughnut } from "react-chartjs-2";
import api from "../../../services/api";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, ArcElement, Title, Tooltip, Legend, Filler);

const Dashboard = () => {
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // 1. Auth Check
  const checkAuth = useCallback(() => {
    const token = localStorage.getItem("admin-token") || localStorage.getItem("token");
    if (!token) return null;
    try {
      const decoded = jwtDecode(token);
      return decoded.role === "ADMIN" ? token : null;
    } catch { return null; }
  }, []);

  // 2. Fetch Data (Ch·ªâ d√πng 2 API g·ªëc)
  const fetchData = useCallback(async () => {
    const token = checkAuth();
    if (!token) { navigate("/login"); return; }

    try {
      setLoading(true);
      const [ordersRes, productsRes] = await Promise.all([
        api.get("/admin/orders?pageSize=100", { headers: { Authorization: `Bearer ${token}` } }),
        api.get("/admin/products?pageSize=100", { headers: { Authorization: `Bearer ${token}` } })
      ]);
      setOrders(ordersRes.data.content || []);
      setProducts(productsRes.data.content || []);
    } catch (err) {
      console.error("‚ùå Dashboard Error:", err);
    } finally {
      setLoading(false);
    }
  }, [checkAuth, navigate]);

  useEffect(() => { fetchData(); }, [fetchData]);

  // ================= LOGIC T·ª∞ T√çNH TO√ÅN (KH√îNG CALL BE) =================
  
  // T√≠nh t·ªïng doanh thu & s·ªë l∆∞·ª£ng
  const stats = useMemo(() => {
    const total = orders.reduce((sum, order) => 
      order.orderStatus !== 'CANCELLED' ? sum + (order.totalAmount || 0) : sum, 0);
    return {
      totalRevenue: total,
      ordersCount: orders.length,
      productsCount: products.length
    };
  }, [orders, products]);

  // T√≠nh Top s·∫£n ph·∫©m b√°n ch·∫°y nh·∫•t
  const topSelling = useMemo(() => {
    const map = {};
    orders.forEach(order => {
      if (order.orderStatus !== 'CANCELLED') {
        order.orderItems?.forEach(item => {
          if (!map[item.productId]) {
            map[item.productId] = { name: item.productName, sold: 0, img: item.image };
          }
          map[item.productId].sold += item.quantity;
        });
      }
    });
    return Object.values(map).sort((a, b) => b.sold - a.sold).slice(0, 5);
  }, [orders]);

  // D·ªØ li·ªáu bi·ªÉu ƒë·ªì tr√≤n (T·ªâ l·ªá Status)
  const statusChartData = useMemo(() => {
    const counts = { PENDING: 0, SHIPPED: 0, DELIVERED: 0, CANCELLED: 0 };
    orders.forEach(o => counts[o.orderStatus]++);
    return {
      labels: ['Ch·ªù', 'Ship', 'Xong', 'H·ªßy'],
      datasets: [{
        data: Object.values(counts),
        backgroundColor: ['#FF9F43', '#2563EB', '#16A34A', '#DC2626'],
        borderWidth: 0,
      }]
    };
  }, [orders]);

  if (loading) return <div className="p-5 text-center fw-bold text-primary">ƒêang ph√¢n t√≠ch d·ªØ li·ªáu...</div>;

  return (
    <div className="container-fluid p-4" style={{ backgroundColor: "#f8f9fa", minHeight: "100vh" }}>
      <h3 className="fw-bold mb-4">üìä H·ªá th·ªëng Qu·∫£n tr·ªã</h3>

      {/* Th·∫ª th·ªëng k√™ */}
      <div className="row g-3 mb-4">
        <StatCard title="Doanh thu" value={`${stats.totalRevenue.toLocaleString()}ƒë`} icon={<DollarSign color="#fff"/>} color="bg-primary" />
        <StatCard title="ƒê∆°n h√†ng" value={stats.ordersCount} icon={<ShoppingCart color="#fff"/>} color="bg-success" />
        <StatCard title="S·∫£n ph·∫©m" value={stats.productsCount} icon={<Package color="#fff"/>} color="bg-warning" />
      </div>

      <div className="row g-4">
        {/* Bi·ªÉu ƒë·ªì tr√≤n */}
        <div className="col-lg-4">
          <div className="card border-0 shadow-sm p-4 h-100">
            <h6 className="fw-bold text-secondary mb-4"><PieChart size={18} className="me-2"/> T·ªà L·ªÜ NH·∫¨N ƒê∆†N</h6>
            <div style={{ height: "250px" }}>
              <Doughnut data={statusChartData} options={{ maintainAspectRatio: false }} />
            </div>
          </div>
        </div>

        {/* Top b√°n ch·∫°y */}
        <div className="col-lg-4">
          <div className="card border-0 shadow-sm p-4 h-100">
            <h6 className="fw-bold text-secondary mb-3"><TrendingUp size={18} className="me-2"/> B√ÅN CH·∫†Y NH·∫§T</h6>
            {topSelling.map((p, i) => (
              <div key={i} className="d-flex align-items-center mb-3">
                <img src={`http://localhost:8080/api/public/products/image/${p.img}`} className="rounded me-3" style={{width: "40px", height:"40px", objectFit:"cover"}} />
                <div className="flex-grow-1">
                  <div className="small fw-bold">{p.name}</div>
                  <div className="text-muted" style={{fontSize:"11px"}}>ƒê√£ b√°n: {p.sold}</div>
                </div>
                <div className="badge bg-primary-subtle text-primary">#{i+1}</div>
              </div>
            ))}
          </div>
        </div>

        {/* C·∫£nh b√°o kho */}
        <div className="col-lg-4">
          <div className="card border-0 shadow-sm p-4 h-100">
            <h6 className="fw-bold text-danger mb-3"><AlertTriangle size={18} className="me-2"/> C·∫¢NH B√ÅO KHO</h6>
            <div className="overflow-auto">
              {products.filter(p => p.quantity < 10).map(p => (
                <div key={p.productId} className="d-flex justify-content-between align-items-center mb-2 p-2 bg-light rounded">
                  <span className="small fw-bold text-truncate me-2" style={{maxWidth:"150px"}}>{p.productName}</span>
                  <span className="badge bg-danger">C√≤n {p.quantity}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const StatCard = ({ title, value, icon, color }) => (
  <div className="col-md-4">
    <div className="card border-0 shadow-sm p-4 h-100 transition-hover">
      <div className="d-flex align-items-center">
        <div className={`p-3 rounded-circle ${color} me-3 shadow-sm`}>{icon}</div>
        <div>
          <p className="text-muted small fw-bold mb-0">{title}</p>
          <h4 className="fw-bold m-0">{value}</h4>
        </div>
      </div>
    </div>
  </div>
);

export default Dashboard;
