import React, { useEffect, useState, useCallback } from "react";
import { ShoppingCart, Users, Package, DollarSign, TrendingUp, ArrowUpRight } from "lucide-react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler,
} from "chart.js";
import { Line } from "react-chartjs-2";
import api from "../../services/api";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import Swal from "sweetalert2";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler);

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalRevenue: 0,
    ordersCount: 0,
    productsCount: 0,
    customersCount: 0,
  });
  const [latestOrders, setLatestOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // 1. Ki·ªÉm tra quy·ªÅn Admin
  const checkAuth = useCallback(() => {
    const token = localStorage.getItem("admin-token") || localStorage.getItem("token");
    if (!token) return null;
    try {
      const decoded = jwtDecode(token);
      return decoded.role === "ADMIN" ? token : null;
    } catch (e) {
      return null;
    }
  }, []);

  // 2. Fetch d·ªØ li·ªáu t·ª´ API
  const fetchData = useCallback(async () => {
    const token = checkAuth();
    if (!token) {
      navigate("/login");
      return;
    }

    try {
      setLoading(true);
      // G·ªçi ƒë·ªìng th·ªùi c√°c API ƒë·ªÉ t·ªëi ∆∞u t·ªëc ƒë·ªô
      const [statsRes, ordersRes] = await Promise.all([
        api.get("/admin/stats/summary", { headers: { Authorization: `Bearer ${token}` } }),
        api.get("/admin/orders?pageSize=5&sortBy=orderDate&sortOrder=desc", { 
          headers: { Authorization: `Bearer ${token}` } 
        })
      ]);

      setStats(statsRes.data);
      setLatestOrders(ordersRes.data.content || []);
    } catch (err) {
      console.error("‚ùå [DASHBOARD] L·ªói:", err);
      if (err.response?.status === 401) navigate("/login");
    } finally {
      setLoading(false);
    }
  }, [checkAuth, navigate]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  // D·ªØ li·ªáu bi·ªÉu ƒë·ªì (Th∆∞·ªùng Backend s·∫Ω tr·∫£ v·ªÅ m·∫£ng doanh thu 6 th√°ng)
  const chartData = {
    labels: ["T1", "T2", "T3", "T4", "T5", "T6"],
    datasets: [
      {
        fill: true,
        label: "Doanh thu",
        data: stats.monthlyRevenue || [12, 19, 15, 22, 18, 25], // M·∫´u n·∫øu ch∆∞a c√≥ data th·∫≠t
        borderColor: "#4e73df",
        backgroundColor: "rgba(78, 115, 223, 0.1)",
        tension: 0.4,
      },
    ],
  };

  if (loading) return <div className="p-5 text-center">ƒêang t·∫£i d·ªØ li·ªáu h·ªá th·ªëng...</div>;

  return (
    <div className="container-fluid p-4" style={{ backgroundColor: "#f4f6f9", minHeight: "100vh" }}>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h3 className="fw-bold text-dark m-0">üìä Dashboard Qu·∫£n Tr·ªã</h3>
        <button className="btn btn-primary btn-sm shadow-sm" onClick={() => window.print()}>
          <TrendingUp size={16} className="me-2" /> Xu·∫•t b√°o c√°o
        </button>
      </div>

      {/* 4 Th·∫ª th·ªëng k√™ */}
      <div className="row g-3 mb-4">
        <div className="col-md-3">
          <StatCard title="DOANH THU" value={`${stats.totalRevenue?.toLocaleString()}ƒë`} icon={<DollarSign color="#fff" />} bgColor="bg-primary" trend="+12%" />
        </div>
        <div className="col-md-3">
          <StatCard title="ƒê∆†N H√ÄNG" value={stats.ordersCount} icon={<ShoppingCart color="#fff" />} bgColor="bg-success" trend="+5%" />
        </div>
        <div className="col-md-3">
          <StatCard title="S·∫¢N PH·∫®M" value={stats.productsCount} icon={<Package color="#fff" />} bgColor="bg-warning" trend="0%" />
        </div>
        <div className="col-md-3">
          <StatCard title="KH√ÅCH H√ÄNG" value={stats.customersCount} icon={<Users color="#fff" />} bgColor="bg-info" trend="+18%" />
        </div>
      </div>

      <div className="row g-4">
        <div className="col-lg-8">
          <div className="card border-0 shadow-sm p-4 h-100">
            <h6 className="fw-bold text-secondary mb-4">BI·ªÇU ƒê·ªí TƒÇNG TR∆Ø·ªûNG</h6>
            <div style={{ height: "300px" }}>
              <Line data={chartData} options={{ responsive: true, maintainAspectRatio: false, plugins: { legend: { display: false } } }} />
            </div>
          </div>
        </div>

        <div className="col-lg-4">
          <div className="card border-0 shadow-sm p-4 h-100">
            <h6 className="fw-bold text-secondary mb-3">ƒê∆†N H√ÄNG M·ªöI NH·∫§T</h6>
            <div className="list-group list-group-flush">
              {latestOrders.map((order) => (
                <OrderLink 
                  key={order.orderId}
                  id={order.orderId} 
                  email={order.email} 
                  price={`${order.totalAmount?.toLocaleString()}ƒë`} 
                  status={order.orderStatus} 
                />
              ))}
            </div>
            <button className="btn btn-light btn-sm mt-3 w-100 text-primary fw-bold" onClick={() => navigate("/admin/orders")}>
              Xem t·∫•t c·∫£ ƒë∆°n h√†ng
            </button>
          </div>
        </div>
      </div>

      <style>{`
        .transition-hover { transition: all 0.3s; }
        .transition-hover:hover { transform: translateY(-5px); box-shadow: 0 10px 20px rgba(0,0,0,0.1) !important; }
      `}</style>
    </div>
  );
};

const StatCard = ({ title, value, icon, bgColor, trend }) => (
  <div className="card border-0 shadow-sm overflow-hidden h-100 transition-hover">
    <div className="card-body p-4">
      <div className="d-flex justify-content-between align-items-center mb-2">
        <div className={`p-2 rounded-circle ${bgColor} shadow-sm`}>{icon}</div>
        <span className="badge bg-light text-success border small"><ArrowUpRight size={12} /> {trend}</span>
      </div>
      <p className="text-muted small fw-bold mb-1">{title}</p>
      <h4 className="fw-bold m-0">{value}</h4>
    </div>
  </div>
);

const OrderLink = ({ id, email, price, status }) => {
  const getStatusColor = (s) => {
    if (s === 'CANCELLED') return 'bg-danger';
    if (s === 'SHIPPED') return 'bg-primary';
    if (s === 'DELIVERED') return 'bg-success';
    return 'bg-warning';
  };

  return (
    <div className="list-group-item px-0 border-0 d-flex justify-content-between align-items-center">
      <div className="d-flex align-items-center">
        <div className={`p-1 rounded-circle ${getStatusColor(status)} me-3`} style={{ width: "10px", height: "10px" }}></div>
        <div>
          <div className="fw-bold small">#{id} - {email.split('@')[0]}</div>
          <div className="text-muted" style={{ fontSize: "11px" }}>{status}</div>
        </div>
      </div>
      <div className="fw-bold text-dark small">{price}</div>
    </div>
  );
};

export default Dashboard;
