import React, { useState, useEffect, useCallback } from "react";
import api from "../../../services/api";
import { Eye, X } from "lucide-react";

const OrderListAD = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedOrder, setSelectedOrder] = useState(null); // Lưu đơn hàng đang xem chi tiết
  const [showModal, setShowModal] = useState(false);
  const [pageConfig, setPageConfig] = useState({ pageNumber: 0, totalPages: 0, totalElements: 0 });

  // 1. Lấy danh sách đơn hàng (Hiện đơn hàng)
  const fetchOrders = useCallback(async (page = 0) => {
    try {
      setLoading(true);
      const token = localStorage.getItem("admin-token") || localStorage.getItem("token");
      const res = await api.get(`/admin/orders?pageNumber=${page}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOrders(res.data.content || []);
      setPageConfig({
        pageNumber: res.data.pageNumber,
        totalPages: res.data.totalPages,
        totalElements: res.data.totalElements
      });
    } catch (err) {
      console.error("❌ Lỗi lấy đơn hàng:", err);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchOrders(0);
  }, [fetchOrders]);

  // 2. Xem chi tiết đơn hàng (Dùng API debug để lấy full thông tin)
  const handleViewOrder = async (orderId) => {
    try {
      const token = localStorage.getItem("admin-token") || localStorage.getItem("token");
      const res = await api.get(`/admin/debug/orders/${orderId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSelectedOrder(res.data);
      setShowModal(true);
    } catch (err) {
      alert("Không thể lấy chi tiết đơn hàng này!");
    }
  };

  return (
    <div className="card border-0 shadow-sm text-start">
      <div className="card-header bg-white py-3">
        <h5 className="m-0 fw-bold text-dark text-uppercase">Danh sách đơn hàng</h5>
      </div>

      <div className="table-responsive">
        <table className="table table-hover align-middle mb-0">
          <thead className="table-light">
            <tr>
              <th>ID</th>
              <th>Email khách hàng</th>
              <th>Ngày đặt</th>
              <th>Tổng tiền</th>
              <th>Trạng thái</th>
              <th className="text-center">Xem</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan="6" className="text-center py-4">Đang tải...</td></tr>
            ) : orders.map((o) => (
              <tr key={o.orderId}>
                <td className="fw-bold">#{o.orderId}</td>
                <td>{o.email}</td>
                <td className="small">{o.orderDate}</td>
                <td className="fw-bold text-danger">{o.totalAmount?.toLocaleString()}đ</td>
                <td>
                    <span className={`badge ${o.orderStatus === 'DELIVERED' ? 'bg-success' : 'bg-warning'}`}>
                        {o.orderStatus}
                    </span>
                </td>
                <td className="text-center">
                  <button className="btn btn-sm btn-light border shadow-sm" onClick={() => handleViewOrder(o.orderId)}>
                    <Eye size={16} className="text-primary" />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Phân trang */}
      <div className="card-footer bg-white d-flex justify-content-between align-items-center py-3">
        <div className="small text-muted">Tổng cộng: {pageConfig.totalElements} đơn</div>
        <nav>
          <ul className="pagination pagination-sm mb-0">
            {[...Array(pageConfig.totalPages)].map((_, i) => (
              <li key={i} className={`page-item ${pageConfig.pageNumber === i ? 'active' : ''}`}>
                <button className="page-link" onClick={() => fetchOrders(i)}>{i + 1}</button>
              </li>
            ))}
          </ul>
        </nav>
      </div>

      {/* MODAL CHI TIẾT ĐƠN HÀNG */}
      {showModal && selectedOrder && (
        <div className="modal d-block shadow" style={{ backgroundColor: "rgba(0,0,0,0.5)", zIndex: 1050 }}>
          <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content border-0">
              <div className="modal-header bg-light">
                <h6 className="modal-title fw-bold">CHI TIẾT ĐƠN HÀNG #{selectedOrder.orderId}</h6>
                <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
              </div>
              <div className="modal-body">
                <div className="row mb-3 small">
                    <div className="col-6"><strong>Khách hàng:</strong> {selectedOrder.email}</div>
                    <div className="col-6"><strong>Ngày đặt:</strong> {selectedOrder.orderDate}</div>
                </div>
                
                <table className="table table-sm border">
                    <thead className="table-light">
                        <tr>
                            <th>Sản phẩm</th>
                            <th className="text-center">SL</th>
                            <th className="text-end">Đơn giá</th>
                            <th className="text-end">Thành tiền</th>
                        </tr>
                    </thead>
                    <tbody>
                        {selectedOrder.orderItems?.map((item, idx) => (
                            <tr key={idx}>
                                <td>{item.product?.productName || "Sản phẩm không tên"}</td>
                                <td className="text-center">{item.quantity}</td>
                                <td className="text-end">{item.orderedProductPrice?.toLocaleString()}đ</td>
                                <td className="text-end fw-bold">{(item.quantity * item.orderedProductPrice)?.toLocaleString()}đ</td>
                            </tr>
                        ))}
                    </tbody>
                    <tfoot>
                        <tr className="table-light">
                            <td colSpan="3" className="text-end fw-bold">TỔNG CỘNG:</td>
                            <td className="text-end fw-bold text-danger fs-5">{selectedOrder.totalAmount?.toLocaleString()}đ</td>
                        </tr>
                    </tfoot>
                </table>
              </div>
              <div className="modal-footer">
                <button className="btn btn-secondary btn-sm px-4" onClick={() => setShowModal(false)}>Đóng</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default OrderListAD;
