import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import userService from "../../services/userService";
import { ToastContainer, toast } from "react-toastify";
// import "react-toastify/dist/ReactToastify.css";

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    remember: true,
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const validate = () => {
    const { email, password } = formData;
    const errors = [];

    if (!email || !password) {
      errors.push("Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß th√¥ng tin.");
    }

    if (email && !/\S+@\S+\.\S+/.test(email)) {
      errors.push("Email kh√¥ng h·ª£p l·ªá.");
    }

    return errors.length > 0 ? errors : null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const errors = validate();
    if (errors) {
      errors.forEach(error => toast.error(error));
      return;
    }

    setLoading(true);

    try {
      console.log("üîê Attempting login with:", { email: formData.email });

      // G·ªçi API login
      const res = await authService.login({
        email: formData.email,
        password: formData.password
      });

      console.log("‚úÖ LOGIN SUCCESS:", res.data);
      const loginData = res.data;

      // L·∫•y token t·ª´ response
      const token = loginData.token || loginData['jwt-token'] || loginData.jwt;

      if (!token) {
        throw new Error("Server kh√¥ng tr·∫£ v·ªÅ token");
      }

      console.log("üîê Token received");

      // L∆∞u token v√†o localStorage
      localStorage.setItem("token", token);

      // L·∫•y th√¥ng tin user t·ª´ API /auth/me
      console.log("üîç Getting user info from /auth/me...");
      let userInfo = null;

      try {
        userInfo = await userService.getCurrentUser();
        console.log("‚úÖ User info:", userInfo);
      } catch (userError) {
        console.warn("‚ö†Ô∏è Could not get user info:", userError);
        // Fallback: s·ª≠ d·ª•ng th√¥ng tin t·ª´ login response
        userInfo = loginData.user || {
          userId: loginData.userId,
          email: formData.email,
          first_name: loginData.first_name || "",
          last_name: loginData.last_name || "",
          mobile_number: loginData.mobile_number || ""
        };
      }

      // L∆∞u th√¥ng tin user v√†o localStorage
      const userToStore = {
        userId: userInfo.userId || userInfo.user_id || userInfo.id,
        email: userInfo.email || formData.email,
        first_name: userInfo.first_name || userInfo.firstName || "",
        last_name: userInfo.last_name || userInfo.lastName || "",
        mobile_number: userInfo.mobile_number || userInfo.phone || "",
        role: userInfo.role || "user"
      };

      localStorage.setItem("user", JSON.stringify(userToStore));
      console.log("üíæ User data saved:", userToStore);

      // L∆∞u role n·∫øu c√≥
      if (userToStore.role) {
        localStorage.setItem("role", userToStore.role);

        // Set cookie for role (n·∫øu c·∫ßn)
        const expiryDate = new Date();
        expiryDate.setDate(expiryDate.getDate() + 30);
        document.cookie = `role=${userToStore.role}; path=/; expires=${expiryDate.toUTCString()};`;
      }

      // Ki·ªÉm tra final
      const savedToken = localStorage.getItem("token");
      const savedUser = JSON.parse(localStorage.getItem("user"));

      console.log("üîç FINAL AUTH CHECK:", {
        token: savedToken ? "EXISTS" : "MISSING",
        user: savedUser ? `EXISTS (ID: ${savedUser.userId})` : "MISSING",
        role: userToStore.role
      });

      toast.success("ƒêƒÉng nh·∫≠p th√†nh c√¥ng!");

      // Redirect based on role or default
      setTimeout(() => {
        if (userToStore.role === "admin" || userToStore.role === "admin-web") {
          window.location.href = "/admin";
        } else {
          window.location.href = "/";
        }
      }, 1000);

    } catch (err) {
      console.error("‚ùå LOGIN ERROR:", err);
      localStorage.clear();

      const errorMessage = err.response?.data?.message || 
                          err.response?.data?.error?.message || 
                          err.message || 
                          "ƒêƒÉng nh·∫≠p th·∫•t b·∫°i!";

      toast.error(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-4">
      <ToastContainer 
        position="top-right" 
        autoClose={3000} 
        hideProgressBar={false} 
        newestOnTop
        closeOnClick 
        rtl={false} 
        pauseOnFocusLoss 
        draggable 
        pauseOnHover 
        theme="colored"
      />

      <div className="card mx-auto" style={{ maxWidth: "400px", marginTop: "50px" }}>
        <div className="card-body">
          <h4 className="card-title mb-4 text-center text-primary">Sign in</h4>

          <form onSubmit={handleSubmit}>
            <div className="form-group mb-3">
              <label className="form-label">Email</label>
              <input
                name="email"
                className="form-control"
                placeholder="Enter your email"
                type="email"
                value={formData.email}
                onChange={handleChange}
                required
                disabled={loading}
              />
            </div>

            <div className="form-group mb-3">
              <label className="form-label">Password</label>
              <input
                name="password"
                className="form-control"
                placeholder="Enter your password"
                type="password"
                value={formData.password}
                onChange={handleChange}
                required
                disabled={loading}
              />
            </div>

            <div className="form-group mb-3">
              <div className="form-check">
                <input
                  name="remember"
                  className="form-check-input"
                  type="checkbox"
                  checked={formData.remember}
                  onChange={handleChange}
                  disabled={loading}
                />
                <label className="form-check-label">
                  Remember me
                </label>
              </div>
            </div>

            <button 
              type="submit" 
              className="btn btn-primary w-100"
              disabled={loading}
            >
              {loading ? (
                <>
                  <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                  ƒêang ƒëƒÉng nh·∫≠p...
                </>
              ) : (
                'Login'
              )}
            </button>
          </form>

          <div className="text-center mt-3">
            <a href="/forgot-password" className="text-decoration-none">
              Forgot password?
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
