import React, { useEffect, useState, useCallback } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../services/userService";
import categoryService from "../services/categoryService";
import cartService from "../services/cartService";
import Swal from "sweetalert2";

const Header = () => {
  const [user, setUser] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [categories, setCategories] = useState([]);
  const [cartCount, setCartCount] = useState(0);
  const [localSearch, setLocalSearch] = useState(""); // State tìm kiếm tại Header

  const navigate = useNavigate();

  const initUser = useCallback(async () => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const parsedUser = JSON.parse(storedUser);
      setUser(parsedUser);
      if (parsedUser.userId) {
        try {
          const data = await userService.getUserById(parsedUser.userId);
          setUserInfo(data);
        } catch (err) { console.error(err); }
      }
    } else {
      setUser(null);
      setUserInfo(null);
    }
  }, []);

  useEffect(() => {
    initUser();
    fetchCategories();
    window.addEventListener("authUpdated", initUser);
    window.addEventListener('cartUpdated', loadCartCount);
    return () => {
      window.removeEventListener("authUpdated", initUser);
      window.removeEventListener('cartUpdated', loadCartCount);
    };
  }, [initUser]);

  useEffect(() => { loadCartCount(); }, [user]);

  const loadCartCount = async () => {
    const currentUser = JSON.parse(localStorage.getItem('user') || '{}');
    if (currentUser?.email) {
      const count = await cartService.getCartCount(currentUser.email);
      setCartCount(count);
    }
  };

  const fetchCategories = async () => {
    try {
      const data = await categoryService.getAllCategories();
      setCategories(data || []);
    } catch (err) { console.error(err); }
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (localSearch.trim()) {
      navigate("/product", { 
        state: { searchKeyword: localSearch.trim() } 
      });
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    setUser(null);
    setCartCount(0);
    window.dispatchEvent(new Event("authUpdated"));
    navigate("/login");
  };

  return (
    <header className="section-header shadow-sm sticky-top bg-white">
      <div className="top-banner-ads py-1 text-center bg-dark text-white small">
        GIAO SIÊU TỐC 2H - FREESHIP 0Đ CHO ĐƠN TỪ 199K
      </div>

      <section className="header-main py-3 border-bottom" style={{backgroundColor: '#ff6a00'}}>
        <div className="container">
          <div className="row align-items-center">
            <div className="col-lg-2 col-6">
              <Link to="/" className="text-decoration-none">
                <h3 className="text-white fw-bold mb-0">ShopBeAnh</h3>
              </Link>
            </div>

            <div className="col-lg-6 d-none d-md-block">
              <form onSubmit={handleSearchSubmit}>
                <div className="input-group overflow-hidden rounded-pill bg-white px-2 py-1">
                  <input 
                    type="text" 
                    className="form-control border-0 shadow-none" 
                    placeholder="Bạn muốn tìm mỹ phẩm gì..."
                    value={localSearch}
                    onChange={(e) => setLocalSearch(e.target.value)}
                  />
                  <button className="btn btn-link text-warning" type="submit">
                    <i className="fa fa-search fa-lg"></i>
                  </button>
                </div>
              </form>
            </div>

            <div className="col-lg-4 col-6 d-flex justify-content-end align-items-center gap-3">
              <div className="dropdown">
                <div className="text-white text-center dropdown-toggle" data-bs-toggle="dropdown" style={{cursor: 'pointer'}}>
                  <i className="fa fa-user fa-lg d-block mb-1"></i>
                  <small>{userInfo?.firstName || user?.email?.split('@')[0] || "Tài khoản"}</small>
                </div>
                <ul className="dropdown-menu dropdown-menu-end shadow border-0 mt-2">
                  {!user ? (
                    <>
                      <li><Link className="dropdown-item" to="/login">Đăng nhập</Link></li>
                      <li><Link className="dropdown-item" to="/register">Đăng ký</Link></li>
                    </>
                  ) : (
                    <>
                      <li><Link className="dropdown-item" to="/profile">Cá nhân</Link></li>
                      <li><Link className="dropdown-item" to="/orders">Đơn hàng</Link></li>
                      <li><hr className="dropdown-divider"/></li>
                      <li><button className="dropdown-item text-danger" onClick={handleLogout}>Đăng xuất</button></li>
                    </>
                  )}
                </ul>
              </div>

              <Link to="/cart" className="text-white text-center position-relative text-decoration-none">
                <i className="fa fa-shopping-bag fa-lg d-block mb-1"></i>
                <span className="badge rounded-pill bg-danger position-absolute top-0 start-100 translate-middle" style={{fontSize: '10px'}}>
                  {cartCount}
                </span>
                <small>Giỏ hàng</small>
              </Link>
            </div>
          </div>
        </div>
      </section>
    </header>
  );
};

export default Header;
