import React, { useState } from 'react';
import axios from 'axios';

const GeminiChat = ({ cartId }) => {
    const [message, setMessage] = useState('');
    const [chatLog, setChatLog] = useState([]);
    const [loading, setLoading] = useState(false);

    const handleSend = async () => {
        if (!message.trim()) return;
        setLoading(true);
        const userMsg = { role: 'user', text: message };
        setChatLog([...chatLog, userMsg]);

        try {
            const res = await axios.post('http://localhost:8080/api/ai/ask', {
                message: message,
                cartId: cartId || 1 // Mặc định cart 1 nếu chưa login để test
            });

            const aiMsg = { 
                role: 'ai', 
                text: res.data.reply, 
                products: res.data.suggested_products 
            };
            setChatLog(prev => [...prev, aiMsg]);
        } catch (err) {
            console.error(err);
            setChatLog(prev => [...prev, { role: 'ai', text: 'Lỗi rồi nàng ơi, check console xem!' }]);
        }
        setMessage('');
        setLoading(false);
    };

    return (
        <div style={{ padding: '20px', maxWidth: '500px', margin: 'auto' }}>
            <div style={{ height: '400px', overflowY: 'auto', border: '1px solid #ccc', padding: '10px', marginBottom: '10px' }}>
                {chatLog.map((chat, i) => (
                    <div key={i} style={{ textAlign: chat.role === 'user' ? 'right' : 'left', margin: '10px 0' }}>
                        <div style={{ background: chat.role === 'user' ? '#dcf8c6' : '#eee', padding: '8px', borderRadius: '10px', display: 'inline-block' }}>
                            {chat.text}
                        </div>
                        {chat.products && chat.products.map(p => (
                            <div key={p.productId} style={{ border: '1px solid pink', marginTop: '5px', padding: '5px' }}>
                                <small>Gợi ý: {p.productName} - {p.specialPrice || p.price}đ</small>
                            </div>
                        ))}
                    </div>
                ))}
            </div>
            <input value={message} onChange={(e) => setMessage(e.target.value)} style={{ width: '80%' }} placeholder="Hỏi Ngọc Anh Beauty..." />
            <button onClick={handleSend} disabled={loading}>{loading ? '...' : 'Gửi'}</button>
        </div>
    );
};

export default GeminiChat;
