import React, { useState, useEffect, useCallback } from "react";
import api from "../../../services/api";
import { Eye, Package, User, Truck, CheckCircle, XCircle, Calendar, CreditCard, MapPin, Search, Filter } from "lucide-react";
import Swal from "sweetalert2";

const OrderListAD = () => {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedOrder, setSelectedOrder] = useState(null);
    const [showModal, setShowModal] = useState(false);

    const fetchOrders = useCallback(async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem("token");
            const res = await api.get(`/admin/orders`, { headers: { Authorization: `Bearer ${token}` } });
            setOrders(res.data.content || []);
        } catch (err) {
            console.error(err);
        } finally {
            setLoading(false);
        }
    }, []);

    useEffect(() => { fetchOrders(); }, [fetchOrders]);

    const handleViewOrder = async (orderId) => {
        try {
            const token = localStorage.getItem("token");
            const res = await api.get(`/admin/debug/orders/${orderId}`, { headers: { Authorization: `Bearer ${token}` } });
            setSelectedOrder(res.data);
            setShowModal(true);
        } catch (err) {
            Swal.fire("Lỗi", "Không thể lấy dữ liệu", "error");
        }
    };

    const updateStatus = async (id, status) => {
        try {
            const token = localStorage.getItem("token");
            await api.put(`/admin/orders/${id}/status?orderStatus=${status}`, {}, { headers: { Authorization: `Bearer ${token}` } });
            Swal.fire("Thành công", "Đã cập nhật trạng thái", "success");
            fetchOrders();
            setShowModal(false);
        } catch (err) {
            Swal.fire("Lỗi", "Thất bại", "error");
        }
    };

    return (
        <div style={{ backgroundColor: "#F8F9FB", minHeight: "100vh", padding: "40px", fontFamily: "'Inter', sans-serif" }}>
            {/* Header VIP */}
            <div className="d-flex justify-content-between align-items-end mb-5">
                <div>
                    <h1 style={{ fontWeight: 800, fontSize: "32px", letterSpacing: "-1px", color: "#1A1A1A" }}>Orders</h1>
                    <p style={{ color: "#666", fontSize: "14px" }}>Quản lý danh sách đơn hàng hệ thống</p>
                </div>
                <div className="d-flex gap-3">
                    <div className="position-relative">
                        <Search className="position-absolute top-50 start-0 translate-middle-y ms-3 text-muted" size={16}/>
                        <input type="text" placeholder="Tìm đơn hàng..." className="form-control border-0 shadow-sm ps-5 py-2 rounded-4" style={{ width: "300px" }} />
                    </div>
                    <button className="btn btn-white border-0 shadow-sm rounded-4 px-4 py-2 fw-bold text-dark">
                        <Filter size={16} className="me-2"/> Lọc
                    </button>
                </div>
            </div>

            {/* List Header (Fake Table Header) */}
            <div className="px-4 py-2 d-none d-lg-flex text-muted fw-bold mb-3" style={{ fontSize: "11px", letterSpacing: "1px", textTransform: "uppercase" }}>
                <div style={{ width: "15%" }}>Mã đơn</div>
                <div style={{ width: "30%" }}>Khách hàng</div>
                <div style={{ width: "20%" }}>Ngày đặt</div>
                <div style={{ width: "15%" }}>Tổng tiền</div>
                <div style={{ width: "15%" }}>Trạng thái</div>
                <div style={{ width: "5%" }}></div>
            </div>

            {/* Order Items */}
            {loading ? (
                <div className="text-center py-5 fw-bold text-muted">Đang tải dữ liệu...</div>
            ) : orders.map(o => (
                <div 
                    key={o.orderId} 
                    className="order-card mb-3 p-4 bg-white d-flex align-items-center transition-all shadow-hover rounded-4 border-0"
                    style={{ cursor: "pointer" }}
                    onClick={() => handleViewOrder(o.orderId)}
                >
                    <div style={{ width: "15%" }} className="fw-bold text-dark">#{o.orderId}</div>
                    <div style={{ width: "30%" }}>
                        <div className="fw-bold text-dark small">{o.email}</div>
                        <div className="text-muted" style={{ fontSize: "11px" }}>Customer ID: {o.userId || '---'}</div>
                    </div>
                    <div style={{ width: "20%" }} className="small text-secondary">{o.orderDate}</div>
                    <div style={{ width: "15%" }} className="fw-bold text-dark">{o.totalAmount?.toLocaleString()}đ</div>
                    <div style={{ width: "15%" }}>
                        <StatusPill status={o.orderStatus} />
                    </div>
                    <div style={{ width: "5%" }} className="text-end">
                        <Eye size={20} className="text-muted"/>
                    </div>
                </div>
            ))}

            {/* MODAL - LUXURY VERSION */}
            {showModal && selectedOrder && (
                <div className="modal d-block shadow-lg" style={{ background: 'rgba(0,0,0,0.3)', backdropFilter: 'blur(12px)', zIndex: 1050 }}>
                    <div className="modal-dialog modal-dialog-centered" style={{ maxWidth: '800px' }}>
                        <div className="modal-content border-0 shadow-none" style={{ borderRadius: '32px', backgroundColor: '#FFF' }}>
                            <div className="modal-body p-5">
                                <div className="d-flex justify-content-between align-items-center mb-5">
                                    <div className="d-flex align-items-center gap-3">
                                        <div className="p-3 bg-dark text-white rounded-4 shadow">
                                            <Package size={24}/>
                                        </div>
                                        <div>
                                            <h2 className="fw-bold m-0" style={{ letterSpacing: '-1px' }}>Chi tiết đơn hàng</h2>
                                            <span className="text-muted small">Mã định danh: #{selectedOrder.orderId}</span>
                                        </div>
                                    </div>
                                    <button onClick={() => setShowModal(false)} className="btn-close shadow-none"></button>
                                </div>

                                <div className="row g-4 mb-5">
                                    <InfoBox icon={<User size={18}/>} label="Khách hàng" value={selectedOrder.email} />
                                    <InfoBox icon={<MapPin size={18}/>} label="Giao tới" value={selectedOrder.shippingAddress} />
                                    <InfoBox icon={<CreditCard size={18}/>} label="Phương thức" value={selectedOrder.paymentMethod || "COD"} />
                                </div>

                                <div className="mb-5">
                                    <h6 className="fw-bold mb-3 d-flex align-items-center gap-2">
                                        <ShoppingBag size={18}/> Sản phẩm đã đặt
                                    </h6>
                                    <div className="bg-light rounded-4 p-2">
                                        {selectedOrder.orderItems?.map((item, i) => (
                                            <div key={i} className="d-flex align-items-center justify-content-between p-3 bg-white mb-2 rounded-3 shadow-sm">
                                                <div className="d-flex align-items-center gap-3">
                                                    <img src={`http://localhost:8080/api/public/products/image/${item.image}`} alt="" style={{width: '50px', height: '50px', objectFit: 'cover', borderRadius: '10px'}} />
                                                    <div>
                                                        <div className="fw-bold small text-dark">{item.productName}</div>
                                                        <div className="text-muted small">Số lượng: {item.quantity}</div>
                                                    </div>
                                                </div>
                                                <div className="fw-bold text-dark">{item.orderedProductPrice?.toLocaleString()}đ</div>
                                            </div>
                                        ))}
                                    </div>
                                </div>

                                <div className="d-flex justify-content-between align-items-center pt-4 border-top">
                                    <div>
                                        <div className="text-muted small">Tổng thanh toán</div>
                                        <div className="h3 fw-bold text-dark">{selectedOrder.totalAmount?.toLocaleString()}đ</div>
                                    </div>
                                    <div className="d-flex gap-2">
                                        <ActionBtn color="#2563EB" label="Vận chuyển" onClick={() => updateStatus(selectedOrder.orderId, 'SHIPPED')} />
                                        <ActionBtn color="#16A34A" label="Hoàn tất" onClick={() => updateStatus(selectedOrder.orderId, 'DELIVERED')} />
                                        <ActionBtn color="#DC2626" label="Hủy" onClick={() => updateStatus(selectedOrder.orderId, 'CANCELLED')} />
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            <style>{`
                .transition-all { transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1); }
                .shadow-hover:hover { 
                    transform: translateY(-4px); 
                    box-shadow: 0 10px 30px rgba(0,0,0,0.05) !important; 
                }
                .order-card { border: 1px solid transparent; }
                .order-card:hover { border-color: #E2E8F0; background-color: #FFF !important; }
                .rounded-4 { border-radius: 16px !important; }
                .rounded-5 { border-radius: 24px !important; }
                .btn-white:hover { background-color: #f1f1f1; }
            `}</style>
        </div>
    );
};

const StatusPill = ({ status }) => {
    const config = {
        'PENDING': { bg: "#FFF4E5", text: "#B45309" },
        'SHIPPED': { bg: "#E0F2FE", text: "#0369A1" },
        'DELIVERED': { bg: "#DCFCE7", text: "#15803D" },
        'CANCELLED': { bg: "#FEE2E2", text: "#B91C1C" }
    };
    const c = config[status] || { bg: "#F3F4F6", text: "#4B5563" };
    return (
        <span className="px-3 py-1 fw-bold rounded-pill" style={{ backgroundColor: c.bg, color: c.text, fontSize: "11px" }}>
            {status}
        </span>
    );
};

const Box = ({ icon, label, value }) => (
    <div className="col-md-4 text-start">
        <div className="d-flex align-items-center gap-2 mb-1 text-muted">
            {icon} <span className="small fw-bold text-uppercase" style={{ fontSize: "10px", letterSpacing: "1px" }}>{label}</span>
        </div>
        <div className="fw-bold small text-dark">{value}</div>
    </div>
);

const ActionBtn = ({ bg, color, icon, label, onClick }) => (
    <button className="btn border-0 flex-fill d-flex align-items-center justify-content-center gap-2 py-3 transition-all rounded-4" 
            style={{ backgroundColor: bg, color: color, fontWeight: 700, fontSize: "14px" }}
            onClick={onClick}>
        {icon} {label}
    </button>
);

export default OrderListAD;
