import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const GeminiChat = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [messages, setMessages] = useState([{ role: 'ai', text: 'Ch√†o n√†ng! Em l√† Ng·ªçc Anh Beauty AI ƒë√¢y. ‚ù§Ô∏è' }]);
    const [input, setInput] = useState('');
    const [loading, setLoading] = useState(false);
    const scrollRef = useRef(null);

    useEffect(() => {
        scrollRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [messages]);

    const handleSend = async () => {
        if (!input.trim() || loading) return;

        const userMsg = input.trim();
        setMessages(prev => [...prev, { role: 'user', text: userMsg }]);
        setInput('');
        setLoading(true);

        try {
            const res = await axios.post('http://localhost:8080/api/ai/ask', { message: userMsg });
            setMessages(prev => [...prev, { role: 'ai', text: res.data.reply }]);
        } catch (error) {
            setMessages(prev => [...prev, { role: 'ai', text: 'H·ªá th·ªëng b·∫≠n, n√†ng ch·ªù em x√≠u nh√©!' }]);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ position: 'fixed', bottom: '20px', right: '20px', zIndex: 1050 }}>
            <button onClick={() => setIsOpen(!isOpen)} className="btn btn-warning rounded-circle shadow">
                {isOpen ? '‚ùå' : 'üí¨'}
            </button>

            {isOpen && (
                <div className="card shadow-lg mt-2" style={{ width: '350px', height: '450px', display: 'flex', flexDirection: 'column' }}>
                    <div className="card-header bg-warning fw-bold">Ng·ªçc Anh Beauty AI</div>
                    <div className="card-body overflow-auto p-3" style={{ flex: 1 }}>
                        {messages.map((msg, i) => (
                            <div key={i} className={`mb-2 ${msg.role === 'user' ? 'text-end' : 'text-start'}`}>
                                <div className={`p-2 rounded shadow-sm d-inline-block ${msg.role === 'user' ? 'bg-warning' : 'bg-light'}`} style={{ whiteSpace: 'pre-wrap', maxWidth: '80%' }}>
                                    {msg.text}
                                </div>
                            </div>
                        ))}
                        <div ref={scrollRef} />
                    </div>
                    <div className="card-footer p-2">
                        <div className="input-group">
                            <input type="text" className="form-control shadow-none" value={input} onChange={(e) => setInput(e.target.value)} onKeyPress={(e) => e.key === 'Enter' && handleSend()} />
                            <button onClick={handleSend} className="btn btn-warning">G·ª≠i</button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default GeminiChat;
