import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import productService from "../../services/ProductService";

const ProductDetail = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const data = await productService.getProductById(id);
        setProduct(data);
      } catch (error) {
        console.error("❌ Không tìm thấy sản phẩm:", error);
      }
    };
    fetchProduct();
  }, [id]);

  if (!product) return <p>Đang tải sản phẩm...</p>;

  return (
    <div className="product-detail p-6">
      <h2 className="text-2xl font-bold mb-4">{product.productName}</h2>
      <div className="flex gap-6">
        <img
          src={productService.getProductImage(product.image)}
          alt={product.productName}
          className="w-96 h-96 object-cover rounded"
        />
        <div className="info">
          <p className="mb-2"><strong>Giá gốc:</strong> {product.price.toLocaleString("vi-VN", { style: "currency", currency: "VND" })}</p>
          <p className="mb-2 text-red-600 font-bold"><strong>Giá khuyến mãi:</strong> {product.specialPrice.toLocaleString("vi-VN", { style: "currency", currency: "VND" })}</p>
          <p className="mb-2"><strong>Số lượng:</strong> {product.quantity}</p>
          <p><strong>Mô tả:</strong> {product.description}</p>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
