import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import bannerService from '../../services/bannerService';

// S·ª≠a ƒë∆∞·ªùng d·∫´n API - th√™m 's' v√†o 'banner'
const IMAGE_BASE_URL = "http://localhost:8080/api/public/banners/image";

const Banner = () => {
  const [banners, setBanners] = useState([]);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchBanners();
  }, []);

  const fetchBanners = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await bannerService.getActiveBanners();
      console.log('üì¢ Banner data t·ª´ API:', data);
      setBanners(data);
    } catch (err) {
      console.error('‚ùå L·ªói khi l·∫•y banner:', err);
      setError('Kh√¥ng th·ªÉ t·∫£i banner');
    } finally {
      setLoading(false);
    }
  };

  // T·ª± ƒë·ªông chuy·ªÉn slide
  useEffect(() => {
    if (banners.length > 1) {
      const timer = setInterval(() => {
        setCurrentSlide((prev) => (prev + 1) % banners.length);
      }, 5000);

      return () => clearInterval(timer);
    }
  }, [banners.length]);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % banners.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + banners.length) % banners.length);
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  const getImageUrl = (banner) => {
    // Ki·ªÉm tra nhi·ªÅu tr∆∞·ªùng h·ª£p cho imageUrl
    const imageName = banner.imageUrl || banner.image_url || banner.imageName;

    if (!imageName) {
      return 'https://via.placeholder.com/800x400/667eea/ffffff?text=Banner+Image';
    }

    // N·∫øu ƒë√£ l√† URL ƒë·∫ßy ƒë·ªß th√¨ return lu√¥n
    if (imageName.startsWith('http')) {
      return imageName;
    }

    return `${IMAGE_BASE_URL}/${imageName}`;
  };

  if (loading) {
    return (
      <section className="banner-section mb-5">
        <div className="container">
          <div 
            className="banner-placeholder rounded-3 d-flex align-items-center justify-content-center"
            style={{
              height: '400px',
              background: 'linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%)'
            }}
          >
            <div className="text-center">
              <div className="spinner-border text-primary mb-3" role="status">
                <span className="visually-hidden">Loading...</span>
              </div>
              <p className="text-muted mb-0">ƒêang t·∫£i banner...</p>
            </div>
          </div>
        </div>
      </section>
    );
  }

  if (error) {
    return (
      <section className="banner-section mb-5">
        <div className="container">
          <div 
            className="banner-error rounded-3 d-flex align-items-center justify-content-center"
            style={{
              height: '200px',
              background: 'linear-gradient(135deg, #fff5f5 0%, #fed7d7 100%)'
            }}
          >
            <div className="text-center">
              <div className="text-danger mb-2">
                <i className="fas fa-exclamation-triangle fa-2x"></i>
              </div>
              <p className="text-danger mb-2">{error}</p>
              <button 
                onClick={fetchBanners}
                className="btn btn-sm btn-outline-danger"
              >
                <i className="fas fa-redo me-1"></i>
                Th·ª≠ l·∫°i
              </button>
            </div>
          </div>
        </div>
      </section>
    );
  }

  if (!banners || banners.length === 0) {
    return (
      <section className="banner-section mb-5">
        <div className="container">
          <div 
            className="banner-empty rounded-3 d-flex align-items-center justify-content-center"
            style={{
              height: '200px',
              background: 'linear-gradient(135deg, #f0f9ff 0%, #e0f2fe 100%)'
            }}
          >
            <div className="text-center text-muted">
              <i className="fas fa-image fa-2x mb-2"></i>
              <p className="mb-0">Ch∆∞a c√≥ banner n√†o ƒë·ªÉ hi·ªÉn th·ªã</p>
            </div>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section className="banner-section mb-5">
      <div className="container-fluid px-0">
        <div className="banner-slider position-relative rounded-3 overflow-hidden shadow-lg">

          {/* Main Banner Slides */}
          <div 
            className="banner-track d-flex" 
            style={{ 
              transform: `translateX(-${currentSlide * 100}%)`,
              transition: 'transform 0.5s ease-in-out'
            }}
          >
            {banners.map((banner, index) => (
              <div
                key={banner.bannerId || banner.id || index}
                className="banner-slide d-flex align-items-center flex-shrink-0 w-100"
                style={{
                  background: banner.backgroundColor || banner.background_color || '#FFEBCC',
                  minHeight: '400px'
                }}
              >
                <div className="container">
                  <div className="row align-items-center">

                    {/* Banner Content */}
                    <div className="col-lg-6">
                      <div className="banner-content text-dark p-4">
                        <h1 className="banner-title display-5 fw-bold mb-3">
                          {banner.title}
                        </h1>

                        {banner.description && (
                          <p className="banner-description lead mb-4">
                            {banner.description}
                          </p>
                        )}

                        {banner.buttonText && (
                          <Link
                            to={banner.targetUrl || banner.target_url || '/products'}
                            className="btn btn-primary btn-lg px-4 py-2 fw-semibold"
                          >
                            {banner.buttonText || banner.button_text} 
                            <i className="fas fa-arrow-right ms-2"></i>
                          </Link>
                        )}
                      </div>
                    </div>

                    {/* Banner Image */}
                    <div className="col-lg-6">
                      <div className="banner-image text-center p-4">
                        <img
                          src={getImageUrl(banner)}
                          alt={banner.title}
                          className="img-fluid rounded-3 shadow"
                          style={{ 
                            maxHeight: '300px',
                            objectFit: 'cover'
                          }}
                          onError={(e) => {
                            console.error('‚ùå L·ªói t·∫£i ·∫£nh:', e.target.src);
                            e.target.src = 'https://via.placeholder.com/600x400/667eea/ffffff?text=Banner+Image';
                          }}
                          onLoad={() => console.log('‚úÖ ·∫¢nh t·∫£i th√†nh c√¥ng:', banner.imageUrl || banner.image_url)}
                        />
                      </div>
                    </div>

                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Navigation Arrows */}
          {banners.length > 1 && (
            <>
              <button
                className="banner-arrow banner-arrow-prev position-absolute top-50 start-0 translate-middle-y btn btn-light rounded-circle shadow"
                onClick={prevSlide}
                style={{ 
                  left: '20px', 
                  zIndex: 10,
                  width: '50px',
                  height: '50px',
                  fontSize: '1.2rem'
                }}
              >
                <i className="fas fa-chevron-left"></i>
              </button>

              <button
                className="banner-arrow banner-arrow-next position-absolute top-50 end-0 translate-middle-y btn btn-light rounded-circle shadow"
                onClick={nextSlide}
                style={{ 
                  right: '20px', 
                  zIndex: 10,
                  width: '50px',
                  height: '50px',
                  fontSize: '1.2rem'
                }}
              >
                <i className="fas fa-chevron-right"></i>
              </button>
            </>
          )}

          {/* Dots Indicator */}
          {banners.length > 1 && (
            <div className="banner-dots position-absolute bottom-0 start-50 translate-middle-x mb-3">
              {banners.map((_, index) => (
                <button
                  key={index}
                  className={`banner-dot btn btn-sm rounded-circle mx-1 ${
                    index === currentSlide ? 'btn-primary' : 'btn-outline-primary'
                  }`}
                  onClick={() => goToSlide(index)}
                  style={{ 
                    width: '12px', 
                    height: '12px'
                  }}
                />
              ))}
            </div>
          )}

        </div>
      </div>
    </section>
  );
};

export default Banner;
