import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const GeminiChat = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [messages, setMessages] = useState([
        { role: 'ai', text: 'ChÃ o nÃ ng, Ngá»c Anh AI cÃ³ thá»ƒ giÃºp gÃ¬ cho váº» Ä‘áº¹p cá»§a nÃ ng hÃ´m nay? âœ¨' }
    ]);
    const [input, setInput] = useState('');
    const [loading, setLoading] = useState(false);
    const scrollRef = useRef(null);

    useEffect(() => {
        if (scrollRef.current) scrollRef.current.scrollIntoView({ behavior: 'smooth' });
    }, [messages, loading]);

    const handleSend = async () => {
        if (!input.trim() || loading) return;
        const userMsg = input.trim();
        setMessages(prev => [...prev, { role: 'user', text: userMsg }]);
        setInput('');
        setLoading(true);

        try {
            const res = await axios.post('http://localhost:8080/api/ai/ask', { message: userMsg });
            const { reply, suggested_products } = res.data;
            setMessages(prev => [...prev, { 
                role: 'ai', 
                text: reply || 'TÃ´i Ä‘ang láº¯ng nghe nÃ ng...',
                suggestions: suggested_products || [] 
            }]);
        } catch (error) {
            setMessages(prev => [...prev, { role: 'ai', text: 'Xin lá»—i nÃ ng, há»‡ thá»‘ng Ä‘ang báº­n má»™t chÃºt. ðŸŒ¸' }]);
        } finally { setLoading(false); }
    };

    return (
        <div className="beauty-ai-wrapper">
            {/* NÃºt Chat thanh máº£nh */}
            <button onClick={() => setIsOpen(!isOpen)} className="ai-toggle-btn shadow-sm">
                {isOpen ? <i className="bi bi-chevron-down"></i> : <i className="bi bi-stars"></i>}
                {!isOpen && <span className="btn-label">TÆ° váº¥n AI</span>}
            </button>

            {/* Cá»­a sá»• Chat Slim */}
            {isOpen && (
                <div className="ai-window shadow-2xl">
                    <div className="ai-header">
                        <div className="d-flex align-items-center">
                            <div className="status-indicator"></div>
                            <span className="header-title">Beauty Assistant</span>
                        </div>
                        <i className="bi bi-three-dots-vertical opacity-50"></i>
                    </div>

                    <div className="ai-content chat-scrollbar">
                        {messages.map((msg, idx) => (
                            <div key={idx} className={`msg-row ${msg.role === 'user' ? 'user' : 'ai'}`}>
                                <div className="msg-bubble">
                                    {msg.text}
                                </div>
                                
                                {msg.suggestions?.length > 0 && (
                                    <div className="suggestion-shelf hide-scrollbar">
                                        {msg.suggestions.map((p, i) => (
                                            <a href={`/product/${p.productId}`} key={i} className="mini-product-card">
                                                <div className="img-holder">
                                                    <img src={`http://localhost:8080/api/public/products/image/${p.image}`} alt="" />
                                                </div>
                                                <div className="p-info">
                                                    <span className="p-name">{p.productName}</span>
                                                    <span className="p-price">{(p.specialPrice || p.price).toLocaleString()}â‚«</span>
                                                </div>
                                            </a>
                                        ))}
                                    </div>
                                )}
                            </div>
                        ))}
                        {loading && <div className="typing-loader"><span></span><span></span><span></span></div>}
                        <div ref={scrollRef} />
                    </div>

                    <div className="ai-footer">
                        <input 
                            type="text" 
                            placeholder="Nháº¯n nhá»§ Ä‘iá»u gÃ¬ Ä‘Ã³..." 
                            value={input} 
                            onChange={(e) => setInput(e.target.value)}
                            onKeyDown={(e) => e.key === 'Enter' && handleSend()}
                        />
                        <button onClick={handleSend} disabled={loading}>
                            <i className="bi bi-arrow-up-short"></i>
                        </button>
                    </div>
                </div>
            )}

            <style>{`
                .beauty-ai-wrapper { position: fixed; bottom: 30px; right: 30px; z-index: 3000; font-family: 'Inter', sans-serif; }
                
                /* NÃºt Toggle tinh táº¿ */
                .ai-toggle-btn { 
                    background: #222; color: #fff; border: none; padding: 12px 24px;
                    border-radius: 50px; display: flex; align-items: center; gap: 10px;
                    transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
                }
                .ai-toggle-btn:hover { transform: translateY(-3px); background: #c5a059; }
                .btn-label { font-size: 13px; font-weight: 500; letter-spacing: 0.5px; }

                /* Cá»­a sá»• Chat Slim */
                .ai-window {
                    position: absolute; bottom: 70px; right: 0; width: 340px; height: 500px;
                    background: rgba(255, 255, 255, 0.9); backdrop-filter: blur(20px);
                    border-radius: 24px; display: flex; flexDirection: column;
                    border: 1px solid rgba(0,0,0,0.05); animation: scaleIn 0.3s ease;
                }
                @keyframes scaleIn { from { opacity: 0; transform: scale(0.9) translateY(20px); } }

                .ai-header { padding: 20px; display: flex; justify-content: space-between; align-items: center; border-bottom: 1px solid rgba(0,0,0,0.03); }
                .header-title { font-size: 14px; font-weight: 600; color: #333; letter-spacing: -0.2px; }
                .status-indicator { width: 6px; height: 6px; background: #c5a059; border-radius: 50%; margin-right: 8px; }

                .ai-content { flex: 1; padding: 20px; overflow-y: auto; }
                .msg-row { margin-bottom: 15px; display: flex; flex-direction: column; }
                .msg-bubble { 
                    max-width: 85%; padding: 12px 16px; font-size: 13px; line-height: 1.5;
                    border-radius: 18px; 
                }
                .ai .msg-bubble { background: #fff; color: #444; border-bottom-left-radius: 4px; border: 1px solid #f0f0f0; }
                .user .msg-bubble { background: #c5a059; color: #fff; align-self: flex-end; border-bottom-right-radius: 4px; }

                /* Shelf sáº£n pháº©m mini */
                .suggestion-shelf { display: flex; gap: 10px; overflow-x: auto; margin-top: 10px; padding-bottom: 5px; }
                .mini-product-card { 
                    min-width: 130px; background: #fff; border-radius: 14px; 
                    text-decoration: none; border: 1px solid #f5f5f5; overflow: hidden;
                }
                .img-holder { height: 90px; background: #fafafa; }
                .img-holder img { width: 100%; height: 100%; object-fit: cover; }
                .p-info { padding: 8px; }
                .p-name { font-size: 11px; color: #333; display: block; font-weight: 500; height: 16px; overflow: hidden; white-space: nowrap; text-overflow: ellipsis; }
                .p-price { font-size: 11px; color: #c5a059; font-weight: 700; }

                /* Footer nháº­p liá»‡u */
                .ai-footer { padding: 15px 20px; display: flex; gap: 10px; background: #fff; border-radius: 0 0 24px 24px; }
                .ai-footer input { 
                    flex: 1; border: none; background: #f8f8f8; padding: 10px 15px;
                    border-radius: 12px; font-size: 13px; outline: none;
                }
                .ai-footer button { 
                    width: 35px; height: 35px; border-radius: 10px; border: none;
                    background: #222; color: #fff; display: flex; align-items: center; justify-content: center;
                }

                .typing-loader span { width: 4px; height: 4px; background: #c5a059; display: inline-block; border-radius: 50%; margin: 0 2px; animation: blink 1s infinite; }
                @keyframes blink { 0% { opacity: 0.2; } 50% { opacity: 1; } 100% { opacity: 0.2; } }
                .hide-scrollbar::-webkit-scrollbar { display: none; }
                .chat-scrollbar::-webkit-scrollbar { width: 3px; }
                .chat-scrollbar::-webkit-scrollbar-thumb { background: #eee; }
            `}</style>
        </div>
    );
};

export default GeminiChat;
