import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BannerThree = () => {
  const [leftBanner, setLeftBanner] = useState(null);
  const [rightBanner, setRightBanner] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchBanners();
  }, []);

  const fetchBanners = async () => {
    try {
      // Lấy cả 2 banner ID 7 và 8 cùng lúc
      const [banner7Response, banner8Response] = await Promise.all([
        axios.get('http://localhost:8080/api/public/banners/7'),
        axios.get('http://localhost:8080/api/public/banners/8')
      ]);

      setLeftBanner(banner7Response.data);
      setRightBanner(banner8Response.data);

    } catch (err) {
      console.error('Lỗi lấy banners:', err);
      // Fallback: lấy 2 banner đầu tiên
      try {
        const allResponse = await axios.get('http://localhost:8080/api/public/banners?size=2');
        if (allResponse.data?.content?.length >= 2) {
          setLeftBanner(allResponse.data.content[0]);
          setRightBanner(allResponse.data.content[1]);
        } else if (allResponse.data?.content?.length === 1) {
          setLeftBanner(allResponse.data.content[0]);
        }
      } catch (fallbackErr) {
        console.error('Lỗi lấy banner fallback:', fallbackErr);
      }
    } finally {
      setLoading(false);
    }
  };

  const getImageUrl = (banner) => {
    if (!banner?.imageUrl) return 'https://via.placeholder.com/600x400/2d3748/ffffff?text=Electronics';
    return `http://localhost:8080/api/public/banners/image/${banner.imageUrl}`;
  };

  if (loading) {
    return (
      <section className="banner-three-section mb-5">
        <div className="container">
          <div className="row">
            <div className="col-md-6">
              <div className="placeholder-glow">
                <div className="placeholder rounded-3" style={{ height: '400px' }}></div>
              </div>
            </div>
            <div className="col-md-6">
              <div className="placeholder-glow">
                <div className="placeholder rounded-3" style={{ height: '400px' }}></div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }

  // Banner Component riêng để tái sử dụng
  const BannerCard = ({ banner, isLeft = true }) => {
    if (!banner) return null;

    const defaultContent = isLeft 
      ? {
          title: "Big Deal on Electronics",
          description: "This is a wider card with text below and special offers on the latest gadgets, smartphones, and tech accessories. Limited time offer!",
          buttonText: "Discover"
        }
      : {
          title: "Tech Bundle for You", 
          description: "Card with text below and amazing bundle deals on electronics. Get the best combo packages with premium quality products.",
          buttonText: "Shop Now"
        };

    return (
      <div 
        className="rounded-3 position-relative overflow-hidden h-100"
        style={{
          height: '400px',
          backgroundImage: `url(${getImageUrl(banner)})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center'
        }}
      >
        {/* Overlay */}
        <div 
          className="position-absolute top-0 start-0 w-100 h-100"
          style={{
            background: 'linear-gradient(135deg, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.4) 100%)'
          }}
        ></div>

        {/* Content */}
        <div className="position-absolute top-0 start-0 w-100 h-100 d-flex align-items-center">
          <div className="p-5 text-white">
            <h2 className="fw-bold mb-3" style={{ fontSize: '2rem' }}>
              {banner.title || defaultContent.title}
            </h2>
            <p className="mb-4 lead" style={{ maxWidth: '500px' }}>
              {banner.description || defaultContent.description}
            </p>

            {/* Divider */}
            <div className="mb-4">
              <div 
                className="bg-warning"
                style={{ 
                  width: '60px', 
                  height: '3px',
                  opacity: 0.8
                }}
              ></div>
            </div>

            <button className="btn btn-warning btn-lg px-4 fw-bold">
              {banner.buttonText || defaultContent.buttonText}
            </button>
          </div>
        </div>

        {/* Badge ID */}
        <div 
          className="position-absolute top-0 end-0 m-3"
          style={{ zIndex: 10 }}
        >
          <span className="badge bg-dark bg-opacity-50 text-white">
            ID: {banner.bannerId || banner.id}
          </span>
        </div>
      </div>
    );
  };

  return (
    <section className="banner-three-section mb-5">
      <div className="container">
        <div className="row g-4">
          {/* Banner trái - ID 7 */}
          <div className="col-md-6">
            <BannerCard banner={leftBanner} isLeft={true} />
          </div>

          {/* Banner phải - ID 8 */}
          <div className="col-md-6">
            <BannerCard banner={rightBanner} isLeft={false} />
          </div>
        </div>
      </div>
    </section>
  );
};

export default BannerThree;
