import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import userService from "../../services/userService";

const EditProfile = () => {
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    email: "",
    mobile_number: ""
  });
  const [loading, setLoading] = useState(true); // true ƒë·ªÉ show loader khi mount
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState({ type: "", text: "" });
  const navigate = useNavigate();

  useEffect(() => {
    let mounted = true;
    const storedUser = localStorage.getItem("user");

    if (!storedUser) {
      setMessage({ type: "error", text: "Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ ch·ªânh s·ª≠a h·ªì s∆°" });
      // redirect nhanh sau 1.5s ƒë·ªÉ user th·∫•y message
      setTimeout(() => navigate("/login"), 1500);
      return;
    }

    const parsed = JSON.parse(storedUser);
    setUser(parsed);

    const userId = parsed.userId || parsed.user_id;
    if (!userId) {
      setMessage({ type: "error", text: "Kh√¥ng t√¨m th·∫•y ID ng∆∞·ªùi d√πng" });
      setLoading(false);
      return;
    }

    const init = async () => {
      try {
        setLoading(true);
        // N·∫øu c√≥ token, l·∫•y profile m·ªõi nh·∫•t t·ª´ server (v√¨ header auth ƒë∆∞·ª£c x·ª≠ l√Ω trong service)
        const token = localStorage.getItem("token");
        if (token) {
          try {
            const profile = await userService.getUserProfile(userId);
            if (!mounted) return;
            setFormData({
              first_name: profile.first_name || "",
              last_name: profile.last_name || "",
              email: profile.email || "",
              mobile_number: profile.mobile_number || ""
            });
            // Optionally update localStorage to keep in sync
            const merged = { ...parsed, ...profile };
            localStorage.setItem("user", JSON.stringify(merged));
          } catch (err) {
            // N·∫øu l·ªói g·ªçi API th√¨ fallback d√πng localStorage
            console.warn("‚ö†Ô∏è Kh√¥ng l·∫•y ƒë∆∞·ª£c profile t·ª´ API, d√πng localStorage:", err);
            setFormData({
              first_name: parsed.first_name || "",
              last_name: parsed.last_name || "",
              email: parsed.email || "",
              mobile_number: parsed.mobile_number || ""
            });
          }
        } else {
          // Kh√¥ng c√≥ token -> d√πng localStorage
          setFormData({
            first_name: parsed.first_name || "",
            last_name: parsed.last_name || "",
            email: parsed.email || "",
            mobile_number: parsed.mobile_number || ""
          });
        }
      } catch (error) {
        console.error("‚ùå initializeUser error:", error);
        setMessage({ type: "error", text: "L·ªói kh·ªüi t·∫°o. Vui l√≤ng th·ª≠ l·∫°i." });
      } finally {
        if (mounted) setLoading(false);
      }
    };

    init();

    return () => {
      mounted = false;
    };
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage({ type: "", text: "" });

    const storedUser = localStorage.getItem("user");
    if (!storedUser) {
      setMessage({ type: "error", text: "Phi√™n ƒë√£ h·∫øt. Vui l√≤ng ƒëƒÉng nh·∫≠p l·∫°i." });
      setTimeout(() => navigate("/login"), 1200);
      return;
    }

    const parsed = JSON.parse(storedUser);
    const userId = parsed.userId || parsed.user_id;
    if (!userId) {
      setMessage({ type: "error", text: "Kh√¥ng t√¨m th·∫•y ID ng∆∞·ªùi d√πng" });
      return;
    }

    // Validation
    if (!formData.first_name.trim() || !formData.last_name.trim() || !formData.email.trim()) {
      setMessage({ type: "error", text: "Vui l√≤ng ƒëi·ªÅn ƒë·∫ßy ƒë·ªß th√¥ng tin b·∫Øt bu·ªôc" });
      return;
    }

    setSaving(true);

    try {
      console.log("üöÄ Submit update:", { userId, formData });
      const token = localStorage.getItem("token");
      let dbUpdated = false;
      let apiResult = null;

      if (token) {
        try {
          apiResult = await userService.updateUserProfile(userId, formData);
          dbUpdated = true;
          console.log("‚úÖ Update API result:", apiResult);
        } catch (apiErr) {
          console.error("‚ùå API update failed:", apiErr);
          // N·∫øu 401 -> force logout to re-login
          if (apiErr.response?.status === 401) {
            setMessage({ type: "error", text: "Token kh√¥ng h·ª£p l·ªá. Vui l√≤ng ƒëƒÉng nh·∫≠p l·∫°i." });
            // remove invalid token and redirect
            localStorage.removeItem("token");
            setTimeout(() => navigate("/login"), 1400);
            return;
          } else {
            setMessage({ type: "warning", text: "Kh√¥ng th·ªÉ l∆∞u l√™n server. ƒê√£ l∆∞u c·ª•c b·ªô." });
          }
        }
      } else {
