import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import userService from "../../services/userService";

const Login = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // ‚úÖ B∆Ø·ªöC 1: LOGIN ƒê·ªÇ L·∫§Y TOKEN
      const res = await authService.login(form);
      console.log("‚úÖ LOGIN RESULT:", res.data);

      const loginData = res.data;
      const token = loginData['jwt-token'];

      // ‚úÖ B∆Ø·ªöC 2: G·ªåI API ƒê·ªÇ L·∫§Y USER INFO BY EMAIL
      console.log("üîÑ Fetching user by email:", form.email);
      const userResponse = await userService.getUserByEmail(form.email);
      console.log("‚úÖ USER INFO:", userResponse);

      // ‚úÖ B∆Ø·ªöC 3: L∆ØU USER V√ÄO LOCALSTORAGE
      const userToStore = {
        userId: userResponse.userId || userResponse.user_id,
        email: userResponse.email,
        first_name: userResponse.firstName || "", // ‚úÖ L·∫§Y FIRST_NAME T·ª™ API
        last_name: userResponse.lastName || ""    // ‚úÖ L·∫§Y LAST_NAME T·ª™ API
      };

      console.log("üíæ Saving user to localStorage:", userToStore);
      localStorage.setItem("user", JSON.stringify(userToStore));
      localStorage.setItem("token", token);

      alert("‚úÖ ƒêƒÉng nh·∫≠p th√†nh c√¥ng!");
      window.location.href = "/"; // ‚úÖ RELOAD ƒê·ªÇ C·∫¨P NH·∫¨T HEADER

    } catch (err) {
      console.error("‚ùå LOGIN ERROR:", err);
      alert("‚ùå ƒêƒÉng nh·∫≠p th·∫•t b·∫°i! Vui l√≤ng ki·ªÉm tra email v√† m·∫≠t kh·∫©u.");
    }
  };

  return (
    <div className="card mx-auto" style={{ maxWidth: "380px", marginTop: "100px" }}>
      <div className="card-body">
        <h4 className="card-title mb-4">Sign in</h4>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              name="email"
              className="form-control"
              placeholder="Email"
              type="email"
              value={form.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <input
              name="password"
              className="form-control"
              placeholder="Password"
              type="password"
              value={form.password}
              onChange={handleChange}
              required
            />
          </div>

          <button type="submit" className="btn btn-primary btn-block">
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
