import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import userService from "../../services/userService";

const EditProfile = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    firstName: "",
    lastName: "", 
    email: "",
    mobileNumber: ""
  });
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    const loadUserData = async () => {
      try {
        const token = localStorage.getItem("token");
        const userData = localStorage.getItem("user");

        if (!token || !userData) {
          alert("‚ùå Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ ti·∫øp t·ª•c!");
          navigate("/login");
          return;
        }

        const user = JSON.parse(userData);
        const userId = user.userId;

        console.log("üë§ Current User ID:", userId);

        if (typeof userId === 'number' && userId > 0) {
          setLoading(true);
          const userProfile = await userService.getUserById(userId);

          console.log("‚úÖ User data loaded:", userProfile);

          // ‚úÖ D√πng camelCase theo DTO
          setForm({
            firstName: userProfile.firstName || "",
            lastName: userProfile.lastName || "",
            email: userProfile.email || "",
            mobileNumber: userProfile.mobileNumber || ""
          });
        } else {
          setForm({
            firstName: user.firstName || "",
            lastName: user.lastName || "",
            email: user.email || "",
            mobileNumber: user.mobileNumber || ""
          });
        }

      } catch (error) {
        console.error("‚ùå Failed to load user data:", error);
        alert("‚ùå Kh√¥ng th·ªÉ t·∫£i th√¥ng tin ng∆∞·ªùi d√πng!");
      } finally {
        setLoading(false);
      }
    };

    loadUserData();
  }, [navigate]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validation
    if (!form.firstName.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p h·ªç!");
      return;
    }

    if (!form.lastName.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p t√™n!");
      return;
    }

    if (!form.email.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p email!");
      return;
    }

    if (!/\S+@\S+\.\S+/.test(form.email)) {
      alert("‚ùå Email kh√¥ng h·ª£p l·ªá!");
      return;
    }

    setSaving(true);

    try {
      const userData = JSON.parse(localStorage.getItem("user"));
      const userId = userData.userId;

      console.log("üíæ Saving to database with user ID:", userId);

      // ‚úÖ D√πng camelCase theo DTO
      const updateData = {
        firstName: form.firstName,
        lastName: form.lastName, 
        email: form.email,
        mobileNumber: form.mobileNumber
      };

      console.log("üì§ Sending camelCase data:", updateData);

      // G·ªçi API update
      const response = await userService.updateUser(userId, updateData);

      console.log("‚úÖ UPDATE SUCCESS:", response);

      // C·∫≠p nh·∫≠t localStorage
      const updatedUser = { 
        ...userData,
        firstName: form.firstName,
        lastName: form.lastName,
        email: form.email,
        mobileNumber: form.mobileNumber
      };
      localStorage.setItem("user", JSON.stringify(updatedUser));

      alert("‚úÖ C·∫≠p nh·∫≠t h·ªì s∆° th√†nh c√¥ng!");
      navigate("/profile");

    } catch (error) {
      console.error("‚ùå UPDATE FAILED:", error);

      if (error.response) {
        console.log("üîç Error details:", error.response.data);
        alert("‚ùå L·ªói server: " + (error.response.data?.message || "Vui l√≤ng th·ª≠ l·∫°i sau"));
      } else {
        alert("‚ùå L·ªói: " + error.message);
      }
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <div className="p-4">
        <div className="card mx-auto" style={{ maxWidth: "400px", marginTop: "50px" }}>
          <div className="card-body text-center py-5">
            <div className="spinner-border text-primary mb-3" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p className="mb-0">ƒêang t·∫£i th√¥ng tin h·ªì s∆°...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-4">
      <div className="card mx-auto" style={{ maxWidth: "400px", marginTop: "50px" }}>
        <div className="card-body">
          <h4 className="card-title mb-4 text-center text-primary">Ch·ªânh s·ª≠a h·ªì s∆°</h4>

          <form onSubmit={handleSubmit}>
            <div className="form-group mb-3">
              <label className="form-label">First name</label>
              <input
                name="firstName"
                className="form-control"
                placeholder="Enter first name"
                type="text"
                value={form.firstName}
                onChange={handleChange}
                required
                disabled={saving}
              />
            </div>

            <div className="form-group mb-3">
              <label className="form-label">Last name</label>
              <input
                name="lastName"
                className="form-control"
                placeholder="Enter last name"
                type="text"
                value={form.lastName}
                onChange={handleChange}
                required
                disabled={saving}
              />
            </div>

            <div className="form-group mb-3">
              <label className="form-label">Email</label>
              <input
                name="email"
                className="form-control"
                placeholder="Enter email"
                type="email"
                value={form.email}
                onChange={handleChange}
                required
                disabled={saving}
              />
            </div>

            <div className="form-group mb-3">
              <label className="form-label">Mobile number</label>
              <input
                name="mobileNumber"
                className="form-control"
                placeholder="Enter mobile number"
                type="text"
                value={form.mobileNumber}
                onChange={handleChange}
                disabled={saving}
              />
            </div>

            <div className="d-flex gap-2">
              <button 
                type="button" 
                className="btn btn-secondary flex-fill"
                onClick={() => navigate(-1)}
                disabled={saving}
              >
                H·ªßy
              </button>
              <button 
                type="submit" 
                className="btn btn-primary flex-fill"
                disabled={saving}
              >
                {saving ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                    ƒêang l∆∞u...
                  </>
                ) : (
                  'C·∫≠p nh·∫≠t'
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default EditProfile;
