import React, { Component } from "react";
import { Link } from "react-router-dom";
import logo from "../assets/images/logo.png";
import vnFlag from "../assets/images/icons/flags/VN.webp";

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      users: null,
    };
  }

  componentDidMount() {
    this.loadusers();
  }

  loadusers = () => {
    const storedusers = localStorage.getItem("users");
    if (storedusers) {
      try {
        this.setState({ users: JSON.parse(storedUser) });
      } catch {
        this.setState({ user: null });
      }
    }
  };

  handleLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    this.setState({ user: null });
    window.location.reload(); // Reload ƒë·ªÉ header c·∫≠p nh·∫≠t l·∫°i
  };

  render() {
    const { user } = this.state;
    const fullName =
      user && (user.first_name || user.last_name)
        ? `${user.first_name || ""} ${user.last_name || ""}`.trim()
        : "";

    return (
      <header className="section-header">
        {/* ---------- Thanh tr√™n ---------- */}
        <nav className="navbar d-none d-md-flex p-md-0 navbar-expand-sm navbar-light border-bottom">
          <div className="container">
            <div className="collapse navbar-collapse show" id="navbarTop4">
              <ul className="navbar-nav mr-auto">
                <li className="nav-item">
                  {!user ? (
                    <span className="nav-link">
                      Hi, <Link to="/login">Log In</Link> or{" "}
                      <Link to="/register">Register</Link>
                    </span>
                  ) : (
                    <span className="nav-link">
                      üëã Xin ch√†o, <strong>{fullName}</strong>
                    </span>
                  )}
                </li>
                <li className="nav-item">
                  <a href="#" className="nav-link">
                    Help
                  </a>
                </li>
              </ul>
              <ul className="navbar-nav">
                <li className="nav-item">
                  <a href="#" className="nav-link">
                    <img src={vnFlag} height="22" alt="flag" /> Ship to
                  </a>
                </li>
              </ul>
            </div>
          </div>
        </nav>

        {/* ---------- Thanh gi·ªØa ---------- */}
        <div className="container">
          <section className="header-main border-bottom">
            <div className="row row-sm align-items-center">
              {/* Logo */}
              <div className="col-6 col-md-3">
                <Link to="/" className="brand-wrap">
                  <img src={logo} height={56} alt="Logo" />
                </Link>
              </div>

              {/* √î t√¨m ki·∫øm */}
              <div className="col-lg-6 col-md-6 col-sm-12">
                <form action="#" className="search-header">
                  <div className="input-group">
                    <input
                      type="text"
                      className="form-control"
                      placeholder="T√¨m ki·∫øm s·∫£n ph·∫©m..."
                    />
                    <div className="input-group-append">
                      <button className="btn btn-primary" type="submit">
                        üîç
                      </button>
                    </div>
                  </div>
                </form>
              </div>

              {/* User + Gi·ªè h√†ng + ƒêƒÉng xu·∫•t */}
              <div
                className="col-lg-3 col-md-3 col-sm-12"
                style={{
                  display: "flex",
                  justifyContent: "flex-end",
                  alignItems: "center",
                  gap: "15px",
                }}
              >
                {user ? (
                  <>
                    <Link
                      to="/profile"
                      className="text-dark"
                      style={{
                        display: "flex",
                        alignItems: "center",
                        gap: "6px",
                        textDecoration: "none",
                      }}
                    >
                      <i
                        className="fa fa-user-circle"
                        style={{ fontSize: "22px" }}
                      ></i>
                      <span>{fullName}</span>
                    </Link>

                    <Link
                      to="/cart"
                      className="text-dark"
                      style={{ textDecoration: "none" }}
                    >
                      <i
                        className="fa fa-shopping-cart"
                        style={{ fontSize: "22px" }}
                      ></i>
                    </Link>

                    <button
                      onClick={this.handleLogout}
                      className="btn btn-outline-danger btn-sm"
                      style={{
                        borderRadius: "20px",
                        padding: "4px 12px",
                        fontWeight: "500",
                      }}
                    >
                      ƒêƒÉng xu·∫•t
                    </button>
                  </>
                ) : (
                  <Link
                    to="/login"
                    className="btn btn-primary"
                    style={{
                      borderRadius: "20px",
                      padding: "6px 16px",
                      textDecoration: "none",
                    }}
                  >
                    Sign In
                  </Link>
                )}
              </div>
            </div>
          </section>

          {/* ---------- Navbar d∆∞·ªõi ---------- */}
          <nav className="navbar navbar-main navbar-expand pl-0">
            <ul className="navbar-nav flex-wrap">
              <li className="nav-item">
                <Link to="/" className="nav-link">
                  Home
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/product" className="nav-link">
                  Products
                </Link>
              </li>
            </ul>
          </nav>
        </div>
      </header>
    );
  }
}

export default Header;
