import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const GeminiChat = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [messages, setMessages] = useState([
        { role: 'ai', text: 'Ch√†o n√†ng! Em l√† Ng·ªçc Anh Beauty AI, n√†ng c·∫ßn t∆∞ v·∫•n m·ªπ ph·∫©m g√¨ c·ª© b·∫£o em nh√©! ‚ù§Ô∏è' }
    ]);
    const [input, setInput] = useState('');
    const [loading, setLoading] = useState(false);
    const scrollRef = useRef(null);

    useEffect(() => {
        if (scrollRef.current) {
            scrollRef.current.scrollIntoView({ behavior: 'smooth' });
        }
    }, [messages, loading]);

    const handleSend = async () => {
        if (!input.trim() || loading) return;

        const userMsg = input.trim();
        setMessages(prev => [...prev, { role: 'user', text: userMsg }]);
        setInput('');
        setLoading(true);

        const user = JSON.parse(localStorage.getItem('user') || '{}');
        const token = localStorage.getItem('token') || user?.token;
        let cartId = localStorage.getItem('cartId') || user?.cartId;
        cartId = cartId ? Number(cartId) : null;

        try {
            const res = await axios.post('http://localhost:8080/api/ai/ask', {
                message: userMsg,
                cartId: cartId 
            }, {
                headers: {
                    'Authorization': token ? `Bearer ${token}` : '',
                    'Content-Type': 'application/json'
                }
            });

            const { reply, suggested_products } = res.data;

            setMessages(prev => [...prev, { 
                role: 'ai', 
                text: reply || 'Em nghe ƒë√¢y, n√†ng n√≥i ti·∫øp ƒëi...',
                suggestions: suggested_products || []
            }]);

        } catch (error) {
            setMessages(prev => [...prev, { role: 'ai', text: 'Hic, k·∫øt n·ªëi b·∫≠n r·ªìi, n√†ng th·ª≠ l·∫°i nh√©! üòÖ' }]);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ position: 'fixed', bottom: '20px', right: '20px', zIndex: 1050 }}>
            {/* Toggle Button */}
            <button onClick={() => setIsOpen(!isOpen)} className="btn btn-warning rounded-circle shadow-lg p-0" style={{ width: '60px', height: '60px' }}>
                <i className={`bi ${isOpen ? 'bi-x-lg' : 'bi-chat-dots-fill'} fs-3`}></i>
            </button>

            {/* Chat Window */}
            {isOpen && (
                <div className="card shadow-lg border-0 rounded-4 mt-2" style={{ width: '380px', height: '550px', display: 'flex', flexDirection: 'column', overflow: 'hidden' }}>
                    <div className="card-header bg-warning text-dark py-3 px-3 border-0">
                        <div className="d-flex align-items-center">
                            <div className="bg-white rounded-circle p-1 me-2 shadow-sm d-flex justify-content-center align-items-center" style={{width: '32px', height: '32px'}}>
                                <i className="bi bi-robot text-warning fs-5"></i>
                            </div>
                            <div>
                                <h6 className="mb-0 fw-bold">Ng·ªçc Anh Beauty AI</h6>
                                <small className="opacity-75">ƒêang tr·ª±c tuy·∫øn</small>
                            </div>
                        </div>
                    </div>

                    <div className="card-body p-3 overflow-auto" style={{ flex: 1, backgroundColor: '#f8f9fa' }}>
                        {messages.map((msg, idx) => (
                            <div key={idx} className={`d-flex flex-column mb-3 ${msg.role === 'user' ? 'align-items-end' : 'align-items-start'}`}>
                                {/* Message Bubble */}
                                <div className={`p-2 px-3 rounded-4 shadow-sm ${msg.role === 'user' ? 'bg-warning text-dark' : 'bg-white text-dark border'}`} 
                                     style={{ maxWidth: '85%', fontSize: '14px', whiteSpace: 'pre-line' }}>
                                    {msg.text}
                                </div>
                                
                                {/* Product Suggestions - Horizontal Scrollable Cards */}
                                {msg.suggestions && msg.suggestions.length > 0 && (
                                    <div className="w-100 mt-2 d-flex overflow-auto pb-2 custom-scrollbar" style={{ gap: '12px' }}>
                                        {msg.suggestions.map((p, i) => (
                                            <div key={i} className="card border-0 shadow-sm flex-shrink-0" 
                                                 style={{ width: '150px', borderRadius: '15px', overflow: 'hidden', backgroundColor: '#fff' }}>
                                                <div style={{ height: '110px', overflow: 'hidden' }}>
                                                    <img 
                                                        src={`http://localhost:8080/api/public/products/image/${p.image}`} 
                                                        style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                                                        alt={p.productName}
                                                        onError={(e) => { e.target.src = 'https://via.placeholder.com/150x110?text=No+Image' }}
                                                    />
                                                </div>
                                                <div className="p-2">
                                                    <div className="small fw-bold text-dark text-truncate mb-1">{p.productName}</div>
                                                    <div className="d-flex justify-content-between align-items-center">
                                                        <span className="small text-danger fw-bold">
                                                            {(p.specialPrice > 0 ? p.specialPrice : p.price).toLocaleString()}‚Ç´
                                                        </span>
                                                        <a href={`/product/${p.productId}`} className="btn btn-sm btn-warning rounded-circle p-0 d-flex justify-content-center align-items-center" style={{width: '24px', height: '24px'}}>
                                                            <i className="bi bi-chevron-right small" style={{fontSize: '10px'}}></i>
                                                        </a>
                                                    </div>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                )}
                            </div>
                        ))}
                        {loading && (
                            <div className="text-muted small">
                                <span className="spinner-border spinner-border-sm me-2 text-warning"></span> ƒêang tr·∫£ l·ªùi...
                            </div>
                        )}
                        <div ref={scrollRef} />
                    </div>

                    {/* Input Area */}
                    <div className="card-footer bg-white border-top-0 p-3">
                        <div className="input-group bg-light rounded-pill px-3 py-1 border">
                            <input 
                                type="text" 
                                className="form-control border-0 bg-transparent shadow-none" 
                                placeholder="H·ªèi em v·ªÅ m·ªπ ph·∫©m..." 
                                value={input} 
                                onChange={(e) => setInput(e.target.value)} 
                                onKeyDown={(e) => e.key === 'Enter' && handleSend()} 
                            />
                            <button onClick={handleSend} className="btn border-0 text-warning px-0" disabled={loading}>
                                <i className="bi bi-send-fill fs-5"></i>
                            </button>
                        </div>
                    </div>
                </div>
            )}
            
            {/* Th√™m CSS inline ƒë·ªÉ ·∫©n thanh cu·ªôn x·∫•u x√≠ cho ƒë·∫πp h∆°n */}
            <style>{`
                .custom-scrollbar::-webkit-scrollbar { display: none; }
                .custom-scrollbar { -ms-overflow-style: none; scrollbar-width: none; }
            `}</style>
        </div>
    );
};

export default GeminiChat;
